@document url-prefix("https://musi.sh/") {
/* main content */
    body, #app-root {
        background-color: black !important;
        color: #bbb !important;
    }

    /* play and shuffle buttons */
    .c358, .ac5c {
        background-color: #222 !important;
        border-color: #222 !important;
        color: #aaa !important;
    }
    .c358:hover, .ac5c:hover {
        background-color: #222 !important;
        border-color: #222 !important;
        color: #ccc !important;
        box-shadow: 2px 3px 3px #111;
    }
    .c358:active, .ac5c:active {
        background-color: #222 !important;
        border-color: #222 !important;
        color: #ccc !important;
    }

    /* description text */
    ._2949 {
        color: #777 !important;
    }

    /* song title */
    .a83a {
        color: #bbb !important;
    }

    /* song album */
    .b7a3 > span:not(.a83a) {
        color: #777 !important;
    }

    /* album art */
    .a231, .e2f4, .aa5e, ._800c, ._6fd9, ._7fe5 {
        filter: brightness(80%) !important;
    }

    /* genre box */
    ._1dfe {
        overflow: hidden;
    }
    ._6110 {
        filter: blur(5px) !important;
    }
    .a101:hover ._6110 {
        opacity: 1.0 !important;
        filter: blur(4px) !important;
    }
    .f089 {
        background-color: rgba(0, 0, 0, 0.75) !important;
        color: rgba(255, 255, 255, 0.38) !important;
    }
    .f089:hover {
        color: rgba(255, 255, 255, 0.7) !important;
        filter: none;
    }

    /* radio wip screen */
    .de04, ._6d6f {
        color: #d51831 !important;
    }

    /* artist actions button */
    ._9c93 > .d2b9 {
        background-color: #222 !important;
        border-radius: 3px !important;
    }
    ._9c93 > .d2b9:hover {
        background-color: #222 !important;
    }

    .react-contextmenu {
        background-color: #222 !important;
        box-shadow: 1px 1px 5px #111 !important;
    }
    .react-contextmenu .react-contextmenu-item:hover {
        color: #ccc !important;
        background-color: #d51831 !important; 
    }
    .react-contextmenu ._9cca > h1 {
        color: #bbb !important;
    }
    .react-contextmenu ._9cca > h2, .react-contextmenu ._9cca > h3, .react-contextmenu .react-contextmenu-item {
        color: #aaa !important;
    }
    .react-contextmenu .react-contextmenu-item--divider:hover {
        background-color: #222 !important;
    }
    .react-contextmenu .react-contextmenu-item--divider {
        border-color: #1f1f1f !important;
        filter: blur(1px) !important;
    }


    

    /* Artist tabs */
    ._5894.f714 {
        background-color: #222 !important;
    }

    ._47e2 > ul > div > div > div{
        background-color: black !important;
    }

    .e49a {
        background-color: #222 !important;
        color: #bbb !important;
    }
    ._5894.f714 .e49a {
        background-color: black !important;
        color: #777 !important;
    }

    /* song hover */
    ._3bf9:hover .d1f2::after {
        background-color: #222 !important;
        box-shadow: 1px 1px 10px #111 !important;
    }

    /* modal */
    ._92f0 {
            background-color: black !important;
            box-shadow: 1px 1px 10px #111 !important;
        }
    ._1a01 {
        background-color: rgba(0, 0, 0, 0.6) !important;
    }
    ._8e61 {
        filter: blur(3px) !important;
    }
    ._8e61:only-child {
        filter: none !important;
    }

/* header */
    ._3383 {
        background-color: black !important;
    }

    /* logo */
    ._811c > a > span, .a6e3 > i, .bf8b {
        color: #777 !important;
    }

    ._811c > a:hover > span, .a6e3 > i:hover, .bf8b > span:hover {
        color: #aaa !important;
    }

    /* search bar */
    ._6fb2 > form > input, ._6fb2 > form > button {
        background-color: #222 !important;
        border-color: #222 !important;
    }
    ._6fb2 > form > input, ._6fb2 > form > button:hover {
        color: #ccc !important;
    }
    ._6fb2 > form > input::placeholder, ._6fb2 > form > button {
        color: #aaa !important;
    }

    /* search popup */
    .cd42.ffe4 {
        margin-top: -4px !important;
        margin-right: 0 !important;
        background-color: #222 !important;
        box-shadow: 0 8px 36px -10px #222 !important;
    }

    /* search popup section titles */
    .d9b7 {
        background-color: #222 !important;
        color: #bbb !important;
    }

/* sidebar */
    .ddf5, ._70a2 {
        background-color: black !important;
        color: #bbb !important;
        border: none !important;
    }

    /* section title */
    ._70a2 h3 {
        color: #d51831 !important;
    }

    /* inactive tab */
    li a:not(.a19b) {
        color: #777 !important;
    }
    li a:not(.a19b):hover {
        color: #aaa !important;
    }

    /* active tab */
    li a.a19b {
        border-color: #333 !important;
        background-color: #333 !important;
        color: #bbb !important;
    }

/* remove weird focus behavior */
.ddf5 ._1083 ._70a2 ul li a._86ea {
    border: 1px solid transparent !important;
}

    /* footer */
    ._46bd {
        margin-bottom: 24px !important;
    }

/* player controls */
    ._7353 {
        background-color: black !important;
        color: #bbb !important;
        margin-top: 0;
    }

    /* album art */
    ._4df4 {
        filter: brightness(80%) !important;
    }

    /* progress bar */
    .bfd5 {
        filter: brightness(80%) !important;
    }

    /* control icons */
    ._7353 .fas {
        color: #bbb !important;
    }
}
