/**
* Hide Cookie Consent Prompts
*
* Attempts to hide all pop up boxes that ask for your consent to store cookies, 
* such as those implemented in response to the GPDR.
* 
* By pkiff
* Version 0.91 beta
* Last updated 2019-06-21
*
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http), url-prefix(https), url-prefix(file) {

/* i really want this to be global */

/* smallnetbuilder.com */
#cadre_alert_cookies, div#leaderboardspacer {
    display:none;
}

/* apolitical.co */
#catapult-cookie-bar {
    display:none;
}

/* Cookie Consent */
.cc_banner-wrapper, .cc-window {
    display:none;
}

/* dsogaming.com */
#cookie-law-info-bar {
    display:none !important;
}

/* en.ostrog.com */
body > div.cookies {
    display:none;
}

/* intomore.com */
#cookieBanner, .cookieBanner {
    display:none;
}

/* thedreamcastjunkyard.co.uk */
.cookie-choices-info {
    display:none;
}

/* gamebuz.co */
.cookie-container {
    display:none !important;
}

/* eventbrite.ca */
.cookie_header {
  display:none;
}

/* politico.com */
.cookie-modal {
    display:none;
}

/* yoast.com */
#cookie-notice {
    display:none !important;
}

/* rescuetime.com */
.cookies-eu {
    display:none;
}

/* razer.com */
#cookies_popup_container {
    display:none !important;
}

/* pcgamer.com */
#futureConsent {
    display:none;
}

/* deepdyve.com */
.gdpr-banner {
    display:none;
}

/* readyfx.com */
.gdpr-policy {
    display:none !important;
}

/* g2.com */
.js-cookie-consent-overlay {
  display:none;
}

/* userstyles.org */
.NotificationLine {
    display:none;
}

/* pcmag.com */
.privacy-content {
    display:none;
}

/* theverge.com */
.m-privacy-consent {
    display:none;
}

/* canadacomputers.com */
.privacy-modal {
    display:none;
}

/* cbc.ca */
.privacyNotification {
    display:none !important;
}

/* chatelaine, rogers? */
.rdm-daac-notice-template {
    display:none;
}

/* cira.ca, pnsa.org */
#sliding-popup {
    display:none !important;
}

/* theweathernetwork.ca */
.noMobile, .woahbar {
    display:none;
}

#renderdfptags  {
    display:none;
}

/* prysmcat.com */
.widget_eu_cookie_law_widget {
    display:none !important;
}


/* Specific */

/* acquia.com */
#acquia_cookie_consent {
    display:none !important;
}

/* nhl.com */
.btm-nhl-announcement-modal {
    display:none;
}

/* eventbrite.com */
.eds-notification-bar {
  display: none;
}

/* adobe.com */
.evidon-banner {
    display:none !important;
}

/* huawei.com */
.huawei-cookie {
    display:none !important;
}

/* nba.com */
#nba_tos {
  display: none;
}

/* optomoa.com */
.optomaCookieWrap {
    display:none;
}

/* ipc.on.ca */
.PIWIK-button {
  display: none;
}
#PIWIK.modal-content {
  display:none !important;
}

/* thewalrus.ca */
#spu-103885 {
    display:none !important;
}

}

@-moz-document domain("expertreviews.co.uk") {
/* expertreviews.co.uk */
html {
  overflow-y: visible !important; 
  height: auto !important;
}
body {
  overflow-y: visible !important;
}
div#lightbox ~ div {
  display: none !important;
}
div#lightbox ~ div.xgallery-adv-overlay, 
div#lightbox ~ div.xgallery-adv-container {
  display: block !important;
}

.block-dennis-newsletter-popup {
  display: none !important;
}



}
