css:
@-moz-document url-prefix("https://www.zhihu.com/")
{
body {
    background-color: #252525;
    color: #C0C0C0;
}

.AppHeader {
    background: #1A1A1A;
}

.Card {
    background-color: #1A1A1A;
    border-bottom: 1px solid #252525;
}

.MoreAnswers .List-headerText {
    background: #1A1A1A;
}

.MoreAnswers .List-header:after {
    border-bottom: 1px solid #252525;
}

.List-item + .List-item:after {
    border-bottom: 2px solid #252525;
}

.ContentItem-actions {
    background: #1A1A1A;
}

.Topstory-mainColumn .Card:not(.Topstory-tabCard) {
    border-bottom: 3px solid #252525;
}

.Topstory-tabs.Tabs {
    border-bottom: 5px solid #252525;
}

.TopstoryTabs-link.Tabs-link.is-active {
    color: #444444;
}

.QuestionTopicReviewCardExtraInfo-cardTitle {
    color: #C0C0C0;
}

.VotableTopicCard {
    background-color: #252525;
}

.QuestionHeaderTopicMeta.Card {
    border: 1px solid #C0C0C0;
}

.QuestionHeader-title {
    color: #C0C0C0;
}

.NumberBoard-itemValue {
    color: #C0C0C0;
}

.ContentItem-more {
    color: #3183F9;
}

.GlobalSideBar-navNumber {
    background: #252525;
}

.CornerButton {
    background: #1a1a1a;
}

.QuestionHeader {
    background: #1a1a1a;
}

.QuestionHeader-footer {
    background: #1A1A1A;
}

a {
    color: #C0C0C0;
}

.Body--Mobile {
    background: #1A1A1A;
}
.MobileAppHeader-inner {
    background-color: #252525;
}
.Body--Mobile .RichContent-inner--collapsed .ContentItem-more:before,
.Body--Mobile .RichContent.is-collapsed .RichContent-inner .ContentItem-more:before {
    background: #1A1A1A;
}
.Body--Mobile .RichContent-inner--collapsed .ContentItem-more,
.Body--Mobile .RichContent.is-collapsed .RichContent-inner .ContentItem-more {
    background: none;
}
.TopstoryMain {
    background-color: #252525;
}

.CommentsV2-withPagination {
    background: #252525;
}

.Topbar {
    background: #252525;
}

.CommentTopbar-title {
    color: #C0C0C0;
}

.CommentListV2-header-divider {
    color: #C0C0C0;
    background-color: #1A1A1A;
}

.CommentsV2-footer {
    background: #252525;
}

.CommentsV2-Packup-Button {
    background: #1A1A1A;
}

.CommentListV2 {
    background-color: #252525;
}
.SearchTabs {
    background-color: #1A1A1A;
}
.Tabs-link {
    color: #C0C0C0;
}
.WikiBoxHeader-Meta {
    color: #C0C0C0;
}
.WikiBoxHeader-IntroductionWrapper {
    color: #C0C0C0;
}
.RichContent {
    color: #C0C0C0;
}
.Search-container {
    color: #C0C0C0;
}
.TopSearch-itemLink {
    color: #C0C0C0;
}
.RelevantQuery h2 {
    color: #C0C0C0;
}
}
