@-moz-document domain("spotify.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("spotify.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.ebe37a9866c20b333823f638c4d67453-scss {
	overflow: visible;
}

/*___ Remove Items _________________________________________________________________*/

.Root__ads-container,
.AdsContainer__inner {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Margins ______________________________________________________________________*/

.main-view-container {
	margin: 12px!important;
}

/*___ Heights ______________________________________________________________________*/

.content {
	max-height: none!important;
}

.index-homepage.has-shelf .simplified .container-simplified {
	min-height: 0!important;
	height: 37rem!important;
}

.index-homepage.has-shelf .hero-home.simplified {
	min-height: 0!important;
}

/*___ Widths _______________________________________________________________________*/

.contentSpacing,
.content {
	max-width: none!important;
}

.content {
	width: 98%!important;
}

.col-xl-8 {
	width: 77%!important;
}

.col-xl-4 {
	width: 22.333333%!important;
}

.Root__nav-bar {
	width: 24rem;
}
}

@-moz-document domain("spotify.com") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

body,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.TrackListHeader .mo-info-name,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}

}

@-moz-document domain("spotify.com") {
/*__________________________________________________________________________________*/

body {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.front-navigation,
.index-segmented-header header.sticky,
.layout-signup .l-signup-header {
	background-image: none!important;
}

/*___ Background Gray ______________________________________________________________*/

.head,
.layout-signup .l-signup-header,
.main-view__top-bar,
.navbar-default,
.now-playing-bar-container,
body {
	background-color: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

.CreatePlaylistButton__svg,
._4e275e65b2f67440893f0adf03608dab-scss,
.container-fluid,
.index-homepage .simplified .hero-home-bg-cover,
.index-homepage .wrap,
body {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.RootlistItem--is-active:before,
._0efd6b9f9de35e4d1fe896fe2ce5c17c-scss,
.bff8867ee69a86428f5d4d1c9cbb6e68-scss,
.container-fluid,
.shelves-container,
.shelves-container .shelves>.shelf,
body {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background _________________________________________________________*/

.RootlistItem__link.RootlistItem__link--is-highlighted,
.tracklist-row.tracklist-row--active {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*__ Blue __________________________________________________________________________*/

.btn.btn-white,
._1edf52628d509e6baded2387f6267588-scss {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn.btn-white:hover,
._1edf52628d509e6baded2387f6267588-scss:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn.btn-white:active,
._1edf52628d509e6baded2387f6267588-scss:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

.btn.btn-green {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn.btn-green:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.btn.btn-green:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.d3108ced6460158d1ef3bed71327e0cb-scss,
.main-view-container,
.shelves-container .shelves {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.d3108ced6460158d1ef3bed71327e0cb-scss:hover,
.main-view-container:hover,
.shelves-container .shelves:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

._4e275e65b2f67440893f0adf03608dab-scss,
._5658c6accf868644593ce36887a475b1-scss,
.react-contextmenu {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

._4e275e65b2f67440893f0adf03608dab-scss:hover,
._5658c6accf868644593ce36887a475b1-scss:hover,
.react-contextmenu:hover,
.tracklist-row:focus,
.tracklist-row:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Highlight Background _________________________________________________________*/

.react-contextmenu-wrapper li:hover {
	background: linear-gradient(to right, #5c0303, #0000)!important;
	border-left-color: #434343!important;
}
}

@-moz-document domain("spotify.com") {
/*__________________________________________________________________________________*/

._5658c6accf868644593ce36887a475b1-scss,
.tracklist-col
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

._5658c6accf868644593ce36887a475b1-scss:hover,
.tracklist-col:hover,
.tracklist-row.tracklist-row--active
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("spotify.com") {
.spoticon-heart-active-24:before,
.spoticon-pause-16:before,
.spoticon-heart-16:before,
.spoticon-heart-24:before,
.spoticon-heart-active-16:before,
.spoticon-play-16:before,
.spoticon-skip-back-16:before,
.spoticon-skip-forward-16:before {
	content: "";
}

/* Add _______________________________________________________________*/

.CreatePlaylistButton__svg {
	background: url(https://i.ibb.co/r063cKM/add-32x32.png) no-repeat!important;
	box-sizing: border-box!important;
}

.CreatePlaylistButton__svg {
	width: 26px!important;
	background-size: 21px!important;
	height: 21px!important;
	padding-left: 26px!important;
}

/* Device _______________________________________________________________*/

.spoticon-devices-16:before {
	background: url(https://i.ibb.co/VHrGNFV/Devices.png) no-repeat!important;
}

.spoticon-devices-16:before {
	background-size: 34px!important;
	padding-left: 33px!important;
}

/* Heart _______________________________________________________________*/

/* Off __________________________________*/

.spoticon-heart-active-24:before,
.spoticon-heart-16:before,
.spoticon-heart-24:before {
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
}

.spoticon-heart-active-24:before,
.spoticon-heart-24:before {
	height: 25px!important;
	padding-left: 27px!important;
	background-size: 23px!important;
}

.spoticon-heart-16:before {
	height: 17px!important;
	padding-left: 17px!important;
	background-size: 16px!important;
}

/* On __________________________________*/

.spoticon-heart-active-16:before,
.header-top .icon-favorited,
.icon.icon-badge-favorite,
.view-cam-controls .favorited.toggle-favorites svg {
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
}

.header-top .icon-favorited,
.view-cam-controls .favorited.toggle-favorites svg {
	height: 25px!important;
	padding-left: 27px!important;
	background-size: 23px!important;
}

.spoticon-heart-active-16:before {
	height: 17px!important;
	padding-left: 17px!important;
	background-size: 16px!important;
}

/* Home _______________________________________________________________*/

.icon.home-icon.NavBar__icon svg {
	background: url(https://image.ibb.co/hK4LCb/Home-48x48.png) no-repeat!important;
	box-sizing: border-box!important;
}

.icon.home-icon.NavBar__icon svg {
	width: 26px!important;
	background-size: 21px!important;
	height: 21px!important;
	padding-left: 26px!important;
}

/* Logo _______________________________________________________________*/

.navBar-header .logo svg {
	background: url(https://i.ibb.co/1mjV5TJ/spotify-logo.png) no-repeat!important;
	box-sizing: border-box!important;
}

.navBar-header .logo svg {
	width: 169px!important;
	background-size: 141px!important;
	height: 62px!important;
	padding-left: 172px!important;
}

/* Next ____________________________________________________________*/

.control-button.spoticon-skip-forward-16,
.control-button.spoticon-skip-back-16,
.spoticon-skip-forward-16:before {
	background: url(https://i.ibb.co/DVFmn9r/next.png) no-repeat!important;
	box-sizing: border-box;
}

.control-button.spoticon-skip-forward-16,
.control-button.spoticon-skip-back-16 {
	width: 37px!important;
	height: 37px!important;
	background-size: 37px!important;
}

.control-button.spoticon-skip-back-16 {
	transform: rotate(180deg);
}

/* Pause ____________________________________________________________*/

.control-button.spoticon-pause-16.control-button--circled {
	background: url(https://i.ibb.co/RbCNj60/pause-88x88.png) no-repeat!important;
	box-sizing: border-box;
}

.control-button.spoticon-pause-16.control-button--circled {
	width: 37px!important;
	height: 37px!important;
	background-size: 37px!important;
}

/* Play ____________________________________________________________*/

.control-button.spoticon-play-16.control-button--circled {
	background: url(https://i.ibb.co/fHQFmYd/play.png) no-repeat!important;
	box-sizing: border-box;
}

.control-button.spoticon-play-16.control-button--circled {
	width: 37px!important;
	height: 37px!important;
	background-size: 37px!important;
}

/* Search _______________________________________________________________*/

.icon.search-icon.NavBar__icon svg {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
}

.icon.search-icon.NavBar__icon svg {
	width: 26px!important;
	background-size: 17px!important;
	height: 21px!important;
	padding-left: 26px!important;
}

/* Collection _______________________________________________________________*/

.icon.collection-icon.NavBar__icon svg {
	background: url(https://i.ibb.co/rH3gZtt/assembly-4f222763d8142.png) no-repeat!important;
}

.icon.collection-icon.NavBar__icon svg {
	width: 26px!important;
	background-size: 21px!important;
	height: 21px!important;
	padding-left: 21px!important;
}

/* Play _______________________________________________________________*/

.tracklist-play-pause svg {
	background: url(https://i.ibb.co/PGLBNSP/play-88x88.png) no-repeat!important;
}

.tracklist-play-pause svg {
	width: 21px!important;
	background-size: 21px!important;
	height: 21px!important;
	padding-left: 21px!important;
}

/* Queue _______________________________________________________________*/

.spoticon-queue-16:before {
	background: url(https://i.ibb.co/pL013Fq/playlist-36x36.png) no-repeat!important;
}

.spoticon-queue-16:before {
	background-size: 24px!important;
	padding-left: 29px!important;
}

/* Volumn _______________________________________________________________*/

.spoticon-volume-16:before,
.tracklist-row.tracklist-row--active svg {
	background: url(https://i.ibb.co/ZY3T4Mm/volume-soft-medium-loud-37x38.png) no-repeat!important;
}

.spoticon-volume-16:before,
.tracklist-row.tracklist-row--active svg {
	width: 31px!important;
	background-size: 31px!important;
	height: 31px!important;
	padding-left: 31px!important;
}

/* Pause _______________________________________________________________*/

.tracklist-row--active .tracklist-play-pause svg {
	background: url(https://i.ibb.co/RbCNj60/pause-88x88.png) no-repeat!important;
}

.tracklist-row--active .tracklist-play-pause svg {
	width: 26px!important;
	background-size: 21px!important;
	height: 21px!important;
	padding-left: 21px!important;
}

.spoticon-devices-16:before,
.spoticon-queue-16:before,
.tracklist-row--active .tracklist-play-pause,
.tracklist-row--active .position,
.spoticon-volume-16:before,
.tracklist-row--active .position {
	color: #1ed76000!important;
}
}
