css:
@-moz-document url("about:home"), url("about:Home")
{

  html
  {
    background: url("http://img05.deviantart.net/23a8/i/2014/341/2/e/sword_art_online_kirito_and_asuna_wallpaper_by_kirigawakazuto-d890vo4.jpg") no-repeat center center fixed;
    background-size: cover;
  }

/*The link*/
  a
  {
    color: transparent !important;
    display: none;
  }

/*The text before link*/
  #snippets
  {
    background: transparent !important;
    box-shadow: 0 0 4px transparent inset !important;
    color: #191919 !important;
    display: none;
  }

  #searchText
  {
    -moz-appearance: none !important;
    background: transparent !important;
    border: none !important;
    color: #990000 !important;
    font-size: 16px !important;
    text-indent: 4px !important;
    box-shadow: 0 0 4px rgba(90, 0, 0,.9) inset !important;
  }

  #searchContainer input:not(#searchText)
  {
    -moz-appearance: none !important;
    color: #ddd !important;
    background: rgba(90, 0, 0,.9) !important;
    box-shadow: 0 0 4px rgba(48, 48, 48,.9) inset !important;
    border-radius: 0 4px 4px 0 !important;
    border-top: 1px solid #444 !important;
    border-right: 1px solid #444 !important;
    border-bottom: 1px solid #444 !important;
    border-left: 1px solid transparent !important;
    margin-left: -2px !important;
  }

  #brandLogo
  {
    background: url(http://pre09.deviantart.net/bbaf/th/pre/f/2012/323/d/f/sao_logo_by_iammrx-d5lgpwx.png) no-repeat;
    background-position: center;
    background-size: 300px;
    height: 150px;
    width: 300px;
    margin: 50px auto -5px;
  }

  #searchIcon
  {
    background: url(http://i946.photobucket.com/albums/ad303/Walker_Gross/SAO%20search%20button_zpsgrxwhygw.png) no-repeat center center;
    background-size: 37px;
  }
}
