css:
@-moz-document domain("youtube.com")
{
#logo-icon-container.ytd-topbar-logo-renderer {/*logo*/
    animation-name: logoyad;
	filter: none;
    animation-duration: 0.8s;
    animation-iteration-count: 1;
	fill-opacity: 0;
    background-image: url("data:image/svg+xml;base64,");
    background-position: center;
    background-size: 100%;
    background-repeat: no-repeat;
    width: 100%;
    height: 50px;
    max-width: 100px;
    max-height: 100px;
		
}

ytd-app {
	background-image: url(https://i.postimg.cc/nV7tJYJK/maxresdefault-1.png);
	background-repeat: no-repeat;
	background-attachment:fixed;
	background-color: #14031E;
	background-position: 50% 33%, center;
	background-size: cover!important;
	background-blend-mode: screen;	
}


/*new*/
.badge-style-type-simple.ytd-badge-supported-renderer {
    background: #5e5e5e;
    color: #fff;
}

.style-scope ytd-rich-metadata-renderer {/*side coloumn*/
background-color: #595651;
border-radius: 3px
}
ytd-comments {/*comments box*/
    display: block;
    background-color: #ea1d077d;
	background-image: linear-gradient(180deg, #ea1d07 100%);
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}

.style-scope ytd-watch-next-secondary-results-renderer {/*recommandation*/
    display: block;
    background-color: #ea1d077d;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}

#info.ytd-watch-flexy {/*title video*/
    display: block;
    background-color: #ea1d077d;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 0px solid #fff394;
	border-right: 4px solid #fff394;
}

.style-scope ytd-rich-item-renderer {/*ytb video page*/
    display: block;
    background-color: #ea1d07;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}

.style-scope ytd-post-renderer {/*ytb chl post*/
    display: block;
    background-color: #ea1d07;
}

.style-scope ytd-video-secondary-info-renderer {/*description*/
    display: block;
    background-color: #ea1d077d;
    border-left: 4px solid #fff394;
	border-top: 0px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}
  
#container.ytd-searchbox {/*search bar*/
    background-color: #801308;
    border-left: 2px solid #fff394;
	border-top: 2px solid #fff394;
	border-bottom: 2px solid #fff394;
	border-right: 2px solid #fff394;
}
#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 60px;
    border: 2px solid #fff394;
    background-color: #ea1d07;
}
#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
    width: 30px;
    height: 30px;
    color: #fff;
	opacity: 1;
}
ytd-searchbox[has-focus] #container.ytd-searchbox {
    border: 1px solid #fff;
    box-shadow: inset 0 0px 6px #ea1d07;
}

.ytp-scrubber-pull-indicator::after {
    display: block;
    position: inherit;
    content: '';
    top: -13px;
    left: -13px;
    opacity: 1;
    width: 40px;
    height: 40px;
    border-style: solid;
    border-width: 0px;
    border-color: #e70b0b;
}

.style-scope ytd-expanded-shelf-contents-renderer {/*tendance*/
    display: block;
    background-color: #ea1d0790;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;   
}

.style-scope ytd-grid-renderer {/*abonnement*/
    display: block;
    background-color: #ea1d0790;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}

ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer
  { /*like*/
    background-color: #ffc60d!important;
  }
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1)
  { /*like*/
    color: #ffc60d;
  }
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2)
  { /*dislike*/
    color: var(--yt-spec-icon-inactive);
  }

paper-button.ytd-subscribe-button-renderer {/*sub button*/
    background-color: #ffc60d;
    border-radius: 5px;
}
.ytp-sb-subscribe, a.ytp-sb-subscribe {
    background-color: #ffc60d;
    color: #fff;
}

.guide-entry-badge.ytd-guide-entry-renderer {/*live icon*/
    width: 16px;
    height: 16px;
    color: #5e5e5e;
    display: none;
}

.style-scope ytd-multi-page-menu-renderer {
	display: block;
    background-color: #ea1d07;
    border-left: 4px solid #fff394;
	border-top: 4px solid #fff394;
	border-bottom: 4px solid #fff394;
	border-right: 4px solid #fff394;
}

.style-scope ytd-active-account-header-renderer {
	display: block;
    background-color: #ff9c8f;
    background-image: linear-gradient(180deg, #423737 27%, #ea1d07 100%);
}

.style-scope ytd-simple-menu-header-renderer {
    background-color: #ea1d07;
}


headerytd-multi-page-menu-renderer {/*right dropdown notif*/
    background: #c9b04d;
}

#guide-content.ytd-app {
    background-image: linear-gradient(180deg, #e04d28 100%);
}
yt-icon.ytd-compact-link-renderer {/*dropdown yt icons*/
    color: var(--yt-compact-link-icon-color, var(--yt-spec-icon-inactive));
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {/*toggle button*/
    background-color: #ffffff;
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    opacity: 1;
    background-color:#ffc60d;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {/*toggle button2*/
    background: #ffc60d;
}

.ytp-volume-slider-handle {/*volume buttom*/
    position: absolute;
    top: 50%;
    width: 12px;
    height: 12px;
    border-radius: 5px;
    margin-top: -5px;
    background: #ffffff;
}

.badge-style-type-live-now.ytd-badge-supported-renderer {/*live now*/
    background: transparent;
    color: #fff;
    border: 1px solid #eb1700;
	border-radius: 6px;
}

ytd-thumbnail-overlay-time-status-renderer[overlay-style=LIVE] {
    background-color: #423737;
}

/*watched video bar*/
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: #423737;
    height: 100%;
}

/*notif color*/
#notification-count.ytd-notification-topbar-button-renderer {
    position: absolute;
    top: 4px;
    right: 0;
    color: var(--yt-swatch-important-text, var(--yt-white));
    background-color: #ea1d07;
    width: 18px;
    height: 18px;
    border-radius: 50%;
    line-height: 18px;
    font-size: 10px;
    text-align: center;
    cursor: pointer;
}
/*scrollbar*/
#contentWrapper.iron-dropdown > * {
    overflow: hidden;  
}
#contentWrapper.iron-dropdown > *:hover {
    overflow: auto;
}
::-webkit-scrollbar {
  width: 8px;
}

/* Track */
::-webkit-scrollbar-track {
  border-radius: 10px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #ad1100; 
  border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #ff4733;
  box-shadow: 0 0 9px 2px #5e5e5e;
}

ytd-button-renderer.style-suggestive[is-paper-button] paper-button.ytd-button-renderer {
    border: 1px solid #fff;
    padding: var(--yt-button-padding-minus-border);
}

/*join button*/
ytd-button-renderer.style-suggestive[is-paper-button] {
    --yt-formatted-string-deemphasize-color: var(--yt-blue-opacity-lighten-2);
    background-color: transparent;
    color: #fff;
}

/*video menu*/
paper-listbox {
    display: block;
    padding: 8px 0;
    background-color: #570505;
    color: var(--paper-listbox-color, var(--primary-text-color));
	background-image: linear-gradient(180deg, #ea1d07 100%);
}

/*drop menu BG*/
paper-listbox.yt-dropdown-menu {
    background-color: #570505;
	background-image: linear-gradient(180deg, #ea1d07 100%);
}


.ytp-popup {
    position: absolute;
    overflow: hidden;
    background: rgba(234, 30, 7, .9);
    -moz-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    -webkit-transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    transition: opacity .1s cubic-bezier(0.0,0.0,0.2,1);
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-user-select: none;
	background-image: linear-gradient(180deg, #ea1e07 100%);
}
.html5-video-info-panel {
    background: rgba(234, 30, 7, .9);
    color: #fff;
    left: 10px;
    position: absolute;
    top: 10px;
    z-index: 64;
    min-width: 26em;
}

/*cc color under*/
.ytp-chrome-controls .ytp-button[aria-pressed]:after {
    background-color: #fff;
}

/*hd text color*/
.ytp-swatch-color {
    color: #7affa4;
}

/*subpop*/
paper-toast {
    background-color: var(--paper-toast-background-color, #c22727);
	background-image: linear-gradient(140deg, #c22727 100%);
}
yt-confirm-dialog-renderer[dialog][dialog][dialog] {
    background-color: var(--paper-toast-background-color, #c22727);
	background-image: linear-gradient(180deg, #c22727 100%);
	text-align: center;
}

/*live indicator home*/
ytd-badge-supported-renderer.ytd-rich-grid-media {
    margin: 6px 0px 6px 10px;
}

.ytp-spinner-circle {
    border-style: dashed solid;
    border-color: #fff0 #fffda1 #fff0 #fffa00;
    border-radius: 50%;
}

/*mini player*/
.playlist-items.ytd-playlist-panel-renderer {
    background-color: #400a0a;
}
#info-bar.ytd-miniplayer {
    background-color: #5e5e5e;
}
ytd-playlist-panel-renderer[hide-header-text] .header.ytd-playlist-panel-renderer {
    background-color: #400a0a;
}

/*hd indicator*/
.ytp-settings-button.ytp-hd-quality-badge:after {
    height: 12px;
    width: 16px;
    background-color: #ff4400;
}

/*4k 8k indicator*/
.ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after {
    background-color: #ffed4f;
    box-shadow: 0 0 7px 1px #fff173;
    transform: scale(1.3);
}

/*info box on live*/
#card.yt-live-chat-viewer-engagement-message-renderer {
    background-color: #cfa85b;
}

ytd-clarification-renderer[background-style=info] {
    padding: 12px 0 12px 0;
    background: #cfa85b;
    border: 1px solid #9c7f46;
}
yt-live-chat-renderer {
    background: #a87614;
	background-image: linear-gradient(130deg, #ffed4f 0%, #fff173 100%);
}

html[dark] .watch-skeleton .skeleton-bg-color {/*skeletal system*/
    background-color: #a87614;
}

.sbfl_b {/*report search predictiion*/
    background: rgb(1 1 1 / 0%);
}
.sbsb_a {
    background-image: linear-gradient(180deg, #2b2424 10%, #423737 100%);
	border-radius: 0px 0px 15px 15px;
	background-color: #fad955;
	color: #fff;
}
.sbdd_b {
    background-color: #a87614;
    border: 1px solid #ffa900;
    border-top-color: #ffba33;
    box-shadow: 0 0px 0px rgba(0, 0, 0, 0);
    -webkit-box-shadow: 0 0px 0px rgba(0, 0, 0, 0);
    cursor: default;
	border-radius: 0px 0px 15px 15px;
	backdrop-filter: blur(2px)
}
.gsfs {
    font-size: 1.6rem;
    color: #dfdfdf;
}
.sbpqs_a {
    color: #fcffc4;
}
.sbsb_d {
    background: #eeeeee59;
}
#tooltip.paper-tooltip {
	background-image: linear-gradient(200deg, #c22727 100%);
    background-color: #c22727;
    color: var(--paper-tooltip-text-color, white);
	border: 3px solid #fff394;
}

ytd-toggle-button-renderer.style-text[is-icon-button] {/*thumbs*/
    color: #f0f0f0;
}
span.yt-view-count-renderer {/*views*/
    color: #f0f0f0;
}
#star-survey.ytd-inline-survey-renderer {/*survey*/
    background-color: #9e1b0d;
    background-image: linear-gradient(200deg, #3504444a 0%, #d81b9f4d 100%);
}
ytd-inline-survey-renderer[expanded] #dismissable.ytd-inline-survey-renderer {
    background-color: #9e1b0d;
}
#dismiss-button.ytd-inline-survey-renderer {
    background-color: #9e1b0d;
}
}
