css:
@-moz-document domain("live.bilibili.com") {
.search-bar {
    border-left: 0 !important;
    border-right: 0 !important;
    border-top: 0 !important;
    border-bottom: 2px solid !important;
    border-color: #aaaaaa !important;
}
.placeholder {
    display: none;
}
* {
    border-radius: 0 0 0 0 !important;
    box-shadow: none !important;
}
.radio-icon, .animation, .slider-handle, .bl-switch, .user-head, .hour-rank, .user-avatar, .room-cover, .anchor-avatar {
    border-radius: 100px !important;

}
.link-navbar-ctnr {
    box-shadow: 0 0 5px 1px rgba(158,179,193,.5) !important;
}
.room-bg {
    background-image: none !important;
}
.icon-attention {
    display: none;
}
.footer-right, .footer-linker:nth-child(2) {
    display: none;
}
.link-footer-ctnr {
    min-height: 0 !important;
}
.link-footer {
    height: 0 !important;
}
.gift {
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/action/1x_web/ic_card_giftcard_black_24dp.png);
    background-repeat: repeat;
    background-size: auto;
}
.ranking-big {
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/action/1x_web/ic_trending_up_black_24dp.png);
    background-repeat: repeat;
    background-size: auto;
}
}
