@-moz-document domain("animerost.org")
{
/*[[CUSTOMSETTING01]]*/
  .present,.banerTopTwo
  {
    display: none;
  }

  ::-webkit-scrollbar
  {
    width: 10px;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #1b1b1b!important;
  }

  *
  {
    vertical-align: middle;
  }

  a:hover,a:active
  {
    text-decoration: none!important;
  }

  :not(.imgOngoing)
  {
    color: #C7D4D4!important;
    border: none!important;
    text-shadow: 0 1px 0 #333!important;
    box-shadow: none!important;
  }

  .headbgnewfour,.headbgnewo,.headbgnewthree,.imgOngoing img,.rss,#rulesComment,.commentFinal,.commentFinal>*,.title_quote,.title_spoiler,.scriptcode,.title_quote,.quote,.title_spoiler,.text_spoiler,.shortstoryContent>*,#plist,.zagolovok,.ca-item-main
  {
    background: inherit!important;
  }

  .interDubBgTwo>*,.shortstoryContent #spoiler,.shortstoryContent #buttonLike,.shortstoryContent #shortstoryContentTegi
  {
    background-color: inherit!important;
  }

  #wrap
  {
    margin: 0!important;
  }

  body,html,#wrap,.headbgnew,.form,.block_4 a,.ui-widget-content,.mfp-wrap,#newComment
  {
    background: #0D0D0D!important;
  }

  .interDubBgTwo,#login_name,#login_password,.buttonEx,.block_10,.block_10:focus,input,.login form,#searchsuggestions,#searchsuggestions a
  {
    background-color: #1C1C1C!important;
  }

  .menu,.menu ul li a,.sar span,.banerTopTwo,.banerTopTwo div,.userinfo,.present,p,.modeKino,.fastPunkt,#futer,input,textarea,.block_13,div.topLine div.login div.loginAva,.unit-rating,.global_search,#searchsuggestions a span.searchheading,#toolbar .head,div.short-categori,.menu .content_menu,div.playerTitle,#stdplayer,#stdplayer select,div.playerAddComment,div.playerAddComment form button
  {
    background: #1C1C1C!important;
  }

  .series span,#todown,#toup,.block_13,#xf_floor,#xf_vozrast,#xf_skype,div.topLine div.login span.loginLink,div.topLine div.login a.loginExit,.raspis .popup
  {
    background: rgba(28,28,28,.3)!important;
  }

  li:hover,.menu ul li a:hover,.shortstoryContent h4,#stext,.interDubBgTwo>a:hover,#raspisMon>:hover,#raspisTue>:hover,#raspisWed>:hover,#raspisThu>:hover,#raspisFri>:hover,#raspisSat>:hover,#raspisSun>:hover,#raspisNest a:hover,a[href="javascript:ShowOrHide('raspisMon')"]:focus,a[href="javascript:ShowOrHide('raspisTue')"]:focus,a[href="javascript:ShowOrHide('raspisWed')"]:focus,a[href="javascript:ShowOrHide('raspisThu')"]:focus,a[href="javascript:ShowOrHide('raspisFri')"]:focus,a[href="javascript:ShowOrHide('raspisSat')"]:focus,a[href="javascript:ShowOrHide('raspisSun')"]:focus,a[href="javascript:ShowOrHide('raspisNest')"]:focus,.interDub .interDubBgTwo h2,.interDub .interDubBgTwo b,.shortstoryHead,.modeKino:hover,.fastPunkt:hover,.ui-widget-header,#nexttime,.block_4 span,.unit-rating li.current-rating,.unit-rating li a:hover,.bbcodes,.bbcodes:hover,.ui-state-default,.ui-widget-content .ui-state-default,#form_comment button,.title_spoiler,.nexttime
  {
    background: rgba(183,183,183,.1)!important;
  }

  .fastPunkt
  {
    padding: 10px;
  }

  .prev:after,#toup:after,.ca-nav-prev:after
  {
    content: "<";
  }

  .next:after,#todown:after,.ca-nav-next:after
  {
    content: ">";
  }

  #todown:after,#toup:after
  {
    display: block;
    text-align: center;
    margin-top: 64px;
  }

  .ca-nav-prev:after,.ca-nav-next:after
  {
    display: block;
    text-align: center;
    margin-top: 52px;
  }

  .buttonEx
  {
    background: url(http://animevost.org/templates/AnimeVostNext5/images/buttonExBG.png) no-repeat center!important;
  }

  .searchButton
  {
    background: url(http://animevost.org/templates/AnimeVostNext5/images/button_search.png) no-repeat center!important;
  }

  .prev,.next
  {
    background: #333!important;
    font-size: 13px;
    cursor: pointer;
    padding-top: 10px;
    text-align: center;
    width: 46px;
  }

  .headbgnew a,.bottomProblem
  {
    display: none!important;
  }

  .menu a
  {
    display: block!important;
  }

  .menu
  {
    top: 0!important;
  }

  .headbgnewo,div.headbgnew
  {
    height: 50px!important;
  }

  .menu ul#topnav li span.sar
  {
    z-index: 99999!important;
    top: 50px!important;
    position: absolute!important;
  }

  .imgOngoingVie,.imgOngoing span
  {
    color: #fff!important;
    background: rgba(0,0,0,.33)!important;
  }

  #vk_groups
  {
    opacity: .25;
  }

  #dle-content img
  {
    opacity: .7;
  }

  #dle-content img:hover
  {
    opacity: 1;
  }

  .functionPanel,.modeKino
  {
    padding: 10px;
  }

  .ca-nav-next>img,.ca-nav-prev>img
  {
    display: none;
  }

  ::-webkit-scrollbar-track,::-webkit-scrollbar-track-piece,::-webkit-scrollbar-corner
  {
    background: #0d0d0d!important;
  }

  .shortstory,#leftBlock,.shortstoryFuter form a,#raspisMon a,#raspisTue a,#raspisThu a,#raspisFri a,#raspisSat a,#raspisSun a,#raspisWed a,#raspisNest a,.raspis .popup,.staticInfo
  {
    background: #1C1C1C!important;
  }

  #player2,.epizode
  {
    background: #333!important;
  }

  .headbgnewthree,.headbgnewfour,.headbgnewo
  {
    height: 53px!important;
  }

  #player2,#anime
  {
    min-width: 100%;
    min-height: 100%;
  }

  .active
  {
    color: #f2f2f2!important;
  }

  .active:before
  {
    content: "\1405";
    font-size: .8rem;
    padding: 5px;
  }

  #futer
  {
    margin-top: 15px;
  }

  .shortstoryHead
  {
    border-radius: 15px 15px 0 0;
  }
}

@-moz-document domain("play.aniland.org")
{
  *
  {
    border: none!important;
    color: #ccc!important;
  }

  body
  {
    background: rgb(28, 28, 28) !important;
  }

  #knpki
  {
    background: #333!important;
  }

  object
  {
    min-width: 100%;
    min-height: 100%;
  }

  a
  {
    background: #333!important;
  }

  a:hover
  {
    background: rgba(183,183,183,.1)!important;
  }

  img[src="http://animevost.org/button70.png"]
  {
    filter: invert(0.5);
  }
}
