css:
@-moz-document domain(animemusicquiz.com) {
/*Variables for values used in multiple places*/
:root {
    /*The background image used throughout the game*/
    --bg-image: url(https://images4.alphacoders.com/248/thumb-1920-248024.jpg);
}

/*Backgronds*/
#gameChatPage > .col-xs-9 {
    background-image: none;
    background-color: rgba(255, 255, 255, 0);
}
#gameContainer {
    background-image: var(--bg-image);
    background-size: cover !important;
}
#startPage {
    background-image: var(--bg-image);
    background-size: cover !important;
}
#awMainView {
    background-image: none;
    background-color: rgba(255, 255, 255, 0);
}
#loadingScreen {
    background-image: none;
    background-color: rgba(255, 255, 255, 0);
}
/*Modal Boxes / pop-ups*/
:focus {
    outline-color: #ba3535;
}
.form-control:focus {
    border-color: #930066;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(138,21,21,.6);
}
.customCheckbox label {
    color: #ba3535;
}
.swal2-popup .swal2-styled.swal2-confirm {
    background-color: #6a2020;
    border-color: rgb(140, 10, 140);
}
.swal2-popup .swal2-styled:focus {
    box-shadow: 0 0 0 2px #fff, 0 0 0 4px rgba(75,20,75,.5)
}

/*Link colors - EVERYWHERE*/
a:hover {
    text-decoration: underline solid currentcolor !important;
}
a:link {
    color: #ac55ac;
}
a:visited {
    color: #ba3535;
}

/* These are for all of the links on the login screen.
Keep these specifically no matter the state because it looks better*/
.loginMainForm a {
    color: #ac55ac;
}

/*Coloration of the friends marker in game selection*/
.fa-friend {
    color: #cc4a22;
}

/*The color of the indicator for the active promo banner*/
  .mpPromoCycleEntry.active
  {
    background-color: #cc4a22;
    box-shadow: 0 0 10px 2px #ee6c66;
  }

/*In-game colors*/
/*The answer box that pops up over your head*/
.qpAvatarAnswerContainer .floatingContainer{
    opacity: 1 !important;
}
 .qpAvatarStatusInnerContainer {
    background-color: #8a1515;
    opacity: 1;
}
.qpAvatarStatusBar.watching {
    background-color: #ac55ac;
}
.qpAvatarStatusBar.completed, .qpAvatarStatusBar.looted {
    background-color: #ee6c66;
}
.qpAvatarStatusBar.dropped {
    background-color: #999999;
}
.qpAvatarStatusBar.hold {
    background-color: #8a1515;
}
.qpAvatarStatusBar.hold {
    background-color: #333333;
}
/*Mark of the Beast (host)*/
 .qpAvatarHostIcon {
    background-color: #cc4a22;
}
/*Info Bar below players*/
.qpAvatarInfoBar {
    color: #dcbcbc !important;
    text-shadow: 1px 0 #6a2c26, 0 1px #6a2c26, 1px 0 #6a2c26, 0 1px #6a2c26;
}

/*The corner of the answer pop-up before result checking*/
 .unknownResult {
    background-color: #aa4433;
    opacity: 1;
}
.qpsPlayerName.self {
    color: #b42424;
    text-shadow: 0 0 10px #dd8686;
}
#qpVideoOverflowContainer {
    background-image: url(https://photos1.blogger.com/blogger/7176/2152/1600/snapshot20061006181045.jpg);
    background-size: cover !important;
}
#qpVideoSoundOnly {
    opacity: 0;
}
/*Lobby styling*/
.lbReady .lobbyAvatarImgContainer {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.lbReady .lobbyAvatarTextContainer {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
/*The little mark of the host*/
 .lobbyAvatarHostContainer > .lobbyAvatarSubTextContainer {
    background-color: #cc4a22;
}
.lbReady .lobbyAvatarImgContainer {
    -webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 1);
    box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 1);
}
.lbReady .lobbyAvatarTextContainer {
    -webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 1);
    box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 1);
}
/*The lobby counter*/
 #lobbyCountContainer {
    background-color: rgba(66, 66, 66, 1);
}
/*The player counter*/
 #lobbyCountLoad {
    background-color: #ba3535;
    -webkit-box-shadow: 0 1px 10px 1px #cc4a22;
    box-shadow: 0 1px 10px 1px #cc4a22;
}
/*The ready counter*/
 #lobbyReadyCountLoad {
    background-color: #ac55ac;
}
/*This section changes the particle look for the flying notes/xp when guessing correctly*/
/*The the glow in and around the given answer*/
.particleEmiter {
    -webkit-box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    -webkit-text-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    text-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}
/*The particles you see flying to your xp-bar*/
.particle {
    background-color: #ac55ac;
    -webkit-box-shadow: 0 0 5px #ac55ac, 0 0 10px #ac55ac, 0 0 15px #ac55ac;
    box-shadow: 0 0 5px #ac55ac, 0 0 10px #ac55ac, 0 0 15px #ac55ac;
}
/*The glow around the particles*/
.particleShadow {
    -webkit-box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}

/*The vote skip button after voting skip*/
#qpVoteSkip.toggled {
    background-color: #5a1515;
}

/*This is for the answer pop-up, when you either get points or lose hope*/
/*When the answer is wrong, go red*/
.qpAvatarAnswerText.wrongAnswer {
    text-shadow: 0 0 10px #8a1515, 0 0 20px #8a1515, 0 0 30px #8a1515, 0 0 40px #5a1515, 0 0 70px #5a1515, 0 0 80px #5a1515, 0 0 100px #5a1515, 0 0 150px #5a1515;
}
.knownResult.wrong {
    background-color: #5a1515;
    -webkit-box-shadow: 0 0 1px 2px #8a1515, 0 0 2px 2px #8a1515, 0 0 7px 2px #5a1515, 0 0 10px 2px #5a1515, 0 0 15px 2px #5a1515, 0 0 20px 2px #5a1515, 0 0 40px 2px #5a1515;
    box-shadow: 0 0 1px 2px #8a1515, 0 0 2px 2px #8a1515, 0 0 7px 2px #5a1515, 0 0 10px 2px #5a1515, 0 0 15px 2px #5a1515, 0 0 20px 2px #5a1515, 0 0 40px 2px #5a1515;
}
/*When the answer is right, go purple. Sticking to warm tones*/
 .knownResult.correct {
    background-color: #ac55ac;
    -webkit-box-shadow: 0 0 1px 2px #aa55dd, 0 0 2px 2px #aa55dd, 0 0 7px 2px #ac55ac, 0 0 10px 2px #ac55ac, 0 0 15px 2px #ac55ac, 0 0 20px 2px #ac55ac, 0 0 40px 2px #ac55ac;
    box-shadow: 0 0 1px 2px #aa55dd, 0 0 2px 2px #aa55dd, 0 0 7px 2px #ac55ac, 0 0 10px 2px #ac55ac, 0 0 15px 2px #ac55ac, 0 0 20px 2px #ac55ac, 0 0 40px 2px #ac55ac;
}
.qpAvatarAnswerText.rightAnswer {
    text-shadow: 0 0 10px #aa55dd, 0 0 20px #aa55dd, 0 0 30px #aa55dd, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}
/*The progress bar below the video during results*/
#qpVideoProgressBar {
    background-color: rgba(150,0,25,0.4);
}
/*The glow around the part where you enter the answer*/
#qpAnswerInputContainer.focused {
    box-shadow: 0 0 15px 1px #cc2525;
}
/*This is for the auto-complete and the drop-down*/
/*The text matching your entry*/
.awesomplete mark {
    color: #8a1515;
    background-color: #523252;
    padding: 0;
}
/*Generic list colors*/
.awesomplete > ul {
    background: #424242;
    border: none;
    -webkit-box-shadow: 0 0 10px 2px rgb(0, 0, 0);
    box-shadow: 0 0 10px 2px rgb(0, 0, 0);
}
/*The selected entry from the drop-down*/
.awesomplete > ul > li[aria-selected="true"] {
    background-color: #330033;
}
/*The matching text in the selected entry*/
.awesomplete > ul > li[aria-selected="true"] mark {
    color: hsl(0, 81%, 44%);
    background-color: #660066;
}
/*The entry your mouse hovers over*/
.awesomplete > ul >  li:hover {
    background-color: #663366;
}
/*The matching text on the hovered entry*/
.awesomplete > ul > li:hover mark {
    background-color: #660066;
}
/*The glow from the Vote Skip button*/
#qpVoteSkipGlowContainer {
    box-shadow: 3px 0px 10px 5px #cc2525;
}

/*Floating containers in the game*/
.floatingContainer {
    background-color: #424242;
    border-color: #FF2525;
}
/*Solo / Multiplayer game buttons were given a seperate hoover effect*/
#mpPlayMultiplayer:hover, #mpPlaySolo:hover {
	background-color: rgba(170,37,37,0.65);
}

/*This section changes the look of the bottom bar*/
/*The outer edge of the xp container*/
#xpBarOuter {
    background-color: rgba(234, 104, 64, 0.85);
}
/*Color of the filled part of the xp-bar*/
/*Shadow of the box*/
 #xpBarAnimationContainer {
    background-color: rgba(172, 85, 172, 1);
    -webkit-box-shadow: 0 0 40px #ac55ac, 0 0 70px rgba(172, 85, 172, 1);
    box-shadow: 0 0 40px #ac55ac, 0 0 70px rgba(172, 85, 172, 1);
}
/*Filled part of the XP bar*/
 #xpBarInner {
    background-color: #9c449c;
    -webkit-box-shadow: 0 0 40px #ac55ac, 0 0 70px rgba(172, 85, 172, 0.85);
    box-shadow: 0 0 40px #ac55ac, 0 0 70px rgba(172, 85, 172, 0.85);
}
/*Level color*/
 .levelText {
    color: rgba(0, 0, 0, 0.33);
}
/*Add filters to make the currnecy conform to these colors*/
.currencyIcon {
    filter: hue-rotate(90deg) brightness(80%);
    -webkit-filter: hue-rotate(90deg) brightness(80%);
}

/*generic class for the large buttons on the bar*/
.menuBarMainButton {
    background-color: #8a1515;
}
/*hover effect*/
.menuBarMainButton:hover {
	-webkit-box-shadow: inset 0 0 20px 9px #8a1515;
	box-shadow: inset 0 0 20px 9px #8a1515;
    background-color: rgb(140, 10, 140);
}
/*This sets the border color behind the avatar*/
#avatarUserImgContainer {
    border-color: #930066;
}
/*This sets the border color around the main button*/
#mainMenuSocailButton {
    border-color: #930066;
}

#mainMenuSocailButton:hover {
    -webkit-box-shadow: 0px 0px 10px 2px rgba(238, 108, 102, 0.6);
    box-shadow: 0px 0px 10px 2px rgba(238, 108, 102, 0.6);
}

/*Generic and site-wide stuff. Most of it is found in game settings*/
/*Base for all buttons*/
.button:hover {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
/*Base for all text entry fields*/
.textAreaContainer:focus-within{
	-webkit-box-shadow: inset 0 0px 1px rgba(0,0,0,.075), 0 0 1px rgba(205, 35, 35, 0.6);
	box-shadow: inset 0 0px 1px rgba(0,0,0,.075), 0 0 1px rgba(205, 35, 35, 0.6);
	border: solid 1px #cc2525;
}
/*Sliders in settings and game setup*/
.slider-selection {
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
}
.slider-handle {
	background-color: #770205;
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#a71500 100%);
}
.slider-tick.in-selection {
    background-image: -webkit-linear-gradient(to bottom,#aa1010,#8a1515 100%);
    background-image: -o-linear-gradient(to bottom,#aa1010,#8a1515 100%);
	background-image: linear-gradient(to bottom,#aa1010,#8a1515 100%);
}
/*a subset of buttons, the main/expected button to use. (OK, Host, etc)*/
.btn-primary {
    background-color: #8a1515;
    border-color: #3c153c;
}
/*hover effect on primary buttons*/
.btn-primary:hover {
	-webkit-box-shadow: inset 0 0 20px 9px #8a1515;
	box-shadow: inset 0 0 20px 9px #8a1515;
    background-color: rgb(140, 10, 140);
    border-color: rgb(140, 10, 140);
}
/*the effect when focus is applied (a small border and outline)*/
.btn-primary:focus {
	background-color: #8a1515;
    border-color: #3c153c;
    outline-color: #ba3535;
}
/*focus effect for all buttons*/
btn:focus {
    outline-color: #ba3535;
}
/*Generic progress bars*/
.progress-bar {
	background-color: #8a1515;
}
/*Containers for switches in the on and off states*/
.switchContainer.active {
	background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
	background-image: -o-linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
	background-image: linear-gradient(to bottom,#8a1515 0,#850a0a 100%);
}
.switchContainer > .slider-tick-container > .switchOn  {
	background-color: #770205;
    background-image: -webkit-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: -o-linear-gradient(to bottom,#8a1515 0,#a71500 100%);
    background-image: linear-gradient(to bottom,#8a1515 0,#a71500 100%);
}
/*when filtering for include, exclude and optionals*/
.filterEntryAdditionContainer.include {
	background-color: #ba1c1c;
}
.filterEntryAdditionContainer.exclude {
	background-color: #9a1c9a;
}
.filterEntryAdditionContainer.optional {
	background-color: #aa0780;
}

/*Home page stuff!*/
/*The Donation menu on the homepage*/
#mpDriveFreeDonationHightlight {
	background-color: #8a1515;
	-webkit-box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
	box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
}
#pmPatroenLink:hover {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
#pmLinkAccountButton:hover {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.pmButton:hover {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.icShowElement {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}

/*Trinkets in the Expand Library menu*/
#expandLibraryInfoContainer {
	background-color: #8c7f7f;
}
.elInputBackgroundText {
	color: rgba(140, 127, 140, 0.44);
}
.elResStatus.uploaded {
    background-color: #b85cb8;
}
.elResStatus.pending {
    background-color: #8a1515;
}
.elResStatus.blocked {
    background-color: #ac55ac;;
}
#elQuestionInputState {
	color: #8a1515;
}

/*Styling for the chat in the bottom menu; mostly fixing borders, highlights and glow effects*/
.chatIndicator > .animationContainer{
	-webkit-box-shadow: 0 0 5px 5px #fff, 0 0 10px 5px #fff, 0 0 15px 5px #fff, 0 0 20px 5px #cc2525, 0 0 35px 5px #cc2525, 0 0 40px 10px #cc2525, 0 0 50px 15px #cc2525, 0 0 150px 5px #cc2525;
	box-shadow: 0 0 5px 5px #fff, 0 0 10px 5px #fff, 0 0 15px 5px #fff, 0 0 20px 5px #cc2525, 0 0 35px 5px #cc2525, 0 0 40px 10px #cc2525, 0 0 50px 15px #cc2525, 0 0 150px 5px #cc2525;
}
.chatBoxTextGlow {
	text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #fff, 0 0 40px #cc2525, 0 0 70px #cc2525, 0 0 80px #cc2525, 0 0 100px #cc2525, 0 0 150px #cc2525;
}

/*Styling for the in-game chat*/
#gcMessageContainer > li.highlight {
	background-color: #8a1515;
}
#gcMessageContainer > li.highlight a{
	color: #930066;
}
.gcQueueItem.highlight {
	background-color: #8a1515 !important;
}

/*Buttons and bits and pieces found at the top*/
#qpReturnLobbyVoteContainer {
	box-shadow: 0px 0px 15px 1px #cc2525;
}
#qpVoteReturnYes.selected {
	background-color: #b85cb8;
}
#qpVoteReturnNo.selected {
	background-color: #ac55ac;
}
#qpReturnVoteTimerBar {
	background-color: #8a1515;
}
#qpVolume {
   color: #930066;
}
#qpVolumeBarIndicator {
    background-color: #930066;
}
#qpAnswerStateContainerInner {
    background-color: #8a1515;
}
/*Some more avatar-styling, for in-game*/

.qpAvatarListBar.completed, .qpAvatarListBar.looted {
	background-color: #8f2644;
}
.qpAvatarListBar.watching {
	background-color: #b0392d;
}
.qpAvatarAnswerNumber {
    background-color: #8a1515;
}
/*Report buttons, up- and downvote buttons*/
#qpFeedbackInput.glow {
	-webkit-box-shadow: 0px 0px 10px #8a1515;
	box-shadow: 0px 0px 10px #8a1515;
}
#qpUpvoteContainer.selected {
	background-color: #5a1515;
}
#qpReportContainer.selected {
	background-color: #74204d;
}
#qpDownvoteContainer.selected {
	background-color: #ac55ac;
}
.qpLifeActiveOverlay {
	background-color: #B6FF00;
	-webkit-box-shadow: inset 0 0 5px #fff, inset 0 0 10px #fff, 0 0 3px #fff, 0 0 5px #fff, 0 0 15px #5a1515;
	box-shadow: inset 0 0 5px #fff, inset 0 0 10px #fff, 0 0 3px #fff, 0 0 5px #fff, 0 0 15px #5a1515;
}
/*Life stuff for LMS/BR*/
.qpLifeChargeOverlay {
	background-color: #8a1515;
	box-shadow: inset 0 0 2px #ffffff80, inset 0 0 5px #ffffff80, 0 0 3px #ffffff80, 0 0 7px #8a1515;
	box-shadow: inset 0 0 2px #ffffff80, inset 0 0 5px #ffffff80, 0 0 3px #ffffff80, 0 0 7px #8a1515;
}
.qpLife.target {
	background-color: #5a1515;
}
/*BR Specifics*/
#brMap:focus-within {
	-webkit-box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
}
.brTile > div {
    background-color: #8a1515;
}
.brMapObject.active {
	-webkit-box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	border-color: #8a1515;
}
.brMapObject.active.inventoryFull {
	border-color: #ac55ac;
}
.brPlayer.self > .brPlayerImage {
	-webkit-box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
	box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
}
#brCollectionContainer.lowSlotsCount >#brCollectedList > li::after {
	background-image: -webkit-linear-gradient(to left, rgb(172, 41, 172), rgba(172, 41, 172, 0));
    background-image: -moz-linear-gradient(to left, rgb(172, 41, 172), rgba(172, 41, 172, 0));
    background-image: -ms-linear-gradient(to left, rgb(172, 41, 172), rgba(172, 41, 172, 0));
    background-image: -o-linear-gradient(to left, rgb(172, 41, 172), rgba(172, 41, 172, 0));
    background-image: linear-gradient(to left, rgb(172, 41, 172), rgba(172, 41, 172, 0));
}

/*Styling in the Avatar purchase/selection menu*/
.awSkinPreview.awCurrentAvatar {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.awOptionDisabled {
    background-color: #8a1515;
}
.awHibSkinOption {
    color: #8a1515;
}
/*In the off chance someone is using this with a guest account, make sure the notice is clearly visible*/
 #guestAccountNoticeContainer {
    left: 50%;
    transform: translate(-50%, 0%);
    bottom: 45px;
    background-color: rgba(35, 35, 35, 1);
    border-color: rgba(10, 10, 10, 1);
}
#guestAccountNoticeContainer > h4 > a {
    color: rgba(251, 131, 108, 1);
}
}
