css:
@-moz-document domain(bloard.com) {
/* light blue (text, borders) #27B0CE */
/* darkest (main container) #061d23 */
/* shaded text (links) */
body {
    background-color: #061d23;
    color: #27B0CE
}
.navbar-default {
    background-color: #093944;
    border: none;                     
}
#main-container {
    background-color: #061d23;
    border: none;
}
.list-group-item {
    background-color: #061d23
}	
a {
    color: #1b7998
}
a:hover, a:focus {
 	color:  #27B0CE;
}
.btn-success:hover, .btn-success:focus, .btn-default:hover, .btn-default:focus, .btn:hover, .btn:focus  {
 	background-color: #3DC4C6;  
    color: #FFF;
}
.topic-link {
    color: #27B0CE
}
.bloard-post .bloard-post-user-column {
    background-color: #0A313B;
}
.btn-success {
    background-color: #256E78;
    color: #3dc4d4;
    border-color: #3dc4d4;
}
.btn-default, .btn {
    background-color: #093944;
    color: #3dc4d4;
    border-color: #3dc4d4 !important;
}
.old-post {
    background-color: transparent
}
.breadcrumb-topic-name {
    color: #27B0CE;
}
.breadcrumb {
    background-color: #256E78;
    border-color: #27B0CE;
}
.breadcrumb > .active {
    color: #27B0CE;
}
.breadcrumb a {
    color: #093944
}
.breadcrumb a:hover, .breadcrumb a:focus {
    color: #27B0CE;
}
.breadcrumb > li + li::before {
 	color:    #093944
}
.well {
    background-color: #0A313B;
    color: #27b0ce
}
.pagination > li > a, .pagination > li > span {
    color: #27B0CE;
    background-color: #093944;
    border: 1px solid #27B0CE;
}
.pagination > li > a:focus, .pagination > li > a:hover {
 	background-color: #3DC4C6  !important;
    color: #093944 !important; 
    border: 1px solid #27B0CE;
}
.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus, .inactive-link {
    color: #27B0CE;
    background-color: #256E78;
    border-color: #27B0CE;
}
.pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    color: #27B0CE;
    background-color: #256E78;
    border-color: #27B0CE;
}
.navbar-brand {
    color: #27B0CE !important;
}
.navbar-brand:focus, .navbar-brand:hover  {
 	background-color: #3DC4C6  !important;
    color: #FFF !important;
}
.navbar-nav > li > a {
    color: #27b0ce !important;
}
.navbar-nav a {
 	color: #27b0ce !important
}
.navbar-nav a:focus, .navbar-nav a:hover, .navbar-default .navbar-nav > .active > a {
 	background-color: #3DC4C6  !important;
    color: #093944 !important;  
}
.navbar-nav > .open > a {
 	background-color: #3DC4C6 !important;
    color: #093944 !important;  
}
.dropdown-menu {
    background-color: #093944;
}
.reply-input, .form-control {
    background-color: #093944;
    border-color: #256e78;
    color: #27b0ce;
}
.form-control:focus {
    border-color: #3DC4C6;  
}
.emojiPicker nav {
    background-color: #256e78;
}
.emojiPicker section {
    background: #256e78;
}
.topic-link:format {
    color: 'blue'   
}
.modal-content {
 	background-color: #061d23;
    color: #27B0CE;
    border: 1px solid #27B0CE;
}
.close {
 	color:  #256e78;
    opacity: 1;
    text-shadow: 0 1px 0 #256e78;
}
.close:hover, .close:focus {
 	color:  #27B0CE;
    opacity:1;
 	filter:alpha(opacity=100)
}
.table > tbody > tr > th, .table > tbody > tr > td {
    border-top: 1px solid #27B0CE;
}
.panel {
    background-color: #061d23;
}
.panel-default > .panel-heading {
    background-color: #256E78;
    border: 0;
    color: #27B0CE;
}
}
