css:
@-moz-document domain("4sucres.org") {
.p-3 {
    padding: 0 !important;
}

.post-image {
    width: 50px !important;
}

.op-8 {
    background: #4b5262 !important;
}

.hover-accent img.rounded {
    width: 32px !important;
}

div.text-small.overflow-ellipsis:not(.col-lg-fixed), .owl-carousel {
    display: none !important;
}

.post-image {
    width: 50px !important;
}

.col-lg-fixed.last-activity {
    width: 250px;
}

.account-block .account-details {
    padding: 5px 15px;
}

.d-lg-block {
    display: inline !important;
}

.navbar {
    height: 50px;
}

.account-block .account-image > img {
    max-height: 50px;
}

.blue, .white, .card, .bg-light, .page-link, .nav-link.active:hover, .nav-pills .nav-link.active, .nav-pills .nav-link:hover {
    background: #3b4252 !important;
}

body, .hover-accent:hover, .post:target, blockquote, .CodeMirror, .modal-content, .page-item.active .page-link, .editor-toolbar button.active, .editor-toolbar button:hover, .btn-light, .card-header, .tooltip-inner, .integration {
    background: #2e3440 !important;
    color: white;
}

img[src='/img/imgur_logo.png']{
    filter: invert(1);
}

hr, .post, .border, .editor-toolbar, blockquote, .CodeMirror, .btn-toolbar, .btn-light, .integration {
    border-color: #2e3440 !important;
}

.page-item.active .page-link {
    border-color: white;
}

.btn-light.focus, .btn-light:focus, .btn-light:hover  {
    box-shadow: 0 0 0;
    color: #fff;
    text-shadow: 0px 0px 5px #fff;
}

.btn-light:hover img {
    filter: invert(1) hue-rotate(100deg);
}

.btn-light:hover .spoiler {
    box-shadow: 0 0 0 .2rem rgba(222,222,223,.5);
}

.page-link:hover {
    color: #ddd
}

button.d-inline {
    height: 100%;
}

.sidetag {
    display: none;
}

a, body, .h1, .h2, .h5, h1, h2, h5, .btn-link, .nav-link, .page-link, .nav-link.active:hover, .nav-pills .nav-link.active, .nav-pills .nav-link:hover, a:hover, .fa, .close > span {
    color: #fff;
}

.text-muted {
    color: #fff !important;
}

.editor-toolbar a {
    color: inherit !important;
}

.card {
    border: none;
}

.post {
    padding: 1rem !important;
}

.CodeMirror textarea {
    color: white;
}

footer {
    margin-top: -1.5rem;
    margin-bottom: 0;
}
}
