css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#baseDiv, #channel-base-div
{ width: 1400px !important;}

#movie_player
{width: 1000px !important; height: 570px !important;}

#playnav-play-panel 
{padding-left: 400px !important;height: 200px !important;}

#playnav-play-playlist-uploads-holder
{height: 1100px !important;}

#user_playlist_navigator
{height: 900px !important; background: #000 !important;}

#playnav-bottom-links-clip
{ margin-top: 200px !important; }

#playnav-video-panel-inner
{ margin-top: 30px !important; border: 1px solid #fff !important;}

#playnav-body
{ height: 830px !important; }

.playnav-video-panel.inner-box-colors.border-box-sizing
{background: none !important;}


#leaderboardAd
{display: none !important;}


#baseDiv td 
{ background: none !important; }

h1 
{font-size: 25px !important;
margin-bottom: 10px !important; 
margin-top: 10px !important; }

#masthead-container 
{ width: 1200px !important;
padding-right: 200px !important; 
margin-bottom: 0px !important; 
margin-top: 10px !important; }

#masthead 
{width: 1400px !important; padding-right: 0px !important; margin-top: 15px !important; }

A.yt-menulink-btn.yt-button.yt-button-primary, A.yt-menulink-btn.yt-button.yt-button-  {background: none !important;}

#footer, #copyright, #relatedVidsToggle, #promotedVidsContainer, #channelBrandCap, #channelVidsBody, #logoTagDiv, #channelBrandDiv, .hpContentBlock, #hpSideContent, #gNavDiv, #asDiv
 {display: none !important;}

}
@-moz-document url-prefix(http://www.youtube.com/user/) {

#profile-main-content 
{float:left !important;}

#profile-side-content 
{padding-right: 100px !important;}

#channel-body,#playnav-play-all-scrollbox,#playnav-channel-header,#user_branding,#playnav-play-uploads-scrollbox,#main-channel-content,#user_comments,div.inner-box,#playnav-play-favorites-scrollbox,#playnav-grid-favorites-scrollbox,#playnav-grid-all-scrollbox,#playnav-grid-uploads-scrollbox
{ background: none !important;}

#user_branding,#main-channel-content,#user_comments
{border: 1px solid #fff !important;}

#main-channel-content
{color: #000 !important;}

}
