css:
@-moz-document url-prefix("https://www.iceland.co.uk/") {
body {
    color: #ddd;
    background: #18181a !important;
}

.js .loader-bg {
    background-color: #000;
    opacity: .4;
}

.input-group {
    background: #181818;
}

.login-form {
    background: #232323;
}

.login-popover .login-section,
.password-reset-container{
    background: #18181a;
}

.login-account-section {
    border-top: 1px solid #d8d8d8;
    background: #232323;
}

.top-banner {
    position: relative;
    width: 100%;
    transition: left .2s ease-in;
    background: #232323 !important;
}

.form-control {
    color: #444;
    background-color: #18181a;
    border: 1px solid #999;
}

.search-suggestion-wrapper {
    background-color: #18181a;
    border: 1px solid #777;
}

div .product-price {
    color: #ccc !important;
}

.input-group-text {
    background-color: #333;
    border: 1px solid #999;
    color: #ccc;
}

.mini-cart-total {
    color: #0097cc;
    background: #333;
}

.bg-light-grey {
    background-color: #333 !important;
}

.btn-link.button, .btn-link.button-fancy-large, .btn-link.ui-button, .btn.btn-link, .button-text, a.link, p a {
    color: #ddd;
}

.menu-utility-user .user-info.active .user-account {
    background-color: #18181a;
    border-left: 1px solid #232323;
    border-right: 1px solid #232323;
}

.menu-utility-user .user-panel {
    background-color: #18181a;
    border: 1px solid #232323;
}

.menu-utility-user .user-links {
    color: #ddd;
}

.pt_storefront #main {
    background: #18181a !important;
}

nav.leftnav[role="navigation"] {
    border-top: 1px solid #000;
    background-color: #18181a;
    color: #ddd;
}

.header-nav-menu {
    display: none;
    background: #232323;
    border-top: 1px solid #d8d8d8;
}

.expandable-menu {
    background-color: #18181a !important;
}


.banner-full-inner, .banner-full-inner-link {
    background: #22222275;
}

.breakout-background-color {
    background-color: #18181a;
}

#delivery-options-display {
    background: #222;
}

.home-hero {
    min-height: 316px;
    background-color: #18181a;
}

a .menu-category-link {	
    background: #18181a;
}

.carousel-title > span {
    background-color: #18181a;
}

img {
    filter: brightness(75%);
}

img:hover {
    filter: brightness(100%);
    transition: .5s;
}

.category-tiles .category-tile div.category-tiles-text {
    background: #232323;
    color: #ddd;
}

.horizontal-carousel .grid-tile .product-tile {
    background: #232323;
    color: #999;
}

.product-tile .product-name a span {
    background: #232323;
    color: #ddd;
}

.product-tile .product-pricing {
    color: #cc092f;
}

.mobile-nav-links .nav-group-title span, .title-line span {
    background-color: #18181a;
}

.iceland-cards.offer-list-2x .card-text {
    background: #232323;
    color: #ddd;
}

#wrapper {
    background-color: #18181a;
}

a {
    color: #ddd;
}

footer {
    clear: both;
    background-color: #232323;
}

/* Category page */

.account-menu-select select, .items-per-page fieldset select, .refinement-filter select, .refinement-select select, .select-style select, .sort-by fieldset select {
    border: none;
    background: #18181a !important;
}

.btn-light:not(:disabled):not(.disabled).active {
    color: #4a4b4d;
    background-color: #333;
}

.btn-group > .btn:not(:first-child) {
    background: #18181a;
}

.refinements-title,
.refinement-header,
.refinement-header span  {
    background: #18181a;
}

label {
    color: #999;
}

#price-from,
#price-to {
    background-color: #18181a;
}

.btn-light:hover {
    color: #4a4b4d;
    background-color: #777;
    border-color: #fbfbfb;
}

/* Product page */

.quantity-module-primary input {
    color: #0097cc;
    background: #333;
    border: none;
}

.product-core-inner {
    position: relative;
    background: #232323;
    max-width: 100%;
}

.pdp-main .product-price {
    color: #fff;
}

.product-info-inner,
.product-right-col-inner {
    background: #fff;
    background: #232323 !important;
}

.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    color: #0097cc;
    background-color: #232323;
}

.list-group-item {
    background: #232323;
}

.ui-dialog .modal-header, .ui-dialog .ui-widget-header {
    background: #232323;
}

.pdp-zoom-dialog .dialog-content {
    background: #232323;
}

/* Favourite page */

.favourites-title {
    color: #cc092f;
}

.search-result-items .grid-tile {
    background: #222;
}

/* Basket stuff */

.mini-cart-header {
    background: #18181a;
    border-bottom: 1px solid #d8d8d8;
    color: #0097cc;
}

.btn-light-grey, .quantity-module-light-grey span {
    color: #ccc;
    background-color: #0097cc;
    border-color: #004760;
}

.quantity-module-sm input {
    background-color: #555 !important;
}

.mini-cart-content {
    border: 1px solid #222;
    background: #18181a;
}

.mini-cart-products {
    -ms-flex-preferred-size: 100%;
    flex-basis: 100%;
    overflow: auto;
    background: #232323;
}

.mini-cart-name a,
.mini-cart-price,
.mini-cart-subtotals .value {
    color: #ddd;
}

.delivery-cart-container .delivery-availability-message {
    background: #232323;
}

/* Account page */

.home-last-order .inner {
    padding: 16px;
    background: #232323;
}

.home-last-order .last-order-date {
    border: none;
    background: #232323;
    padding: 0 34px;
}

.content-asset:not(.reset-styles) .account-options {
    filter: brightness(75%);
}

.personal-data-content {
    background: #232323
}

.form-control:disabled, .form-control[readonly] {
    background-color: #18181a;
    opacity: 1;
}

.floatlabel input + label > span, .floatlabel textarea + label > span {
    background-color: #18181a;
    box-shadow: none;
}

.floatlabel.selectbox label span {
    color: #ddd;
    position: relative;
    top: -8px;
    height: 2px;
    background: none;
}

.card,
.card-header,
.address-tile .card-header,
.card-footer,
.add-address-tile {
    background-color: #232323;
    background-clip: border-box;
    border: 1px solid #333;
}

.payment-cards-add {
    background-color: #232323;
}

.floatlabel input.text-entered + label span, .floatlabel input:focus + label span, .floatlabel textarea.text-entered + label span, .floatlabel textarea:focus + label span {
    color: #ddd;
    position: relative;
    top: -8px;
    height: 2px;
    background: none;
}

/* Basket */

.cart-actions.cart-actions-top .top-options-display .card-body,
.cart-actions.cart-actions-top .top-options-display,
#cart-table tbody tr, .product-table tbody tr, .pt_gift-registry fieldset .item-list tbody tr,
#cart-table{
    background: #232323;
}

.item-list tr {
    border-bottom: 1px solid #444;
}

.cart-footer,
.cart-payments-info,
.cart-actions.cart-actions-bottom .cart-buttons-actions {
    background: #232323;
}

.before-you-go-incomplete > h2,
.before-you-go-promo-details,
.before-you-go-container.bottom-container form {
    background: #18181a;
}

/* Checkout */

.checkout-block .checkout-block-header,
.checkout-block .checkout-block-content,
.checkout-order-totals,
.delivery-schedule-day-body,
.delivery-schedule-slot,
.delivery-confirmation-modal .delivery-confirmation-top,
.ui-widget-content, .ui-widget-header,
.delivery-confirmation-footer,
.order-receipt-message div,
.order-receipt .order-receipt-box,
.order-receipt-details .order-shipment-table .line-items .line-item,
.order-receipt-details .order-detail-summary .sub-title,
.order-totals-table {
    background: #232323;
}

.order-receipt-info {
    position: relative;
    background: #232323 !important;
        background-color: #232323 !important;
    padding: 25px 0;
    margin-top: 2px;
}

.order-receipt-details .order-shipment-table .line-items .line-item.line-items-labels {
    background: #232323;
}

/* Orders page */

.pt_order .primary-content .search-result-items.current {
    background: #232323;
}

.progress .fa-check-circle {
    position: absolute;
    top: -3px;
    background:#232323; 
}


.view-count .btn-group .button {
    background: #444;
    border-color: #d8d8d8;
}

.view-count .btn-group .button.active {
    background: #333;
    border-color: #d8d8d8;
}

.pt_order .primary-content .search-result-items {
    background: #232323;
}

.order-history-row:nth-child(2n) {
    background: #1d1d1d;
}

/* View Order Page */

.orderdetails-container,
.orderdetails 
.order-info 
.order-info-item.secondary{
    background: #232323;
}

.orderdetails .line-items .line-item.line-item-title {
    background: #292929;
}

.orderdetails .line-items .line-item {
    background: #232323;
}

.orderdetails .orderdetails-separator {
    height: 10px;
    background: #18181a;
}

.order-details-actions {
    background: #292929;
}

.orderdetails .line-items .line-item .line-item-price {
    color: #fff;
}
}
