css:
@-moz-document domain("web.telegram.org"), domain("web.telegram.org.ru"), domain("web.tlgrm.eu")
{
  
body {
    color: #262626;
}
body.non_osx
  {
font: 14px Open Sans, Segoe UI,Helvetica Neue,Helvetica,Lucida Grande,Arial,Ubuntu,Cantarell,Fira Sans,sans-serif;
    letter-spacing: 0.15px;
  }

div.page_wrap {
    width: 90%;
    margin: 0 auto;
}

.im_page_wrap {
	max-width: 1550px;
}

.tg_head_split {
    max-width: 1550px; 
    background: whitesmoke;
}


.icon-hamburger-wrap .icon-bar {
    background: #262626;
}

.tg_head_peer_media_dropdown .dropdown-toggle .icon-bar {
    display: block;
    background-color: #262626;
}

.tg_head_btn {
    color: silver;
}

.tg_head_peer_title_wrap {
    overflow: hidden;
    padding-left: 50px;
}

.tg_head_peer_title {
    color: #262626;
    font-size: 15px;
}

.im_dialog_message_wrap {
    height: 48px;
}

.im_dialogs_scrollable_wrap a.im_dialog {
    padding: 14px 16px;
}

.nav-stacked>li {
    float: none;
    border-top: 1px solid lavenderblush;
}

div.im_dialog_photo .peer_initials {
    line-height: 48px;
    font-size: 0px;
    background: gainsboro;
}

.im_dialogs_scrollable_wrap .active a.im_dialog {
    border-radius: 0;
    background-color: #f1f1f1;
}

.active .im_dialog_date, .active a.im_dialog:hover .im_dialog_date, .active a.im_dialog_selected .im_dialog_date {
    color: grey;
}

.active .im_dialog_peer {
    color: #262626;
}

.active a.im_dialog .im_dialog_message {
	color: #262626;
}

a.im_dialog .im_dialog_chat_from_wrap, a.im_dialog .im_short_message_media, a.im_dialog .im_short_message_service {
    color: #262626;
}

.active a.im_dialog .im_dialog_chat_from_wrap{
    color: #262626;
}

.active a.im_dialog .im_short_message_text {
       color: #262626;
}

.im_message_wrap {
    max-width: none;
}

a.im_message_contact_photo, a.im_message_from_photo {
    margin: 0 15px 0 0;
    display: none;
}

.im_content_message_wrap {
    margin: 8px 10px 8px 16px;
    border: 1px solid gainsboro;
    border-radius: 8px;
    padding: 10px;
}

.im_send_panel_wrap {
    max-width: none;
    padding-bottom: 21px;
}

.im_send_form {
    max-width: none;
    left: 5%;
    right: 5%;
}

.im_panel_own_photo {
    width: 42px;
    height: 42px;
    line-height: 42px;
    border-radius: 50%;
    overflow: hidden;
    position: fixed;
    top: 4px;
    left: 15%;
}

img.im_panel_peer_photo {
    width: 42px;
    height: 42px;
}

div.im_panel_peer_photo, a.im_panel_peer_photo {
    width: 42px;
    height: 42px;
    line-height: 42px;
    margin-left: 0;
    overflow: hidden;
    border-radius: 50%;
    position: fixed;
    top: 4px;
    left: 70%;
}

.im_history_selected_wrap {
	background: url(https://i.imgur.com/NVFKCcI.jpg);
    background-size: 35%;
}

.im_history_messages {
	padding: 0 3%;
}

.im_send_form_wrap1 {
background-color: white;
    border: 1px solid silver;
    border-radius: 8px;
    padding: 2% 0;
    margin: 0 4%;
}

.im_message_author, .im_message_fwd_author {
    color: darkgrey;
    font-weight: 400;
}

.composer_rich_textarea {
    font-size: 14px;
}

html {
  background: linear-gradient(180deg, rgba(13,129,148,1) 0%, rgba(13,129,148,1) 0%, rgba(13,129,148,1) 32%, rgba(237,237,237,1) 100%);
}

.im_message_text {
    line-height: 130%;
    display: inline-block;
    max-width: 100%;
 }

div.im_message_author, div.im_message_body {
    overflow: visible;
}

.im_grouped_short .im_message_body {
	margin-left: 0;
}

.im_dialog_unread {
    display: none;
}

.im_grouped .im_message_date {
	display: none;
}
.im_grouped_short .im_message_date {
	display: none;
}

.im_content_message_wrap.im_message_out {
	float: right;
    background: #dcf8c6;
    margin-left: 40%;
    max-width: 100%;
    min-width: 25%;
}

.im_content_message_wrap.im_message_in {
    float: left;
    background: #fff;
    margin-right: 40%;
    max-width: 100%;
    min-width: 25%;
}
}
