  }
	@keyframes FocusZoom
  {
    0% {
			transform: scale(1.3);
  }

  25%
  {
    transform: scale(1.32);
  }

  50%
  {
    transform: scale(1.3);
  }

  51%
  {
    transform: scale(1.3);
    filter: brightness(100%);
  }

  75%
  {
    transform: scale(1.32);
    filter: brightness(120%);
  }

  100%
  {
    transform: scale(1.3);
    filter: brightness(100%);
  }
}

