#dance-transition .dt, #dance-transition .dt-row, #dance-transition.animate .dt, #dance-transition.animate .dt-row, #dance-transition.animate.in .dt, #dance-transition.animate.in .dt-row, #dance-transition.animate.out .dt, #dance-transition.animate.out .dt-row
{
  background-position: center!important;
  margin-top: 0!important;
  margin-bottom: 0;
  margin-left: 0;
  width: 100%;
  height: 100%;
  background-size: cover!important;
  -webkit-transform: none!important;
  transform: none!important;
  background-repeat: no-repeat!important;
}

#dance-transition.animate.in .dt-row
{
  -webkit-transition-property: opacity!important;
