  height: 100%;
  z-index: 1;
  transform: scale(1.2);
  animation: backe 2.5s;
}

@keyframes backe
{
  100%
  {
    background-position: 0px 0px, -0px 0px center, center!important;
    opacity: 1;
    right: -10%;
  }

  50%
  {
    background-position: 0px 0px, 0px 0px center, center!important;
    opacity: 1;
  }

  0%
  {
    background-position: -10px 0px, -35px 0px center, center!important;
    opacity: 1;
    right: 10%;
  }
}
