	/*----------STARS----------*/
@keyframes megal
{
  0%
  {
    transform: scale(1.0);
  }

  25%
  {
    transform: scale(1.4);
  }

  50%
  {
    transform: scale(1.0);
  }

  51%
  {
    filter: brightness(100%);
  }

  75%
  {
    transform: scale(1.0);
    filter: brightness(130%);
  }

  100%
  {
    filter: brightness(100%);
  }
}

@keyframes supel
{
  0%
  {
    transform: scale(1.0);
  }

  25%
  {
    transform: scale(1.2);
  }

  50%
  {
    transform: scale(1.0);
  }

  51%
  {
    filter: brightness(100%);
  }

  75%
  {
    transform: scale(1.0);
    filter: brightness(120%);
  }

  100%
  {
    filter: brightness(100%);
  }
}

@keyframes mupel
{
  0%
  {
    transform: scale(1.0);
  }

  25%
  {
    transform: scale(1.1);
  }

  50%
  {
    transform: scale(1.0);
  }

  51%
  {
    filter: brightness(100%);
  }

  75%
  {
    transform: scale(1.0);
    filter: brightness(110%);
  }

  100%
  {
    filter: brightness(100%);
  }
}

