@-moz-document domain('booky.io') {
  body {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }

  #dashboards .title {
      font-family: Calibri, Tahoma, sans-serif !important;
  }
  #dashboards .dashboard.current .title {
      font-family: Calibri, Tahoma, sans-serif !important;
      font-weight: bold !important;
  }

  .portlet-header {
      font-family: Calibri, Tahoma, sans-serif !important;
      font-size: 26px !important;
      line-height: 27px !important;
  }
  .portlet-content {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }

  .button-style-small {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  .button-style-small-grey {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  .button-style-medium {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  #cancel {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  #done {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  #show-login {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  #login-box {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
  input {
      font-family: Segoe UI, Tahoma, sans-serif !important;
  }
}
