/* ==UserStyle==
@name         Mozilla Blogs Dark
@version      1.0.6
@description  Dark theme for the Mozilla Blogs
@namespace    AfroThundr3007730
@author       AfroThundr
@homepageURL  https://github.com/AfroThundr3007730/Mozilla-Blogs-Dark
@supportURL   https://github.com/AfroThundr3007730/Mozilla-Blogs-Dark/issues
@updateURL    https://raw.githubusercontent.com/AfroThundr3007730/Mozilla-Blogs-Dark/master/mozilla-blogs-dark.user.css
@license      GPL-3.0
==/UserStyle== */
@-moz-document domain("blog.mozilla.org"),
               domain("blog.nightly.mozilla.org"),
               domain("hacks.mozilla.org") {
  :root {
    --gray-1: #111;
    --gray-2: #222;
    --gray-3: #333;
    --gray-5: #555;
    --gray-a: #aaa;
    --gray-c: #ccc;
    --gray-48: #484848;
    --gray-9a: #9a9a9a;
    --blue-1: #00a7e0;
    --blue-2: #093153;
    --blue-3: #0f2639;
  }
}
@-moz-document domain("blog.mozilla.org"),
               domain("blog.nightly.mozilla.org") {
  body {
    color: var(--gray-a);
    background-color: var(--gray-1);
  }
  .nav-global, .nav-paging, #categories .cat-list a.on,
  .single .category-news .entry-content h3,
  .page .category-news .entry-content h3,
  .single .category-news .entry-content h4,
  .page .category-news .entry-content h4 {
    background-color: var(--gray-2);
  }
  .entry-link:link, .entry-link:visited, .nav-paging a, #related-posts a:link,
  #related-posts a:visited, .nav-mozilla-menu a {
    color: var(--blue-1);
  }
  .entry-info {
    color: var(--gray-48);
  }
  .pagination a, .pagination .current, #categories.is-active, #sidebar.is-active {
    background-color: var(--gray-3);
  }
  #sidebar .widget_nav_menu li, #sidebar .widget_recent_entries li,
  #sidebar .widget_recent_comments li, #categories .cat-list a,
  #categories .cat-list a:hover {
    background-color: var(--gray-1);
  }
  .nav-global .logo a {
    filter: invert(100%) brightness(60%);
  }
  #site-info .logo {
    filter: brightness(50%);
  }
  .nav-global-twitter a:before, .nav-global-join a:before,
  .nav-global-donate a:before {
    filter: invert(50%);
  }
}
@-moz-document domain("hacks.mozilla.org") {
  body {
    color: var(--gray-9a);
    background-color: var(--gray-1);
  }
  .newsletter, .post code, .post pre {
    background-color: var(--gray-2);
  }
  .comments .children {
    border-left-color: var(--gray-3);
  }
  .article img[src*=".svg"] {
    background-color: var(--gray-c);
  }
  .footer {
    background-color: var(--blue-2);
  }
  .header {
    background-image: /*url("img/blueprint.png"),*/
                          linear-gradient(to bottom, var(--blue-3), var(--blue-2));
  }
  button, input, optgroup, select, textarea {
    background-color: var(--gray-1);
    border-color: var(--gray-5);
    color: var(--gray-c);
  }
  hr.dino::after {
    filter: invert(100%);
  }
}
