css:
@-moz-document domain("fullstackopen.com") {
body,
html {
    background-color: #2e3138;
}

body,
html,
a,
p,
.sub-header,
h1,
h2,
h3,
h4,
h5,
h6,
.body-text__title {
    color: #fff;
}

.header,
.header.header--small .triple-border__logo {
    background-color: #2e3138 !important;
    z-index: 10000;
}

.header.header--small {
    background-color: transparent !important;
    z-index: 100 !important;
}

.nav-item-hover {
    color: #fff;
}

.triple-border::after,
.triple-border::before {
    border: 2px solid #fff;
}

.arrow__rectangle a,
.banner.part-intro__banner p,
.accordion__container .body-text__content,
.accordion__container .body-text__content a {
    color: #33332d;
}

.course-container.spacing--after {
    z-index: 1000;
}

code[class*="language-"],
pre[class*="language-"] {
    font-family: Hack, Consolas, Monaco, Andale Mono, Ubuntu Mono, monospace;
}

.banner.spacing.spacing--after.tasks,
.banner.spacing.spacing--after.tasks p,
.banner.spacing.spacing--after.tasks a,
.banner.spacing.spacing--after.tasks h1,
.banner.spacing.spacing--after.tasks h2,
.banner.spacing.spacing--after.tasks h3,
.banner.spacing.spacing--after.tasks h4,
.banner.spacing.spacing--after.tasks h5,
.banner.spacing.spacing--after.tasks h6 {
    color: #33332d;
}
}
