/* ==UserStyle==
@name			prepchem
@author			DavidJRobertson
@description	Print styles for prepchem.com
@version		1.0
@namespace		DavidJRobertson
@license		Public domain
==/UserStyle== */

@-moz-document domain("prepchem.com"), domain("www.prepchem.com")
{
	
	ins.adsbygoogle,
	.top-widgets-area { 
	    display: none !important;
	}
	
	@media print {
	    #footer,
	    a.ct-totop, 
	    #secondary, 
	    ins.adsbygoogle,
	    .top-widgets-area,
	    .yarpp-related,
	    .comments-block, 
	    .nav-block,
	    .entry-extra,
	    .entry-meta,
	    #mainmenu-block-bg {
	        display: none !important;
	    }
	    #primary {
	        width: 90% !important;
	    }
	    #logo a img {
	        width: 6cm !important;
	    }
	    a {
	        text-decoration: underline !important;
	    }
	    
	    #logo a[href]:after, .wp-caption a[href]:after {
	        content: none !important;
	    }
	    
	    h2 {
	        font-size: 250% !important;
	        margin-bottom: 1.25cm;
	        text-decoration: underline !important;
	    }
	    .wp-caption {
	        border: 1px solid black;
	    }
	    #content {
	        margin-top: 0px !important;
	    }
	    .prepc-bellow_h1 {
	        display: none !important;
	    }
	    
	    h1.entry-title, .entry-content {
	        padding: 0px !important;
	    }
	    
	    /* not sure */
	    a[href]:after {
	        content: none !important;
	    }
	}
}
