css:
@-moz-document url("https://24h.pchome.com.tw/"), url("https://shopping.pchome.com.tw/"), url("https://24h.pchome.com.tw/index/"), url("https://shopping.pchome.com.tw/index/") {

#HEADER * {
    float: unset !important;
    clear: both !important;
    position: static !important;
    background: none !important;
    box-sizing: border-box;
    color: white;
}
*::before {
    display: none;
    
}

body {
    background: #333;
}
.hp_w, .hp_w #WRAPPER {
    width: 100%;
}

#WRAPPER {
    width: 100%;
}

#HEADER {
    width: 100%;
    height: 90vh;
    overflow: hidden;
    padding: 0;
}
#HEADER .logotype {
    display: none;
}
#HEADER .logotype+.site_nav {
    width: 100%;
    margin: 0;
    margin-left: 0;
    padding: 0 !important;
    height: 3em;
    background: black !important;
    border-bottom: none;
}
.logotype+.site_nav > * , .logotype+.site_nav > * > li {
    display: inline-block;
}
.logotype+.site_nav > * > li::before {
    display: none;
}
.main_site li>a, .service li>a, .service .more {
    color: white;
    text-decoration: none;
}
.logotype+.site_nav ul {
    height: 100%;
}
.logotype+.site_nav li, .logotype+.site_nav li * {
    height: 100%;
    font-size: 1rem;
    line-height: 2.3rem;
}
#HEADER #MainSiteContainer::before, .hp_w .Ht .site_nav:before {
    display: none;
}
#HEADER #MainSiteContainer {
    width: unset;
}
#HEADER #ServiceContainer {
    width: unset;
}
#site_ad_topLeft {
    display: none;
}

.block_H .sp {
    display: none;
}
.block_H > * > *:not(#SearchForm) {
    display: none;
}
.block_H.relative {
    background: none;
}

#SearchForm {
    width: 30em;
    margin: auto;
    margin-top: 4em !important;
    padding: 0;
    background: none;
    border: none;
    font-size: 2rem;
    height: 3rem;
    margin-top: 2rem;
}
#SearchForm input:first-of-type {
    width: calc(100% - 10rem);
    display: inline-block;
    height: 3rem;
    border: solid 2px #ddd;
    border-radius: 5px;
}
#SearchForm input:last-of-type {
    width: 5rem;
    display: inline-block;
    height: 3rem;
    border: solid 2px #ddd;
    border-radius: 5px;
}

#SearchForm *:not(input) {
    display: none !important;
}

#SearchForm:before {
    content: "";
    display: block;
    width: 30rem;
    height: 10rem;
    background: url('https://d.ecimg.tw/img/activity/v1/AC59630/AC59630199/img/24HLOGO.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
    margin: auto;
    padding-bottom: 1em;
    transition-duration: .3s;
    
}
#SearchForm:hover:before {
    transform: rotate(-5deg) scale(1.3);
}
#CONTENT {
    width: 1250px;
    margin: auto;
    filter: invert(1);
}
#CONTENT img {
    filter: invert(1);
}


}

@-moz-document domain("24h.pchome.com.tw"), domain("shopping.pchome.com.tw"), domain("ecssl.pchome.com.tw"), domain("pchome.com.tw") {


html, body, .logotype + .site_nav, .bar_search, .style_24h {
