    background:#555;
    color:#aaa;
}
.bar_showmode, .style_24h .Nm, .block_N::before, .Cb .price, .type_2, .type_3, .type_4, .style_24h .layout-center, .style_24h .leaderboard dd, .style_24h .category dl, .style_3c .type_1, .style_3c .type_2, .style_3c .type_2 .price, .Cb .body, .site_nav, .Ht .site_nav::before, .block_H, .block_H sup.sp, .block_H sub.sp {
