.prod_recommend .tabs li, .mMV, .mL, .style_24h .block_N .Nt, .table_galy, .site_ticrf, .s_border, .Cm_Cc .body, .Cm_Cr > dl, .style_24h .site_tooth, .style_24h .site_tooth .r3f a, .style_24h .site_tooth .r2f a, .style_24h .site_tooth .r1f a, .Cb .body, .block_W .site_nav, .hot_list, .msg_list, .site_mkt .body, .site_mkt .head, .festival.extend .head, .style_mall .festival.extend .head, .h4 dd, .Cr1 .body, .Cl .body, .bar_search {
    border-top-color: #666 !important;
