.style_24h .block_N, .Cb .body .s_border, .type_3 .body, .type_3 .foot, .style_life .type_1, .style_life .type_2, .style_life .type_2 .price, .grab_box .s_border .prod_name a, .Cm_Cr .type_1 .prod_name a, .site_nav, .hot_list, .msg_list, .E3, .site_mkt .body, .site_mkt .c1f, .Cl1 .body {
    background-color:#444;
    color:#aaa;
}
.site_mkt .body li, .site_mkt .head, .festival.extend .head, .style_mall .festival.extend .head {
    border-bottom:1px solid #555;
}
.Cl2 .c2f a, .site_mkt .c2f span a, .Cr1 h5 a, .Cl1 h5 a {
    color:#0a5;
}
.site_mkt .c2f h6 strong {
    color:#F33;
}
.site_loop .price, .price, .site_loop .value, .site_mkt .c2f h5, .Cr1 h6 strong, .Cr1 h6 a, .Cl1 h6 strong, .Cl1 i a {
    color:#F74;
}
.winlink, .Ht, .style_24h .searchfield + .msg, .block_N .right a, .block_N .right .msg, .style_24h .Hm .shipping, .note, .slogan, .category a, #FOOTER.ecsite-footer li:nth-last-child(3) a, .E2 .body li h5 a, .E3 .body li, .E3 .body li a, .site_nav .col2f a, .site_nav .body dt a {
    color:#aaa;
}
.Ct_S .site_marketing .msg, .alink, .back, a {
    color:#aaa;
}
.block_N .msg_box .list_box li:first-child, .event_discount, .hot_list .c2f h6 strong a, .hot_list .c2f h6 strong {
    color:#Fa8;
}
.prod_recommend .tabs a, .block_W .head, .E3 .head {
    color:#eee;
    background-color:#666;
}
.overlay_roadsign dd {
    background:#666;
}
.overlay_roadsign ul li a {
    color:#ddd;
}
.overlay_roadsign ul li a:hover {
    color:#333;
}
.pro-name, .content_td, .order_table .content_tr td, .order_td td, strong {
    color:#ddd;
}
font {
    color:#aaa;
}
.prod_describe .nick, .Cb dt, .site_loop .prod_name a, .site_loop .nick a, .type_3 .body a {
    color:#ddd;
}
.style_24h .block_S.go_top {
    background-color:#555;
    color:#666;
}
.service .cart {
    color:#f4a;
}
.order_table {
    background-color:#888;
}
.table_galy, .content_td, .order_table .content_tr td {
    background-color:#666;
}
.middle, .discountRow {
    background-color:#666;
}
td, .span {
    color:#aaa;
}
.category .actived a {
    background-color:#c00;
    color:#eee;
}
.mL::after, .mL::before {
    background:linear-gradient(to bottom, #666 1%, #aaa 100%);
}
.s_grid .site_marketing {
    background-color:#444;
}
.collapsible_box, .block_C, .cart_box .prod_name>a, .sign, .record_form a, .s_marketing, .clause_box a, .b-ord, .back, .s_gift .fieldset_box {
    color: #eee;
}
.record_form .collapsible {
    background: #999;
    color: #fff;
    z-index: 1;
}
.cart_form .footer {
    margin-top: 16px;
    padding: 16px 0;
    border-top: 1px dotted #ccc;
    background: #333;
}
.s_board {
    margin-top: 40px;
    padding: 6px 10px;
    color: #eee;
}
.s_cart dt {
    background-color: #777;
}
.choice_box .head .msg_box {
    line-height: 2;
    background-color: #777;
}
.choice_box .head dd {
    margin-left: 18px;
    color: #eee;
}




}

