/*
 * @date 2021-04-03T02:57:54.653
 */
/*
 * 变量配置
 * optional.txt
 */
@-moz-document domain("keylol.com") {
	/*
	 * 变量配置
	 */
	:root {
		--color50: var(--/*[[theme]]*/50);
		--color100: var(--/*[[theme]]*/100);
		--color200: var(--/*[[theme]]*/200);
		--color300: var(--/*[[theme]]*/300);
		--color400: var(--/*[[theme]]*/400);
		--color500: var(--/*[[theme]]*/500);
		--color600: var(--/*[[theme]]*/600);
		--color700: var(--/*[[theme]]*/700);
		--color800: var(--/*[[theme]]*/800);
		--color900: var(--/*[[theme]]*/900);
		--colorA100: var(--/*[[theme]]*/A100);
		--colorA200: var(--/*[[theme]]*/A200);
		--colorA400: var(--/*[[theme]]*/A400);
		--colorA700: var(--/*[[theme]]*/A700);

		--color-accent: var(--color500);
		--md-block-bg: var(--color500);

		--md-btn-light-text: var(--white1);
		--md-btn-light-text-hover: var(--white1);
		--md-btn-light-text-acrive: var(--white1);
		--md-btn-dark-text: var(--black1);
		--md-btn-dark-text-hover: var(--black1);
		--md-btn-dark-text-acrive: var(--black1);
	}
}

/*
 * @date 2021-04-03T02:57:54.653
 */
/*
 * 导航栏
 * nav.css
 */
@-moz-document domain("keylol.com") {
	:root {
		--nav-height: 56px;
		--nav-bg: var(--color500);

		--nav-meun-item-height: 40px;
		--nav-meun-item-divider: var(--color700);
		--nav-menu-bg: var(--color500);
		--nav-menu-item-bg-hover: var(--color300);
		--nav-meun-item-text: var(--white1);

		--nav-btn-bg: var(--color500);
		--nav-btn-bg-hover: var(--color300);
		--nav-btn-text: var(--white1);

		--nav-nav-menu-offset-left: -356px;
	}

	/* region 导航栏下拉菜单 */
	/* region 横向菜单 */
	#nav-menu > ul > li.has-sub:nth-child(6) {
		display: none;
	}

	#nav-menu > ul {
		display: inline-flex;
		justify-content: flex-start;
	}

	#nav-menu > ul > li {
		width: unset;
		float: unset;
		padding: 0;
		margin: 0;
	}

	#nav-menu > ul > li.has-sub > a:after {
		display: none;
	}

	#nav-menu > ul > li.has-sub > a {
		display: inline-flex;
		align-items: center;
		height: var(--nav-height);
		padding: 0 24px;
		font-size: 14px;
	}

	#nav-menu > ul > li:after,
	#nav-menu > ul > li.has-sub > a:hover {
		background: var(--nav-menu-item-bg-hover);
	}

	/* endregion 横向菜单 */

	/* region 下拉菜单 */
	#nav-menu > ul > li > ul {
		display: flex;
		flex-direction: column;
		align-items: start;
		background: var(--nav-menu-bg);
		width: unset;
		padding: 8px 0;
		border-radius: 0 0 4px 4px;
		-webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
		box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12)
	}

	#nav-menu > ul > li > ul > li {
		border-top: none;
		width: unset;
	}

	#nav-menu .dual-line a,
	#nav-menu > ul > li > ul > li > a {
		background: var(--nav-menu-bg);
		color: var(--nav-meun-item-text);
		display: inline-flex;
		flex-direction: column;
		justify-content: center;
		text-align: left;
		min-width: 110px;
		width: max-content;
		height: var(--nav-meun-item-height);
		padding-left: 16px;
		padding-right: 16px;
		transition: all .2s;
	}

	#nav-menu > ul > li > ul > li > a:hover {
		background: var(--nav-btn-bg-hover);
	}


	/* endregion 下拉菜单 */
	/* endregion 导航栏下拉菜单 */

	/* region logo*/
	#nav-logo > span {
		opacity: 0;
	}

	#nav-logo {
		width: 40px;
		height: 40px;
		background: var(--white1);
		-webkit-mask-position: center;
		-webkit-mask-repeat: no-repeat;
		-webkit-mask-image: url(data:image/png;base64,);
	}

	/* endregion logo  */

	#nav-additional {
		display: flex;
		align-items: center;
	}

	/*region 搜索框*/
	#nav-additional .search-box {
		color: var(--white1);
		padding-left: 8px;
		width: 110px;
	}

	#nav-search-bar {
		background: var(--color300);
		border-radius: 18px;
		border: 1px solid var(--color300);
		width: unset;
		padding: 0 4px;
		margin-right: 8px;
	}

	#nav-search-bar form,
	#nav-search-bar input,
	#nav-search-bar button {
		background: transparent;
	}

	#nav-search-bar > .search-bar {
		padding: 0;
	}

	#nav-additional form.search-bar-form {
		display: flex;
		align-items: center;
		padding: unset;
	}

	.search-bar button[type=submit],
	.search-bar:hover button[type=submit] {
		width: unset !important;
		height: unset !important;
	}

	.search-bar input.search-box::-webkit-input-placeholder {
		color: var(--white1);
	}

	.search-bar input.search-box {
		height: 28px;
		outline: none;
	}

	.search-bar .dropdown .btn {
		color: var(--white1);
		border: none;
		outline: none;
		font-size: 12px;
	}

	.search-bar span.caret {
		display: none;
	}

	/*endregion*/

	/*region 关注的人、消息、提醒*/

	#nav-user-action-bar > ul {
		display: flex;
		align-items: center;
	}

	#nav-user-action-bar > ul > span > a,
	#nav-user-action-bar > ul > li:not(.dropdown) > a {
		background: var(--nav-btn-bg);
		color: var(--nav-btn-text);
		border-radius: 3px;
		padding: 4px 10px;
		margin: 0 4px;
		line-height: unset;
		font-size: 12px;
		cursor: pointer;
		text-decoration: none;
		display: inline-flex;
		align-items: center;
	}

	#nav-user-action-bar > ul > span > a:hover,
	#nav-user-action-bar > ul > li:not(.dropdown) > a:hover {
		background: var(--nav-btn-bg-hover);
	}

	#nav-user-action-bar .btn-user-action.btn-user-action-highlight,
	#nav-user-action-bar .btn-user-action.btn-user-action-highlight .badge {
		animation: unset;
	}

	#nav-user-action-bar .btn-user-action .badge {
		background: unset;
		margin-left: unset;
		top: unset;
		padding-right: 0;
	}

	.list-inline > li {
		padding: unset;
	}

	/* endregion 关注的人 */

	/* region 头像 */

	header img {
		vertical-align: unset;
	}

	/* endregion 头像 */

	/* region 头像弹出菜单*/
	#nav-user-action-bar .dropdown-menu.dropdown-menu-right {
		background: var(--nav-menu-bg);
		border-radius: 3px;
		border: 0;
		-webkit-box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
		box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
	}

	#nav-user-action-bar .divider {
		margin: 5px 0;
		background: var(--nav-meun-item-divider);
	}

	#nav-user-action-bar .dropdown-menu.dropdown-menu-right a {
		color: var(--nav-meun-item-text);
		text-align: left;
		font-size: 13px;
		padding: 10px 20px;
	}

	.dropdown-menu > li > a:hover,
	.dropdown-menu > li > a:focus {
		background: var(--nav-menu-item-bg-hover);
	}

	/* endregion*/

	/* region 滚动到顶部*/
	#J_ScrollTopBtn {
		border-radius: 50%;
		background-color: var(--color500) !important;
		-webkit-box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
		box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);

		transition: all .2s ease-in-out;
	}

	#J_ScrollTopBtn:active {
		-webkit-box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
		box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	}

	#J_ScrollTopBtn {
		background-image: none !important;
	}

	#J_ScrollTopBtn:before {
		content: '';
		display: inline-block;
		width: 32px;
		height: 32px;
		position: absolute;
		top: 13px;
		right: 6px;
		background-size: 129%;
		background-repeat: no-repeat;
		background-position-x: -10px;
		background-image: url(data:image/png;base64,);
	}

	/* endregion*/

	/*region 导航栏背景*/
	#nav-menu {
		height: var(--nav-height);
