	:root {
	    --simple-cubic-bezier: cubic-bezier(0, 0, .2, 1);
	    --post-title-text: var(--white1);
	    --post-breadcrumb-text: var(--white1);
	}
	
	/* region 主题色的背景条*/
	.floatcontainer.doc_header.wp.cl {
	    height: 0;
	}
	
	body:before {
	    content: '';
	    display: inline-block;
	    width: 100%;
	    min-width: 1200px;
	    height: 500px;
	    background: var(--md-block-bg);
	    position: absolute;
	    z-index: -1;
