@-moz-document regexp(".+keylol.com.+mod=post&action=(newthread|reply|edit).*") {
	/*
	 * 发新贴
	 * new-thread.css
	 * @url https://keylol.com/forum.php?mod=post&action=newthread&fid=127&cedit=yes&extra=
	 */
	#postform {
	    --icon-width: 26px;
	    --icon-height: 26px;
	    --icon-margin: 2px;
	    --icon-bg: #f7f7f7;
	    --icon-bg-hover: var(--color700);
	    --btn: var(--white1);
	    --btn-bg: var(--color500);
	}
	
	/* region 编辑器宽度 */
	.wp {
	    min-width: 1300px;
	    max-width: 1300px;
	}
	/* endregion 编辑器宽度 */
	
	/* region 隐藏 */
	.floatcontainer.doc_header.wp.cl,
	#pt,
	#subjectchk,
	#subjectchk + script + .xg1.xw0 {
	    display: none !important;
	}
	
	#wp {
	    padding-top: 70px;
	}
	
	/* endregion 隐藏 */
	
	/* region 发表帖子、发起投票、发布悬赏、发起辩论、发起活动、草稿箱*/
	.tb {
	    display: flex;
	    margin-top: 10px !important;
	    line-height: unset;
	    border-bottom: unset;
	}
	
	ul.tb > li {
	    margin-right: 5px;
	}
	
	ul.tb.cl.mbw > li > a {
	    border: unset !important;
	    color: var(--btn);
	    background: var(--btn-bg) !important;
	    display: inline-block;
	    text-decoration: none;
	    padding: 6px 8px;
	    border-radius: 2px;
	
	    -webkit-box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12) !important;
	    transition: all .2s ease-in-out;
	}
	
	ul.tb.cl.mbw > li > a:hover {
	    opacity: .87;
	    -webkit-box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12) !important;
	}
	
	ul.tb.cl.mbw > li > a:active {
	    -webkit-box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	}
	
	ul.tb.cl.mbw > li > a:before {
	    content: '';
	    width: 16px;
	    height: 16px;
	    display: inline-block;
	    position: relative;
	    top: 3px;
	    margin-right: 5px;
	    background: var(--btn);
	    -webkit-mask-position: center;
	    -webkit-mask-repeat: no-repeat;
	}
	
	/* region 发表帖子 */
	ul.tb.cl.mbw > li:nth-child(2) > a:before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 发表帖子 */
	
	/* region 发起投票 */
	ul.tb.cl.mbw > li:nth-child(3) > a:before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 草稿箱 */
	
	/* region 发起投票 */
	ul.tb.cl.mbw > li:nth-child(4) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* endregion 发起投票 */
	
	/* region 发起辩论 */
	ul.tb.cl.mbw > li:nth-child(5) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* endregion 发起辩论 */
	
	/* region 发起活动 */
	ul.tb.cl.mbw > li:nth-child(6) > a:before {
	    -webkit-mask-image: url(data:image/png;base64,);
	}
	
	/* endregion 发起活动 */
	
	/* region 草稿箱 */
	ul.tb.cl.mbw > li:nth-child(1) {
	    order: 1;
	    margin-left: -2px !important;
	}
	
	ul.tb.cl.mbw > li:nth-child(1) > a:before {
	    -webkit-mask-image: url("data:image/png;base64,");
	    -webkit-mask-size: 100%;
	}
	
	#draftlist_menu {
	    top: 180px;
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	/* endregion 草稿箱 */
	/* endregion  */
	
	/* region re */
	#subjecthide {
	    color: var(--color500);
	    font-size: 28px;
	}
	
	/* endregion re */
	
	/* region 分类 */
	#postbox .ftid,
	#postbox .ftid a {
	    font-size: 16px;
	}
	
	#postbox .ftid {
	    float: unset;
	    display: flex;
	    justify-content: flex-start;
	
	}
	
	/* region select */
	
	#postbox .ftid::before,
	#postbox .ftid a {
	    height: 36px;
	    line-height: 36px;
	}
	
	#postbox .ftid::before {
	    content: '主题分类';
	    display: inline-block;
	}
	
	#postbox .ftid a {
	    text-decoration: none;
	    width: unset !important;
	    border: unset;
	    display: inline-block;
	    margin-left: 8px;
	    padding-left: 8px;
	    padding-right: 23px;
	    border-radius: 5px;
	    color: var(--icon-bg-hover) !important;
	    background: var(--icon-bg);
	    box-shadow: 0 2px 1px -1px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 3px 0 rgba(0, 0, 0, .12);
	    -webkit-box-shadow: 0 2px 1px -1px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 3px 0 rgba(0, 0, 0, .12);
	}
	
	#postbox .ftid a:hover {
	    /*background: var(--grey200);*/
	}
	
	#postbox .ftid a::after {
	    display: inline-block;
	    position: relative;
	    top: -3px;
	    left: 13px;
	    width: 0;
	    height: 0;
	    content: '';
	    border: 4px solid;
	    border-color: transparent var(--icon-bg-hover) var(--icon-bg-hover) transparent;
	    transform: rotate(45deg);
	}
	
	/* endregion select */
	
	/* region option */
	#typeid_ctrl_menu {
	    height: unset;
	    font-size: 14px;
	    padding: 10px 0;
	    border-radius: 0 0 4px 4px;
	    width: unset !important;
	    background: var(--grey100);
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	#typeid_ctrl_menu > ul {
	
	}
	
	#typeid_ctrl_menu > ul > li {
	    color: var(--white3);
	    min-width: 100px;
	    height: 36px;
	    line-height: 36px;
	    padding: 0 16px;
	    transition: all .2s cubic-bezier(.4, 0, .2, 1);
	}
	
	#typeid_ctrl_menu > ul > li:hover {
	    background: var(--grey300);
	}
	
	/* endregion option */
	
	/* endregion 分类、主题 */
	
	/* region 主题 */
	#subject {
	    display: flex;
	    width: 100% !important;
	    height: 48px;
	    -webkit-box-sizing: border-box;
	    box-sizing: border-box;
	    -webkit-appearance: none;
	    padding: 8px 0;
	    margin-top: 5px;
	    margin-bottom: 5px;
	    overflow: hidden;
	    /*font-weight: bold;*/
	    font-size: 28px;
	    line-height: 20px;
	    color: rgba(0, 0, 0, .87);
	    resize: none;
	    background: 0 0;
	    border: none;
	    border-bottom: 1px solid var(--grey300);
	    border-radius: 0;
	    outline: 0;
	    -webkit-box-shadow: none;
	    box-shadow: none;
	    -webkit-transition-timing-function: cubic-bezier(.4, 0, .2, 1);
	    transition-timing-function: cubic-bezier(.4, 0, .2, 1);
	    -webkit-transition-duration: .2s;
	    transition-duration: .2s;
	    -webkit-transition-property: border-bottom-color, padding-right, -webkit-box-shadow;
	    transition-property: border-bottom-color, padding-right, -webkit-box-shadow;
	    transition-property: border-bottom-color, padding-right, box-shadow;
	    transition-property: border-bottom-color, padding-right, box-shadow, -webkit-box-shadow;
	    -moz-appearance: none;
	    appearance: none
	}
	
	#subject:hover,
	#subject:focus {
	    border-bottom-color: var(--icon-bg-hover);
	    -webkit-box-shadow: 0 1px 0 0 var(--icon-bg-hover);
	    box-shadow: 0 1px 0 0 var(--icon-bg-hover);
	}
	
	#postbox .z {
	    float: unset;
	}
	
	#postbox .pbt {
	    margin-bottom: 16px;
	}
	
	/* endregion 主题 */
	
	/* region 编辑框 */
	
	#e_body {
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	/* region 工具栏 */
	
	#e_body,
	#e_body .bar {
	    border-radius: 5px;
	    height: unset;
	}
	
	#e_body {
	    border: 1px solid var(--grey300);
	    padding: unset;
	}
	
	#e_body .bar {
	    padding: 10px 10px;
	    background: #f7f7f7;
	}
	
	/* region 清除浮动 */
	#e_controls .y,
	#e_controls .z,
	.edt .b1r,
	.edt .b2r,
	#e_controls .b2r a,
	.edt .bar a,
	.edt .esb p {
	    float: unset !important;
	}
	
	/* endregion 清除浮动 */
	
	/* region 整体布局 */
	.edt .bar a,
	.edt .b2r a,
	#e_simple,
	#e_fullswitcher,
	#e_switchercheck {
	    border: unset;
	    overflow: unset;
	    padding: unset;
	    display: inline-flex;
	    width: var(--icon-width);
	    height: var(--icon-height);
	    margin: var(--icon-margin);
	    background: #fff;
	    background-position: unset;
	    border-radius: 3px;
	    outline: none;
	    transition: all ease-in-out 300ms;
	}
	
	.edt .bar a:not(.dp),
	.edt .b2r a:not(.dp) {
	    font-size: 0;
	}
	
	.edt .bar a:hover,
	.edt .b2r a:hover,
	.edt .bar a.hover,
	#e_simple:hover,
	#e_fullswitcher:hover,
	#e_switchercheck:hover,
	#e_switchercheck:checked::before {
	    /*background: var(--icon-bg-hover);*/
	    border-radius: 3px;
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	.edt .b1r, .edt .b2r {
	    position: unset;
	    padding: unset;
	    height: unset;
	    border: unset;
	}
	
	#e_adv_2 + .b2r > p {
	    display: flex;
	}
	
	#e_adv_s1 {
	    display: flex;
	}
	
	#e_adv_s3 > p {
	    display: flex;
	}
	
	#e_controls {
	    display: flex;
	    justify-content: space-between;
	}
	
	#e_button {
	    display: flex;
	    justify-content: flex-start;
	}
	
	#e_adv_s3 {
	    display: flex;
	    justify-content: flex-start;
	}
	
	#e_adv_s3 > p {
	    width: unset;
	    flex-direction: column;
	}
	
	#e_controls > .y {
	    order: 1;
	    display: flex;
	}
	
	#e_simple {
	    display: none;
	}
	
	#e_adv_s3 > p a.cst > img {
	    display: none;
	}
	
	/* 全屏 */
	#e_simple, #e_fullswitcher {
	    padding: unset;
	    border: unset;
	    font-size: 0;
	}
	
	#e_controls .mbn {
	    display: flex;
	    height: unset;
	    line-height: unset;
	    margin: unset !important;
	}
	
	#e_fontname {
	    width: 90px;
	}
	
	#e_fontsize {
	    width: 67px;
	}
	
	#e_fontname::before {
	    left: 70px;
	}
	
	#e_fontsize::before {
	    left: 48px;
	}
	
	#e_fontname,
	#e_fontsize {
	    padding-left: 8px;
	    height: var(--icon-height);
	    line-height: var(--icon-height);
	    background: var(--icon-bg);
	    border-radius: 3px;
	    box-shadow: 0 2px 1px -1px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 3px 0 rgba(0, 0, 0, .12);
	    -webkit-box-shadow: 0 2px 1px -1px rgba(0, 0, 0, .2), 0 1px 1px 0 rgba(0, 0, 0, .14), 0 1px 3px 0 rgba(0, 0, 0, .12);
	    color: var(--icon-bg-hover);
	}
	
	#e_fontname::before,
	#e_fontsize::before {
	    display: inline-block;
	    position: relative;
	    top: 7px;
	    width: 0;
	    height: 0;
	    content: '';
	    border: 4px solid;
	    border-color: transparent var(--icon-bg-hover) var(--icon-bg-hover) transparent;
	    transform: rotate(45deg);
	}
	
	
	#e_adv_s2 {
	    width: 210px;
	    display: flex;
	    align-content: flex-start;
	    flex-wrap: wrap;
	}
	
	#e_adv_s1 a {
	    width: calc(var(--icon-width) * 2 + var(--icon-margin) * 2);
	    height: calc(var(--icon-height) * 2 + var(--icon-margin) * 2);
	}
	
	/* endregion 整体布局 */
	
	/* region 图标背景 */
	
	/* region 小图标 */
	#e_controls a:not(.dp)::before,
	#e_controls a:not(.dp):hover::before,
	#e_controls a:not(.dp).hover::before,
	#e_switchercheck::before,
	#e_switchercheck:hover::before,
	#e_switchercheck:checked::before {
	    width: var(--icon-width);
	    height: var(--icon-height);
	    border-radius: 3px;
	    display: flex;
	    justify-content: center;
	    align-items: center;
	    font-weight: bold;
	    font-size: 20px;
	    text-indent: initial;
	    transition: all .2s ease-in-out;
	}
	
	#e_controls a:not(.dp)::before,
	#e_controls a:not(.dp):hover::before,
	#e_controls a:not(.dp).hover::before,
	#e_switchercheck::before,
	#e_switchercheck:hover::before,
	#e_switchercheck:checked::before {
	    content: '';
	    -webkit-mask-repeat: no-repeat;
	    -webkit-mask-position: center;
	}
	
	#e_controls a:not(.dp)::before,
	#e_controls a:not(.dp):hover,
	#e_controls a:not(.dp).hover {
	    background: var(--icon-bg-hover);
	}
	
	#e_controls a:not(.dp),
	#e_controls a:not(.dp):hover::before,
	#e_controls a:not(.dp).hover::before {
	    background: var(--icon-bg);
	}
	
	/* region 分割线 */
	
	#e_inserthorizontalrule::before,
	#e_inserthorizontalrule:hover::before,
	#e_inserthorizontalrule.hover::before {
	    content: '一' !important;
	}
	
	#e_inserthorizontalrule::before {
	    color: var(--icon-bg-hover) !important;
	    background: var(--icon-bg) !important;
	}
	
	#e_inserthorizontalrule:hover::before,
	#e_inserthorizontalrule.hover::before {
	    color: var(--icon-bg) !important;
	    background: var(--icon-bg-hover) !important;
	}
	
	/* endregion 分割线 */
	
	/* region 加粗 */
	#e_bold::before,
	#e_bold:hover::before,
	#e_bold.hover::before {
	    content: 'B' !important;
	}
	
	#e_bold::before {
	    color: var(--icon-bg-hover) !important;
	    background: var(--icon-bg) !important;
	}
	
	#e_bold:hover::before,
	#e_bold.hover::before {
	    color: var(--icon-bg) !important;
	    background: var(--icon-bg-hover) !important;
	}
	
	/* endregion 加粗 */
	
	/* region 文字斜体 */
	
	#e_italic::before,
	#e_italic:hover::before,
	#e_italic.hover::before {
	    content: 'I' !important;
	    font-style: italic;
	}
	
	#e_italic::before {
	    color: var(--icon-bg-hover) !important;
	    background: var(--icon-bg) !important;
	}
	
	#e_italic:hover::before,
	#e_italic.hover::before {
	    color: var(--icon-bg) !important;
	    background: var(--icon-bg-hover) !important;
	}
	
	/* endregion 文字斜体 */
	
	/* region 下划线 */
	
	#e_underline::before,
	#e_underline:hover::before,
	#e_underline.hover::before {
	    content: '' !important;
	    -webkit-mask-image: url("data:image/png;base64,");
	    -webkit-mask-position: 2px 2px !important;
	}
	
	/* endregion 下划线 */
	
	/* region 字体颜色 */
	
	#e_forecolor::before,
	#e_forecolor:hover::before,
	#e_forecolor.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	    -webkit-mask-position: 3px 4px;
	}
	
	/* endregion 字体颜色 */
	
	/* region 字体背景 */
	#e_backcolor::before,
	#e_backcolor:hover::before,
	#e_backcolor.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	    -webkit-mask-position: 4px 3px;
	}
	
	/* endregion 字体背景 */
	
	/* region 添加链接 */
	#e_url::before,
	#e_url:hover::before,
	#e_url.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 添加链接 */
	
	/* region 移除链接 */
	#e_unlink::before,
	#e_unlink:hover::before,
	#e_unlink.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 移除链接 */
	
	/* region 表格 */
	#e_tbl::before,
	#e_tbl:hover::before,
	#e_tbl.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 表格 */
	
	/* region 清除文本格式 */
	#e_removeformat::before,
	#e_removeformat:hover::before,
	#e_removeformat.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 清除文本格式 */
	
	/* region 左浮动 */
	#e_floatleft::before,
	#e_floatleft:hover::before,
	#e_floatleft.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 左浮动 */
	
	/* region 右浮动 */
	#e_floatright::before,
	#e_floatright:hover::before,
	#e_floatright.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	    transform: rotate(180deg);
	}
	
	/* endregion 右浮动 */
	
	/* region 排列的列表 */
	#e_insertorderedlist::before,
	#e_insertorderedlist:hover::before,
	#e_insertorderedlist.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 排列的列表 */
	
	/* region 未排列的列表 */
	#e_insertunorderedlist::before,
	#e_insertunorderedlist:hover::before,
	#e_insertunorderedlist.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 未排列的列表 */
	
	/* region 自动排版 */
	#e_autotypeset::before,
	#e_autotypeset:hover::before,
	#e_autotypeset.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 自动排版 */
	
	/* region 居左 */
	#e_justifyleft::before,
	#e_justifyleft:hover::before,
	#e_justifyleft.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 居左 */
	
	/* region 居中 */
	#e_justifycenter::before,
	#e_justifycenter:hover::before,
	#e_justifycenter.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 居中 */
	
	/* region 居右 */
	#e_justifyright::before,
	#e_justifyright:hover::before,
	#e_justifyright.hover::before {
	    -webkit-mask-image: url("data:image/png;base64,");
	    transform: rotate(180deg);
	}
	
	/* endregion 居右 */
	
	/* region 添加引用文字 */
