/* i really want this to be global */
body {
    background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000; color:white!important;background-size:cover!important
}

.chat_resize_msg_users {
    background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000; color:white!important;background-size:cover!important
}
.chat_msg_sort {
    background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000; color:black!important;background-size:cover!important
}
.chat_resize_msg_users .chat_panel>div .msg_tmp {
    background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000; color:white!important;background-size:cover!important
}
.img_pres {
background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000; color:#fff!important;background-size:cover!important;
        border: 8px solid #0a0b0e73;
}
.chat_resize_msg_users .chat_panel {
    background:transparent;
}
.info_pl .info_pl_menu {
    background: transparent;
}
.info_pl .info_pl_menu div {
    color: #fff;
}
.div1, .div2, .div3, .div4, #bot_menu, #msg_menu {
    background: transparent;
    border: 1px solid transparent;
}
.menu {
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: stretch;
    box-shadow: 0px -3px 0px 0px transparent
}
.tbl2, .tbl3, .tbl4, .tbl6, .tbl7, .tbl10, .tbl12 { background: white;     border: 1px solid transparent;}
.chat_resize_msg_users .chat_msg .bg {
    opacity: 0;
}
.tbl2 td,.tbl3 td,.tbl4 td,.tbl6 td,.tbl7 td,.tbl10 td,.tbl12 td {
    background:url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg)fixed #000;
    padding: 1px 10px
}
.item_min_area>div:nth-child(3) {
    font-size: 0.9em;
    background: transparent;
    padding: 1px 5px;
    display: flex;
    justify-content: space-between
}
.item_min_area {
    border: 1px solid transparent;
    width: auto;
    height: auto;
    margin: 2px;
    padding: 0px;
    border-radius: 1px;
    position: relative;
    display: inline-block
}

.item_min_area [data-prop] {
    background: transparent;

    
    cursor: pointer;
    width: 71px;
    height: 90px;
    margin: -1px;
    align-items: center;
    display: flex;
    justify-content: center
}
.tbl1 {
    background: transparent;
    border: 1px solid transparent;
    border-radius: 1px;
    padding: 2px;
    border-spacing: 1px
}
#l_slots img,#r_slots img,.img_item {
    background: #ffffff6e;}
.si-mic:before {
    background: transparent;}
.item_min_area [data-prop] {
    background: transparent;
    border: 1px solid transparent;}
.nick_hp_cp, .nick_hp_cp_i {
    background: transparent;
    border: 1px solid transparent;}
.menu_shadow {
    box-shadow: 0px -3px 0px 0px transparent !important}
.tbl8 {
    background: transparent;
    border: 1px dotted white;}
.bg3,.l_select {
    background: transparent !important;
    color: white !important;
    text-shadow: none !important;
}
.img_cafe {
    background: transparent;}
.tbl8 {
    background: url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg);
    border: 1px dotted white;
}
.tbl9 td {
    background: url(http://sm.ign.com/ign_latam/screenshot/default/640956_y1zx.jpg);
    border: 1px dotted white;
}
