.highlighting {
  display: block;
  letter-spacing: .04em;
  margin-top: 5px;
}
.categoryList_DefaultContent_SubjectRead .highlighting:not([class="highlighting colornull"]) {
  margin-right: 17px;
  padding-left: 3px;
}

/* Striped background for highlighted items... */
.highlighting:not([class="highlighting colornull"]) {
  background-image: -webkit-repeating-linear-gradient(135deg, transparent, transparent 0px, rgba(245, 245, 220, .4) 6px, rgba(245, 245, 220, .2) 15px);
  background-image: repeating-linear-gradient(140deg, transparent, transparent 0px, rgba(245, 245, 220, .2) 6px, rgba(245, 245, 220, .1) 10px);
  border-left: 2px solid transparent;
  border-width: 2px;
  border-right-width: 0px;
  margin-left: -7px;
  position: relative;
}
/* ...AND 1px inner border on left side of highlighted items: */
.highlighting:not([class="highlighting colornull"]):before {
  border-top-width: 0px;
  border-bottom-width: 0px;
  border-left: 1px solid #FAFAFA;
  content: " ";
  position: absolute;
  top: -2px;
  right: -2px;
  left: -1px;
  bottom: -2px;
}

/* Correction for color pickers: 1px black border on mouse hover... */
.clearhighlighting:hover, .shading[labeltype="shade"]:hover, .highlighting[labeltype="highlight"]:hover {
  border: 1px solid black!important;
}
/* ...AND keep highlighted color pickers correctly positioned: */
.highlighting[labeltype="highlight"] {
  margin-top: 0px!important;
  margin-left: 0px!important;
}

/* List headers NOT in uppercase, but lowercase: */
div.containerHeader.sortable > h3 > div > sd-drop-down > div > div.text {
  text-transform: none!important;
}

/* Follow-up icon RED, instead of grey: */
.follow-up-icon {
  display: none;
  visibility: hidden;
}
.follow-up-icon:after {
  background-image: url("data:image/png;base64,")!important;
  background-repeat: no-repeat;
  background-size: 9px 9px!important;
  content: ' ';
  display: block;
  height: 9px;
  visibility: visible;
  width: 9px;
}

/* Today pop-up (Ctrl-Space) background colors: */
div[class="container placeholder"] {
  background-color: #ADD3E6!important;
}
div[class="container earlier av_today"], div[class="container earlier av_earlier"] {
  background-color: #E6F8FF!important;
}
div[class="container today av_today"], div[class="container today av_earlier"] {
  background-color: lightyellow!important;
}
