css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myphotodiary.com"),domain("bilddagboken.se") {

select {
-moz-appearance:none !important;
border: transparent !important;
}
#userStatus #iconBar {
width: 189px !important;
}
#iconBar #iconDiv {
width: 95px !important;
}
#iconBar a#newsLink,#news,#sfCalendarText,#sfCalendarSelectDiv, div#midAd, #rightAdContent, #adCalendar, #failedDiaryViews, #serverStatus,#adFrontpage,#adFrontpageIframe,#welcome,#latestLogins,#news,#holder_right,#dashGraph,#dayviewContentImage {
display:none !important;
} 
#hdTopLink, #uploadBar{
display:none !important;
}
a.button { 
background: transparent !important; 
color: black !important;
}
#globalMenu, #menuitems a { 
color: black ! important; 
}
.friendsDeniedHolder { 
display:none !important;
}
#search {
left:870px !important;
margin-left:5px !important;
top:51px !important;
width:200px !important;
}
.dashboardBox {
border-right:20px solid #16A8F4 !important
} 
.blueBox,.dashboardBox,#searchField, a.button span, .watcherInfo, #holder_right, #footer, .sandBox, table,td ,#dayviewContentImages.friendsDeniedHolder,#friendGroup0-1, .dashTopBox {
background:transparent !important;
}
.whiteHeader a {
color:black !important; 
font-size:10px !important 
}
.yellowBox {
background: transparent !important;
border: none !important
}
.whiteBold {
color:#666666 !important 
}
.friendGroup, .friendsHeader,.friendsHeader span,.leftUsername a, #userStatus a, #calendar h2,.header, .moderators   {
font-size:11px !important 
}
/*forumet och in/utloggning */

.forum_names_inner {
font-size:12px !important;
}
.forum_cathegories {
font-size:13px   !important;
}
.quote,.quote_outer,#loginbox,#loginExpander   {
background:transparent  !important;
border:none  !important;
}
#content_wide {
padding-left:7px !important 
}
img[src*="icon_news.gif"], img[src*="icon_comment.gif"],img[src*="icon_message.gif"],img[src*="icon_guestbook.gif"]{
display:none !important;
}
div#rightFocus{
visibility: hidden;
}
#top_home {
background-image: url("http://img693.imageshack.us/img693/6114/gohomed.png") !important;
background-position: center center !important;
background-repeat: no-repeat !important;
}
#top_home img {
opacity: 0 !important;
}
}
