css:
@-moz-document domain("dashboard.ambientweather.net") {
span,
.label,
.editable-title,
.uv-data,
.level {
	color: #eee !important;
}

.wind-widget span {
	color: #000 !Important;
}

.wind-widget .corner-wrap,
.corner-wrap .fdp-val {
	color: #eee !Important;
}

.device-device-realtime-dashboard {
	background-color: #333;
}

.component-dashboard-nav {
	background-color: #222;
}

.top {
	background-color: #555;
}

.data-table {
	background-color: #444;
}

.device-widget {
	background-color: #555;
}

.nav.active {
	background-color: #09a8e6 !important;
}

.group,
div.line {
	background-color: #555 !important;
}

.block .line {
	background-color: #fff !important;
}

.recharts-default-legend {
	background-color: #666;
}

.recharts-tooltip-item span {
	color: #000 !Important;
}

.page-container {
	background-color: #333;
}

div.controls.clearfix {
	background-color: #666;
}

rect {
	fill: #444;
}

header {
	background-color: #666;
}

tspan {
	fill: #eee;
}
}
