css:
@-moz-document url-prefix("http://confluence"), url-prefix("https://confluence") {
/* Left Menu simple Text*/
.acs-nav-item-label,
.tip,
.smalltext
{
    color: #58b !important;
}
.aui-page-panel-nav,
.aui-navgroup
{
    background-color: #333;
}
.space-information-container > div.name > a
{
    color: #58f;
}
/* plugin PageTree */
.plugin_pagetree_children_list > li > div.plugin_pagetree_children_content > span > a
{
    color: #58d !important;
}


/*Top Menu */
#space-menu-link,
#quick-create-page-button,
#create-page-button,
#user-menu-link,
#login-link
{
    color: #fff !important;
}
/* Login Page */
div.checkbox > label
{
    color: #58b !important;
}

/* Notifications */
.mw-notifications-list,
.mw-footer
{
    background-color: #222;
}
.aui-toolbar, /* Notification header */
.mw-drilldown-top,
.mw-drilldown-back
{
    background-color: #555 !important;
    color: #bbb !important;
}
.mw-notification-drilldown
{
    background-Color: #222 !important;
}
.mw-drilldown-excerpt
{
    background-color:#444 !important;
}
li.unread
{
    background-color: #247324 !important;
}


/* Page */
p
{
    color: #bbb;
}
#ellipsis > span,
#ellipsis > span > strong
{
    color: #666;
}
/* All space pages */
td.space-name > a
{
    color: #58b;
}
/* space config */
.menu-item > a
{
    color: #58b !important;
}
.greyboxfilled
{
    background-color: #111 !important;
}
/* Macro */
.macro-desc,
.macro-param-div > label
{
    color: #ccc !important;
}
#com-atlassian-confluence
{
    background-color: #222;
}
/* Calendar menu - edit */
ul.dialog-page-menu
{
    background-color: #444 !important;
}
li.selected > button
{
    background-color: #666 !important;
    color: #59d !important;
}
/* inbound comment create */
div.ic-create-comment-view > .ic-comment-container,
div.ic-create-comment-view > .ic-comment-container > .ic-sidebar-heading,
span.ic-nav-x-out-of-y,
a.ic-date-link > span,
div.ic-likes > button.ic-like-button,
ul.aui-list-truncate > li > button.ic-action-resolve,
div.ic-replies,
div.ic-actions > ul > li > button.ic-action-delete-reply,
div.ic-actions > ul > li > button.ic-action-edit-reply,
div.ic-actions > ul > li > button.ic-like-button
{
    color: #ddd;
    background-color: #666 !important;
}
/* inbound comment */
div.ic-display-comment-view > .ic-comment-container,
div.ic-display-comment-view > .ic-comment-container > .ic-sidebar-heading
{
    background-color: #666;
}
div.ic-reply-container
{
    background-color: #444;
}
/* resolved comment dialog */
section#ic-resolved-comment-dialog
{
    background-color: #666 !important;
}
/* Jira macro */
span.jira-issue,
div#inline-dialog-jira-metadata-dialog > .aui-inline-dialog-contents
{
    background-color: #444;
}
div#inline-dialog-jira-metadata-dialog > div.aui-inline-dialog-contents > div#jira-metadata-dialog.rendered-content > div.items-section > div.jira-metadata-section.ISSUES-section > div.section-label > span
{
        color: #ccc;
}


/* Help bubbles */
#cup-helptext, /* Warning */
.aui-message-hint,
#cup-helptext-content,
.aui-message-info,
.aui-message-info > p > strong
{
    border-color: red !important;
    color: red !important;
    background-color: #222;
}

.help-panel-header, /* help banner */
.confluence-information-macro /* info macro */
{
    background-color: #666;
}


/* Profile Settings */
.catContainer
{
    background-color: #222 !important;
}
.fieldLabel
{
    color: #ccc !important;
}


/* Page search */
.RecentContentItem_result-item-title__f9hg- /* Pagetitle */
{
    color: #58f;
}
.RecentContentItem_result-item-metadata__1a_7Y, /* Text of area of page */
.RecentSpaceItem_recent-space-title__qUP_R /* Text of areas of  (idk the english name) */
{
    color: #888 !important;
}
#search-panel-recent-page-link, /* more pages link */
.SearchContainer_more-options__6KHOW /* more option link */
{
    color: #d0003e !important;
}


/* Profile page */
#profileHeader
{
    background-color: #222322 !important;
}
.aui-nav > li > a /* Page navigation */
{
    color: #58b !important;
}
li.aui-nav-selected
{
    color: #5df !important;
}


/* Page edit*/
.status-indicator-message, /* Page edit status text */
.diff-html-added, .diff-html-removed, .diff-html-changed /* Pagehistorie Text */
{
    color: #888 !important;
}
.save-button-container
{
    color: #000 !important;
}
.page-metadata-modification-info /* Page created Text */
{
    color: #58b !important;
}
span.author > a,
span.editor > a,
a.last-modified
{
    color: #58f !important;
}


/* Page dropdown menu */
#action-menu
{
    /*filter: invert(100%);*/
    background-color: #111;
}
#action-menu > div > ul > li > a > span
{
    /*filter: invert(100%);*/
    color: #ccc !important;
}


/* Page restrict */
#update-page-restrictions-dialog
{
    background-color: #111;
}
/* dropdown menu */
#select2-drop-mask,
#select2-drop,
.select2-drop > ul,
.restrictions-dialog-option > span.title
{
    color: #fff;
    background-color: #111;
}
.aui-dialog2-content,
.page-restrictions-dialog-top
{
    color: #ccc;
    background-color: #333;
}
.page-restrictions-dialog-empty-placeholder >h6
{
    color: #fff;
}


/* Site info */
.basicPanelTitle
{
    background-color: #111;
}


/* Space-pages */
.space-view-controls > span /* selected nav */
{
    color: #58f;
    background-color: #111 !important;
}


/* Calendar */
.dialog-panel-body, /* Team-Calendar */
.dialog-button-panel, /* Team-Calendar */
span.today,
span.other-days
{
    color: #F2F2F2 !important;
    background-color: #333 !important;
}
.child-subcalendar
{
    color: #f2f2f2 !important;
}
.dialog-title, /* Team-Calendar */
td.fc-today
{
    background-color: #555 !important;
}


/* Comment */
.quick-comment-prompt
{
    /*filter: invert(100%);*/
    background-color: #111;
    color: #ccc;
}
.comment-content > p > span
{
    color: #ddd !important;
}
.comment-actions > ul > li,
.comment-actions > ul > li > a
{
    color: #58b !important;
}


/* Foother */
.like-summary, /* Like text */
.label-list /* Label text */
{
    color: #58b !important;
}






.aui-layout,
.aui-button {
    background-color: #000 !important
}




.aui-button,
.aui-icon-small {
    color: #f2f2f2 !important;
}


.wiki-content {
    color: #F2F2F2 !important;
    background-color: #222 !important;
}


.pagetitle,
.content-header,
.aui-sidebar-body,
.aui-sidebar-footer,
.nav-item-all-updates,
.aui-page-panel,
.acs-side-bar,
.space-tools-section,
.search-container-component,
.aui-navgroup-horizontal,
.aui-nav-item,
#full-height-container,
#main-header,
#footer,
.confluenceTh > p,
.confluenceTh > p > strong,
.confluenceTh > div > p,
.confluenceTh > div > p > strong,
.confluenceTd > p,
.confluenceTd > p > strong,
.confluenceTd > div > p,
.confluenceTd > div > p > strong,
.highlight-grey > p,
.highlight-grey > p > strong,
.highlight-grey > div > p > strong,
.highlight-grey > strong,
th, td {
    color: #F2F2F2 !important;
    background-color: #333 !important;
}
h1, h2, h3, h4,
#title-text > a,
.aui-nav-selected > a,
.tablesorter-header-inner,
.fc-day-number {
    color: #F2F2F2 !important;
}
#footer-logo {
    background-color: #000!important;
}
}
