css:
@-moz-document regexp("(https?://)?(www\.)google\..*") {
/* latin-ext */
 @font-face {
    font-family:'Libre Franklin';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Libre Franklin'), local('LibreFranklin-Regular'), url(https://fonts.gstatic.com/s/librefranklin/v3/jizDREVItHgc8qDIbSTKq4XkRiUR2zcLig.woff2) format('woff2');
    unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
 @font-face {
    font-family:'Libre Franklin';
    font-style: normal;
    font-weight: 400;
    font-display: swap;
    src: local('Libre Franklin'), local('LibreFranklin-Regular'), url(https://fonts.gstatic.com/s/librefranklin/v3/jizDREVItHgc8qDIbSTKq4XkRiUf2zc.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
#hdtb-msb > div:nth-child(1) {
    height: 40px;
}
#hdtb-msb-vis, g-header-menu, #hdtb-tls {
    position: relative;
    top: -27px;
}
#hdtb-msb > div:nth-child(2) {
    height: 20px;
}
/* main font */
 html, button, input, h1, h2, h3, h4, h5, h6, p, a, div, span {
    font-family:'Libre Franklin', Helvetica, Arial, sans-serif !important;
}
/* links over */
 a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
    color: #de5833
}
/* nav background color */
 #top_nav div, .sfbg {
    background:#f7f7f7 !important;
}
/* main nav border color */
 #hdtb {
    border-bottom: 1px solid rgba(0, 0, 0, 0.15);
}
/* duckduck logo */
 .logo img {
    display: none;
}
.logo a {
    background:url(https://duckduckgo.com/assets/logo_header.v108.svg) no-repeat;
    width: 36px !important;
    height: 36px !important;
}
.logo {
    left: -85px !important;
    padding-left: 13px !important;
    padding-right: 12px !important;
    padding: 4px 28px 0 30px !important;
    top: -1px !important;
}
.minidiv .logo {
    top: -5px !important;
}
/* change main nav colour */
 #hdtb-msb .hdtb-mitem.hdtb-msel {
    border-bottom: 3px solid #111 !important;
    color: #111 !important;
    font-weight: bold !important;
}
/* change main nav height */
 #hdtb-msb, #hdtbSum {
    height: 45px !important;
}
#hdtb-msb .hdtb-mitem.hdtb-msel {
    height: 12px !important;
    line-height: 12px !important;
    padding: 20px 15px 10px !important;
}
#hdtb-msb .hdtb-mitem.hdtb-imb {
    height: 12px !important;
    line-height: 12px !important;
    padding-top: 20px !important;
}
#hdtbMenus.hdtb-td-o {
    top: 46px;
}
/* dropdown style */
 .Mw2I7, .cF4V5c {
    border-radius: 3px !important;
}
.cF4V5c {
    top: 1109px !important;
}
/* change link colour */
 a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
    color: #00278e
}
#hdtb-msb .hdtb-mitem.hdtb-msel {
    color: #111;
}
/* cards radius */
 g-inner-card {
    border-radius: 3px !important;
}
/* maybe you meant... */
 .med p * {
    font-size: 0.9em;
}
.d2IKib {
    color: #de5833;
}
/* link url size */
 .iUh30 {
    font-size: 13px !important;
}
/* url color */
 .TbwUpd {
    color: #20692b;
    font-size: 10px
}
.r a.fl {
    font-size: 13px !important;
}
/* excerpt color */
 .s, .eJ7tvc {
    color: #494949
}
.kp-blk {
    border-radius: 3px !important;
}
/* sticky nav */
 .minidiv .RNNXgb {
    border-radius: 3px !important;
}
/* grey filter to topbar icons */
 .rINcab, .dRYYxd {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
/* fix search bar radius */
 .RNNXgb, .aajZCb {
    border-radius: 3px !important;
    border: 1px solid rgba(0, 0, 0, 0.15) !important;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.06) !important;
}
/* fixing resultstats align */
 #resultStats {
    margin-left: 2px;
}
/* search titles */
 #res h3 {
    letter-spacing: 0.02em !important;
}
/* hide footer */
 #fbar {
    display: none;
}
/* Google pagination */
 .csb, .ss {
    height: 30px;
}
#pnnext > span:nth-child(2)::after {
    content:" »"
}
#pnnext > span:nth-child(2) {
    margin-left: 5px !important;
}
#pnprev > span:nth-child(2)::before {
    content:"« "
}
#pnprev > span:nth-child(2) {
    margin-right: 43px !important;
}
#foot #navcnt .cur {
    color: #111;
    font-weight: bold;
}
#foot #navcnt a {
    color: #777;
}
#nav > tbody > tr > td {
    margin-left: -3px;
    display: inline-block;
}
/* D */
 .b.navend:nth-child(1) .csb:nth-child(1), #pnprev > span.csb.ch {
    background: url(https://svgshare.com/i/DHR.svg) no-repeat !important;
}
/* u */
 #nav > tbody > tr > td > a > span {
    width: 22px !important;
    margin-top: 6px;
    background: url(https://svgshare.com/i/DHS.svg) no-repeat !important;
}
/* u */
 #nav > tbody > tr > td.cur > span {
    width: 22px !important;
    margin-top: 6px;
    background: url(https://svgshare.com/i/DJF.svg) no-repeat !important;
}
/* ck */
 #pnnext > span.csb.ch {
    background: url(https://svgshare.com/i/DHm.svg) no-repeat !important;
    width: 32px !important;
    margin-left: 3px !important;
}
#pnnext > span:nth-child(2), #pnprev > span:nth-child(2) {
    background: none !important;
    margin-top: 0px;
}
/********************** MAIN GOOGLE *******/

/* duckduck logo */
 #lga {
    background:url(https://duckduckgo.com/assets/logo_homepage.normal.v108.svg) no-repeat;
    width: 205px;
    height: 200px;
    margin-top: 240px !important;
}
#hplogo {
    display:none;
}
/* background color */
 #viewport {
    background: #f7f7f7;
}
/* hide buttons you've not used in your hole life */
 .FPdoLc.VlcLAe, .VlcLAe {
    display: none;
}
/* reposition */
 #searchform.jhp #tsf {
    margin-top: 130px !important;
}
.A8SBwf, .RNNXgb {
    width: 620px !important;
}
/* search bar color */
 .gLFyf {
    color: #222;
}
#body > center > div:nth-child(2) {
    display: none;
    height: 0px;
}
/* Subheading format */
 #prm-pt {
    margin-top: 30px !important;
    color: #9a9a9a;
}
#SIvCob {
    font-size: 14px;
}
#footer, #fbarcnt {
    display: none;
}
/*dropdown tools */
 #hdtbMenus.hdtb-td-o {
    top: 46px;
}
/********************** images */
 #ab_ctls {
    padding-top: 20px !important;
}
.ab_dropdown {
    top: 25px;
}
.gko-sp-btn-i {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
#hdtbMenus.hdtb-td-o {
    top: 46px !important;
}
}
