css:
@-moz-document domain('nuxtjs.org') {
  #__nuxt, #__layout {
    width: 100vw;
    height: 100vh;
    position: fixed;
    top: 0;
    left: 0;
    background: white;
    mix-blend-mode: difference;
  }

  #__layout {
      overflow-y: scroll;
  }

  .nWelcome_Content_Title, .Content p, .Content code, .Content ol li, .Content ul li, .Content h1, .Content h2, .Content h3, .Content h4, .Content h5, .Content h6 {
      color: #000000;
  }
}
