css:
@-moz-document domain("photopea.com") {
html {
  background: hsl(0, 0%, 15%)!important;
}

:focus {
  outline-color: hsl(0, 0%, 70%)!important;
}

select {
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAABGdBTUEAALGPC/xhBQAAAAFzUkdCAK7OHOkAAAAVUExURQAAAP///////////////////////0jPbQQAAAAHdFJOUwC/miZgPyH8d3XpAAAAKElEQVQI13XGQRIAMAQAsS3l/082Sm/kFBjZbYbKeUTp5+q1/B+4swoa2wBrBuoieAAAAABJRU5ErkJggg==)!important;
  background-size: 10px 10px!important;
  background-position: center right 4px!important;
  background-repeat: no-repeat!important;
  transition: color .2s, background-color .2s, border-color .2s, background-image .2s!important;
}

select:hover,
select:focus {
  color: hsl(0, 0%, 100%)!important;
  background-color: hsl(0, 0%, 42%)!important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAMAAAC67D+PAAAABGdBTUEAALGPC/xhBQAAAAFzUkdCAK7OHOkAAAAbUExURQAAAP///////////////////////////////+tNPsIAAAAIdFJOUwDNgDMyVCx/KgDLIQAAAC9JREFUCNd1ykEOACAMAkGwtPX/LzbUq86JbACestfoRHAPBqDZlC/ubibeZlX4Oi0IALXycucjAAAAAElFTkSuQmCC)!important;
}

.bbtn:hover,
.bbtn:focus {
  outline: none!important;
  border-top-color: hsla(0, 0%, 100%, .4)!important;
  border-bottom-color: hsla(0, 0%, 0%, .85)!important;
}

option {
  color: hsl(0, 0%, 86%)!important;
  background: hsl(0, 0%, 30%)!important;
}

input {
  color: hsl(0, 0%, 86%)!important;
  background-color: hsl(0, 0%, 22%)!important;
  box-shadow: 0 0 0 1px hsl(0, 0%, 40%)!important;
  transition: background-color .2s, box-shadow .2s!important;
}

input:focus {
  background-color: hsl(0, 0%, 16%)!important;
  box-shadow: 0 0 0 1px hsl(0, 0%, 54%)!important;
  outline: none!important;
}

input[type="color"] {
  padding: 0!important;
}

textarea {
  background-color: hsl(0, 0%, 19%)!important;
  border: 1px solid hsl(0, 0%, 36%)!important;
  transition: background-color .2s, border-color .2s!important;
}

textarea:focus {
  background-color: hsl(0, 0%, 17%)!important;
  border-color: hsl(0, 0%, 42%)!important;
  outline: none!important;
}

input[type="checkbox"] {
  -moz-appearance: none!important;
  -webkit-appearance: none!important;
  position: relative!important;
  opacity: 1!important;
  margin-right: 0!important;
  margin-top: 0!important;
  margin-bottom: 0!important;
  box-shadow: none!important;
  border: 1px solid hsl(0, 0%, 50%)!important;
  height: 12px!important;
  width: 12px!important;
  border-radius: 1px!important;
  background-color: hsl(0, 0%, 20%)!important;
  transition: background-color .2s, border-color .2s!important;
}

input[type="checkbox"]:hover {
  background-color: hsl(0, 0%, 26%)!important;
  border-color: hsl(0, 0%, 70%)!important;
}

input[type="checkbox"]:checked:before {
  content: "";
  background: url(data:image/png;base64,)no-repeat;
  position: absolute;
  height: 10px;
  width: 10px;
  top: 0;
  left: 0;
  transition: background .2s;
}

input[type="checkbox"]:checked:hover:before {
  content: "";
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgTFiUR+fj+4AAAAFNJREFUGNNjYKAG+P///4T///8HEFIU8B8CNuBTJPD////3//////D//38BdEkFJPYGqGkB2Nzy/v///wZ4rfz//38CVPI9TiuxKP5PrE8nUBSmALjXbxrC56mQAAAAAElFTkSuQmCC)no-repeat;
}

input[type="checkbox"] + label {
  padding-left: .3em!important;
  transition: color .2s!important;
  font-size: 13px!important;
  height: 12px!important;
  line-height: 12px!important;
  display: inline-block!important;
  vertical-align: middle!important;
}

input[type="checkbox"]:hover + label,
input[type="checkbox"] + label:hover {
  color: hsl(0, 0%, 100%)!important;
}

.toolconf .cbox > input[type="checkbox"],
.toolconf .cbox > label {
  position: relative!important;
  margin-top: -3px!important;
}

.alertcont > div {
  background: hsl(0, 0%, 15%)!important;
  color: hsl(0, 0%, 86%)!important;
}

.contextpanel {
  background-color: hsl(0, 0%, 18%)!important;
}

.contextpanel .enab {
  transition: background-color .2s!important;
}

.contextpanel .enab:hover {
  background-color: hsl(0, 0%, 22%)!important;
}

.contextpanel .enab span {
  color: hsl(0, 0%, 75%)!important;
  transition: color .2s!important;
}

.contextpanel .enab:hover span {
  color: hsl(0, 0%, 100%)!important;
}

.contextpanel .enab img,
.gsicon,
.block .panelhead div .cross,
.window .head .cross {
  -webkit-filter: invert(1)!important;
  filter: invert(1)!important;
  opacity: .75!important;
  transition: opacity .2s!important;
}

.contextpanel .enab:hover img,
button:hover .gsicon,
button:focus .gsicon,
button:active .gsicon,
.active:not(.fitem) .gsicon,
.block .panelhead div .cross:hover,
.window .head .cross:hover {
  opacity: 1!important;
}

.contextpanel hr {
  background: hsl(0, 0%, 27%)!important;
}

button {
  transition: color .2s, background-color .2s, border-color .2s!important;
}

button:hover,
button:focus,
button:active {
  color: hsl(0, 0%, 100%)!important;
}

.panelblock button,
.toolbtn {
  outline: none!important;
}

.panelblock button:hover,
.panelblock button:focus,
.panelblock button.active {
  color: hsl(0, 0%, 100%)!important;
  background-color: hsla(0, 0%, 0%, .25)!important;
}

.panelblock button:not(.active):not(:hover):focus {
  color: hsl(0, 0%, 84%)!important;
  background-color: transparent!important;
}

.topfloat > a,
.topbar > button {
  background-color: hsl(0, 0%, 38%)!important;
  color: hsl(0, 0%, 84%)!important;
  transition: color .2s, background-color .2s, border-color .2s!important;
}

.topfloat > a:hover,
.topbar > button:hover {
  background-color: hsl(0, 0%, 42%)!important;
  color: hsl(0, 0%, 100%)!important;
  border-top-color: hsla(0, 0%, 100%, .4)!important;
  border-bottom-color: hsla(0, 0%, 0%, .85)!important;
}

.topfloat {
  right: 4px!important;
}

.block .panelhead div {
  color: hsl(0, 0%, 66%)!important;
  transition: color .2s, background-color .2s, border-color .2s!important;
}

.block .panelhead div.active,
.block .panelhead div:hover {
  color: hsl(0, 0%, 90%)!important;
  background-color: hsl(0, 0%, 26%)!important;
  border-top-color: hsla(0, 0%, 100%, .3)!important;
}

.rangecont {
  background-color: hsl(0, 0%, 40%)!important;
}

#cap,
body > .app > div[style*="z-index: 1"] {
  display: none!important;
}

.mainblock > .block > div,
body > .app > div > div[style*="width"] {
  width: unset!important;
  max-width: unset!important;
}

body > .app > div > div[style*="width"] > div[style*="left"] {
  left: 0!important;
  right: 0!important;
}

body > .app > div {
  flex-basis: 100%!important
}

body > .app > div > .cmanager .contextpanel[style*="top: 163"] {
  left: unset!important;
  right: 32px!important;
}

.mainblock {
  flex-grow: 1!important;
}

.mainblock > .block > div > div.pbody {
  width: max-content;
  margin: auto!important;
}

.intro {
  position: absolute!important;
  top: 0!important;
  bottom: 0!important;
  left: 0!important;
  right: 0!important;
  margin: auto!important;
}
}
