css:
@-moz-document url-prefix("https://rbxcity.com/") {
/*Main*/

html, body { /*Stylize main HTML elements*/ 
    background-color:#212527 !important;
    font-family: "Arial" !important;
}

/*Navigational Bar*/

.jumbotron, .navbar, .footer, footer, .is-light, .navbar-link, a.navbar-item { /*Jumbotron and top bar*/
    background: #282D2F !important;
    color: white !important;
}

/*Cards*/

.card, select, textarea, .grey, .box, .is-dark, .button, pre { /*Modify all card elements and buttons*/
    background-color: #282D2F !important;
    color: white !important;
    border: solid 0px #282D2F !important;
}

.jumbotron-fluid, .grey-black-grad { /*Modify all card footer styling*/
    background: #23d160;
}

/*Buttons*/

.button, .btn-success {
    background-color: #23d160 !important;
    border-color: #dbdbdb;
    border-width: 0px;
    color: #363636;
    cursor: pointer;
    justify-content: center;
    padding-bottom: calc(.375em - 1px);
    padding-left: .75em;
    padding-right: .75em;
    padding-top: calc(.375em - 1px);
    text-align: center;
    white-space: nowrap;
}

/*Dropdown Menu*/

.dropdown-menu { 
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 10rem;
    padding: .5rem 0;
    margin: .125rem 0 0;
    font-size: 1rem;
    color: white;
    text-align: left;
    list-style: none;
    background-color: #23d160;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: .25rem;
}

a.dropdown-item, button.dropdown-item { /*Fix dropdown background*/
    background-color: #23d160 !important;
}

/*Site-Wide Text*/

.content h1, .content h2, .content h3, .content h4, .content h5, .content h6, p, .has-text-white, .has-text-dark, .has-text-grey, .has-text-black, .has-text-danger, .text-dark, .text-primary, .content table thead td, .content table thead th, td  { 
    color: white !important;
    font-weight: 600;
    line-height: 1.125;
}

/*Announcement Banners*/

.message { /*Hide announcement ribbon background border*/
    background-color: #282D2F;
    border-radius: 4px;
    font-size: 1rem;
}

.message-body { /*Announcement ribbon inner element styling*/
    color: white;
    background-color: #282D2F;
    border-color: #23d160;
}

.list-group-item { /*Styling for search feature rows*/
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #282D2F;
    border: 1px solid rgba(0,0,0,.125);
}

/*Forums*/

.tabs li.is-active a { /*Styling for active forum tabs*/
    border-bottom-color: #23d160;
    color: #23d160;
}

.tabs ul { /*Remove border on forum tab list*/
    align-items: center;
    border-bottom-color: #282D2F;
    border-bottom-style: solid;
    border-bottom-width: 1px;
    display: flex;
    flex-grow: 1;
    flex-shrink: 0;
    justify-content: flex-start;
}

.tag:not(body) { /*Style tags*/
    align-items: center;
    background-color: #23d160;
    border-radius: 4px;
    color: white;
    display: inline-flex;
    font-size: .75rem;
    height: 2em;
    justify-content: center;
    line-height: 1.5;
    padding-left: .75em;
    padding-right: .75em;
    white-space: nowrap;
}

.tag:not(body).is-success {
    background-color: #23d160 !important;
}

/*Modals*/

.swal2-popup { /*Styling for new thread popup*/
    display: none;
    position: relative;
    flex-direction: column;
    justify-content: center;
    width: 32em;
    max-width: 100%;
    padding: 1.25em;
    border-radius: .3125em;
    background: #212527;
    font-family: inherit;
    font-size: 1rem;
    box-sizing: border-box;
}

.swal2-title { /*Styling for popup title*/
    display: block;
    position: relative;
    max-width: 100%;
    margin: 0 0 .4em;
    padding: 0;
    color: white !important;
    font-size: 1.875em;
    font-weight: 600;
    text-align: center;
    text-transform: none;
    word-wrap: break-word;
}

.swal2-popup .swal2-styled.swal2-confirm { /*Styling for popup confirm / OK button*/
    border: 0;
    border-radius: .25em;
    background: initial;
    background-color: #23d160;
    color: white;
    font-size: 1.0625em;
}

.swal2-icon.swal2-info { /*Modal icon*/
    border-color: #23d160;
    color: #23d160;
}

.table td, .table {
    background-color: #282D2F
}

/*Misc.*/

.regular-border { /*Hide regular-border element on sort buttons*/
    border: solid 0px #972929 !important;
}

.message.is-danger .message-body { /*Modify scammer database's view scammers page*/
    border-color: #23d160;
    color: white;
}

.toastify { /*Server Connection Status*/
    padding: 12px 20px;
    color: #ffffff;
    display: inline-block;
    box-shadow: 0 0px 0px 0px rgba(0, 0, 0, 0.12), 0 0px 0px 0px rgba(46, 47, 52, 0.3);
    background: -webkit-linear-gradient(315deg, #23d160, #23d160) !important;
    background: linear-gradient(135deg, #23d160, #23d160) !important;
    position: fixed;
    opacity: 0;
    transition: all 0.4s cubic-bezier(0.215, 0.61, 0.355, 1);
    border-radius: 4px;
    cursor: pointer;
    text-decoration: none;
    max-width: calc(50% - 20px);
    z-index: 2147483647;
}
}

@-moz-document url-prefix("https://rbxcity.com/user/") {
/*User Page Statistics Jumbotron*/

.jumbotron-fluid, .grey-black-grad {
    background: #282D2F !important;
}
}
