css:
@-moz-document domain("cengage.com") {
/*
TODO Chapter contents pages still have white backgrouds but often have bright images as well so is there a point?
*/

/* Book iframe */
.ebook_document,
.ebook_document .container {
    background-color: #948a8a;
}

/* Book page container */
.ebook_document > #ebook_document > .container, 
#ebook_container > #ebook_document > .container {
    max-width: 90%;
    width: 90%;
}

/* Book page text */
.ebook_document p,
.ebook_document li,
.ebook_document tr,
.ebook_document em {
    color: black;
}

/* Book page header text */
.ebook_document .page h1,
.ebook_document .page h2,
.ebook_document .page h3,
.ebook_document .headingText {
	color: #2929a3;
}

/* Book page url text */
.ebook_document .external,
.ebook_document h2 .sectionLabel,
.ebook_document p > a {
	color: #00609A;
    font-weight: bold;
}

/* In book page notes container */


.ebook_document .procedure,
.ebook_document .excerpt,
.ebook_document .learningObjectives,
.ebook_document .narrative,
.ebook_document .mtexample,
.ebook_document .blockquote,

.ebook_document .independent, 
.ebook_document .dependent, 
.ebook_document .figure, 
.ebook_document .nb_media, 
.ebook_document .quiz, 
.ebook_document .exercise, 
.ebook_document .content > .table, 
.ebook_document .pageSection > .table, 
.ebook_document .discussionList, 
.ebook_document .activities, 
.ebook_document .screenplay {
    background-color: #9d9595;
}

.ebook_document .independent, 
.ebook_document .dependent, 
.ebook_document .figure, 
.ebook_document .nb_media, 
.ebook_document .quiz, 
.ebook_document .exercise, 
.ebook_document .content > .table, 
.ebook_document .pageSection > .table, 
.ebook_document .discussionList, 
.ebook_document .activities, 
.ebook_document .screenplay {
    border: 6px solid #776e6e;
    border-top-color: #776e6e;
    border-right-color: #776e6e;
    border-bottom-color: #776e6e;
    border-left-color: #776e6e;
}

.ebook_document .procedure {
    border-left: none;
}

/* ------------ Start of top toolbar ------------ */

/* toolbar text */
.ToolbarPanel__search-btn__text,
.UserDropdown__button,
.UserDropdown__button.is-active,
.ActivityFrame__title,
.Dropdown__menu--pullRight .Dropdown__option {
    color: black;
}

/* toolbar background */
.ToolbarPanel,
.ActivityFrame__header,
.Dropdown__menu--pullRight {
    background-color: #575757;
}

/* next and back overlay images */
.nextButton, .backButton {
    -webkit-filter: invert(.65);
    filter: invert(.65);
}

/* ------------ Book Index ------------ 
TODO could do more with this


.clCoursePane {
    background-color: #575757;
}

.clCoursePane .activity {
    background-color: white;
}
*/
/*
.launchable.title {
    color: #00609A;
    font-weight: bold;
}*/
}
