css:
@-moz-document regexp("(https|http?):\\/\\/(.)*waterfall\\.social(.)*") {
/*fixed navbar*/
.navbar { position: fixed !important;top:0px;left:0px;width:100%;z-index:2;}

/*adjusts content padding to account for fixed header*/
body > .container-fluid {margin-top:4em;}
body > .container { margin-top: 4.5em}

/* condensed, wider blog dropdown so things don't get stuck off screen.
- list of blogs to switch to will scroll when you have 9 blogs or more to choose from.
- hides 'stats for [url]' label
*/
div.dropdown-menu.show[aria-labelledby="blogsDropdown"] { width: 25rem !important; padding: 0.5rem; }
div.dropdown-menu.show[aria-labelledby="blogsDropdown"] > .dropdown-item { width: calc(50% - 0.2rem);float:left;margin: 0 0.2rem 0 0; padding: 0.25rem 1rem; } 
div.dropdown-menu.show[aria-labelledby="blogsDropdown"] .dropdown-item:first-child h5.text-center {display: none;}
div.dropdown-menu.w-auto.show > .dropdown-item > .img-fluid.avatar {display:block;margin-right:auto;margin-left:auto;}
div.dropdown-menu.w-auto.show .dropdown-item h5 {overflow:hidden;width:100%;white-space:normal !important;word-wrap:break-word !important;}
/*hides divider line*/
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .dropdown-divider {display:none;}
/*fixes height of 'switch blog to...' label */
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .dropdown-header { height: 2rem;}
/* styling for blog list */
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .list-group.bloglist {list-style: none;width:50%;max-height: 18rem;overflow: auto;}
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .list-group.bloglist li a {width:100%;overflow:hidden;height: 2.5rem;padding: 0.25rem 0.5rem;}
/*blogs dropdown scrollbar settings*/
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .list-group {scrollbar-width: thin;}
div.dropdown-menu.w-auto.show[aria-labelledby="blogsDropdown"] .list-group::-webkit-scrollbar { width: 10px; }


/*sticky sidebar for blogs */
.container-fluid > .row > .d-none.d-lg-block[style="width:300px;"] {
    position:sticky;top:4.2rem;
    max-height: calc(100vh - 5.2rem);
    overflow-y: auto;
    padding-right: 0.5rem;
    width: calc(300px + 0.5rem) !important;
}
.container-fluid > .row > .d-none.d-lg-block[style="width:300px;"] {scrollbar-width: thin;}
.container-fluid > .row > .d-none.d-lg-block[style="width:300px;"]::-webkit-scrollbar { width: 10px; }

/*displays blog info instead of hiding when the window size gets too small*/
@media (max-width: 995px) {
    .container-fluid > .row > .d-none.d-lg-block[style="width:300px;"] {
        display: block !important;
        flex: 0 0 100%;
        max-width: 100%;
        padding: 0 15px 3rem;
        position: static;
        max-height: none;
    }
}
@media (max-width: 768px) {
    a#blogsDropdown, div.dropdown-menu.show[aria-labelledby="blogsDropdown"] {
        flex: 0 0 100%;
    }
    #WFNav .nav-item:last-child {
        flex-wrap: wrap;
    }
    body > nav.navbar {
        max-height: 100%;
        overflow: auto;
    }
    body > nav.navbar {scrollbar-width: thin;}
    body > nav.navbar::-webkit-scrollbar { width: 10px; }
}
}
