css:
@-moz-document url-prefix("https://www.deezer.com/") {
/*********************/
/* Groovzer v0.9
/* Last update: 01.08.2019
/* A mostly faithful recreation of the late
/* Grooveshark that was closed on April 30, 2015
/* - skixO
/*********************/
/* Subscribe banner */
.sidebar-header .conversion-banner { display: none; }
body {
    background: #f87106;
}
.page-topbar {
    background: #333333;
    background: -moz-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: -webkit-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: linear-gradient(to bottom, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    border-bottom: 1px solid rgba(0, 0, 0, .4)
}
.page-sidebar .sidebar-header-logo {
    margin: 0;
    padding: 0 15px;
    background: #333333;
    background: -moz-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: -webkit-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: linear-gradient(to bottom, #333333 0%, #2a2a2a 60%, #1e2326 100%);
}
.page-player .player-bottom {
    background: #333333;
    background: -moz-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: -webkit-linear-gradient(top, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    background: linear-gradient(to bottom, #333333 0%, #2a2a2a 60%, #1e2326 100%);
    color: #fff;
    border-top: 1px solid #555;
}
/* Sidebar */
.page-sidebar {
    background: #222;
    border-right: 1px solid #111;
}
.page-sidebar .sidebar-nav-link {
	border-radius: 0;
    color: #eee;
}
.page-sidebar .sidebar-nav-link.is-active {
    background: #333;
    color: #fff;
    text-shadow: 0 1px 0 #000;
    font-weight: normal;
}
.page-sidebar .sidebar-nav-link:hover {
    background: #333;
    color: #fff;
}
.page-sidebar .sidebar-nav-link.is-main {
    background: #404040;
    border-radius: 0;
    margin-bottom: 0;
    padding: 10px 15px;
    height: auto;
    color: #fff;
    border-top: 1px solid #666;
    border-bottom: 1px solid #222;
    font-size: .8rem;
    text-transform: uppercase;
    font-weight: normal;
}
.page-sidebar .sidebar-nav-list {
    margin: 0 0 12px;
    padding: 0;
}
a.sidebar-nav-link.is-main.is-active {
    background: #666;
}
/* Controls */
.slider-autohide .slider-track {
    height: 8px;
    border: 1px solid rgba(0,0,0,0.6);
}
.slide.slide-cut .slide-background, .slide.slide-cut .slide-covers:after {
	background: #f8f8f8;
    background: -moz-linear-gradient(top,  #f8f8f8 0%, #e7e7e7 40%, #dcdcdc 100%);
    background: -webkit-linear-gradient(top,  #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    background: linear-gradient(to bottom,  #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
}
.slide.slide-cut {
    border-color: rgba(0,0,0,0.4); 
}
.svg-icon-group-btn {
    color: #fff;
    text-shadow: 1px 1px 2px #000;
}
.page-player .player-track {
    background: rgba(0,0,0,0.4);
    margin: 0 72px;
    padding: 10px 0;
    box-shadow: inset 0 0 3px 1px #000;
    border-radius: 3px;
}
.page-player .player-track .track-heading {
    color: #f7f7f7;
    text-shadow: 0 1px 0 #000;
}
.page-player .player-options .queuelist-label { 
    color: #f7f7f7;
}
.thumbnail .picture {
    background-color: #333;
}
.page-player .player-options .option-item+.option-item:before {
    background: #111;
}
.slider-track-default {
    background-color: #393939;
}
.slider-track .gradient-default {
    background: #f87106;
}
.svg-icon.active, .svg-icon.is-active {
    color: #f87106;
}
.page-player .player-options .queuelist {
	background: #494949;
    background: -moz-linear-gradient(top, #494949 0%, #424242 53%, #3e3e3e 100%);
    background: -webkit-linear-gradient(top, #494949 0%,#424242 53%,#3e3e3e 100%);
    background: linear-gradient(to bottom, #494949 0%,#424242 53%,#3e3e3e 100%);
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.8), inset 0px 1px 0 0 rgba(255,255,255,.3);
    border: 1px solid rgba(0,0,0,0.4);
    transition: all .3s ease-in-out;
}
.page-player .player-options .queuelist:hover {
    border: 1px solid rgba(0,0,0,1);
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,1), inset 0px 1px 0 0 rgba(255,255,255,.6);
}
.page-player .player-options .queuelist.is-active {
	 border: 1px solid #f87106;
}
/* Grooveshark orange background */
.svg-icon-group-btn:hover, .svg-icon-group-btn.is-active {
    background: transparent;
    color: #f87106;
}
.page-player .player-options .queuelist.is-available:hover, .page-player .player-track .track-label {
    background-color: #f87106;
    color: #fff;
    text-shadow: 0 1px 0 rgba(0,0,0,0.4);
}
/* Orange Headings and Links */
.heading-1, .heading-2, .heading-3, .heading-4, .heading-5, .heading-1 a, .heading-1 a:hover, .heading-2 a, .heading-2 a:hover, .heading-3 a, .heading-3 a:hover, .heading-4 a, .heading-4 a:hover, .heading-5 a, .heading-5 a:hover {
	color: #222;
    text-shadow: 0 1px 0 rgba(255,255,255,0.3);
    font-weight: bold;
}
.heading-1-sub, .heading-2-sub, .heading-3-sub, .heading-4-sub, .heading-5-sub {
    color: #111;
}
.heading-1-sub a, .heading-1-sub a:hover, .heading-2-sub a, .heading-2-sub a:hover, .heading-3-sub a, .heading-3-sub a:hover, .heading-4-sub a, .heading-4-sub a:hover, .heading-5-sub a, .heading-5-sub a:hover {
    color: #333;
}
.thumbnail-caption .caption-released {
	color: #222;
}
/* Logo Replace */
.logo-deezer-black {
    background: transparent;
}
.logo-deezer-black:before {
    content: 'Groovzer';
    font-size: 1.3rem;
    color: #fff;
    text-shadow: 0 1px 2px rgba(0,0,0,0.4);
    font-weight: bold;
    font-style: italic;
}
/* My Library */
#page_profile .profile-header-container {
	background: #f8f8f8;
    background: -moz-linear-gradient(top,  #f8f8f8 0%, #e7e7e7 40%, #dcdcdc 100%);
    background: -webkit-linear-gradient(top,  #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    background: linear-gradient(to bottom,  #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
}
.navbar {
    background: transparent;
}
.navbar-link {
    color: #111;
    font-weight: bold;
    text-shadow: 0px 1px 0px rgba(255,255,255,.4);
}
.navbar-item+.navbar-item {
    padding: 1rem 1.5rem 0;
    border-left: 1px solid #ccc;
    border-right: 1px solid #eee;
}
.navbar-item {
    padding: 1rem 1.5rem 0;
    margin: 0; 
}
.navbar-item:first-child {
	border-right: 1px solid #eee;
}
.navbar-item:last-child {
    border-right: 0;
}
.navbar-item.active {
    background: rgba(0,0,0,.3);
    box-shadow: inset 0 0 4px 1px rgba(0,0,0,0.6);
    border: 0;
}
.navbar-item.active .navbar-link {
    border-bottom: 5px solid #f87106;
    color: #fff;
    text-shadow: 1px 1px 0 rgba(0,0,0,0.6);
}
#page_profile .profile-header-container.not-extended .container {
    padding-top: 0;
}
/* Top Loading Bar */
.page-loader { background-color: #111; }
.page-loader-bar {
    background-color: #f87106;
}
/* Lets get orange */
#page_profile {
    background: #f87106;
}
.livestream .label-onair, .catalog-header .header-info .heading-link:hover {
    color: yellow;
	text-shadow: 0 1px 0 rgba(0,0,0,0.4)
}
.create-assistant-container {
    background-color: rgba(0,0,0,0.1);
    border: 1px dashed #111;
}
#page_profile .user-section .user-section-suggestion, .naboo-catalog .catalog-tab-box {
	background: rgba(255,255,255,0.4);
}
.thumbnail-viewmore {
    background-color: rgba(255,255,255,.1);
}
.catalog-tab-comment .comment-submit-chars, .naboo-catalog .catalog-legal-notice {
    color: #111;
}
.catalog-header .header-info-list {
    color: #fff;
}
#page_naboo_artist > div.catalog-header {
    background: #f4f4f4;
    margin-bottom: 0px;
}
#page_naboo_artist > nav {
	background: #f4f4f4;
    background: -moz-linear-gradient(top,  #f4f4f4 0%, #e9e9e9 53%, #dedede 100%);
    background: -webkit-linear-gradient(top,  #f4f4f4 0%,#e9e9e9 53%,#dedede 100%);
    background: linear-gradient(to bottom,  #f4f4f4 0%,#e9e9e9 53%,#dedede 100%);
    border-top: 2px solid #f7f7f7;
}
.datagrid-toolbar .heading-1 {
    display: block;
    font-size: 1rem;
    padding: 10px;
}
/* Grooveshark toolbar */
.datagrid-toolbar {
    margin: 0 0 1px 0;
    border-radius: 3px 3px 0 0;
    padding: 0;
    border-bottom: 0px solid #ccc;
    background: #f8f8f8;
    background: -moz-linear-gradient(top, #f8f8f8 0%, #e7e7e7 40%, #dcdcdc 100%);
    background: -webkit-linear-gradient(top, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    background: linear-gradient(to bottom, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    width: 100%;
    height: 50px;
}
#page_profile .user-section .heading-1 {
    float: none;
    line-height: inherit;
    margin: 0 0 10px;
}
.datagrid-toolbar .c011 {
    background: transparent;
    color: #111;
    font-size: 1rem;
    text-shadow: 0 1px 0 rgba(255,255,255,0.4);
    border-right: 1px solid #ccc;
    margin: 0;
    height: 50px;
    padding: 0 10px 0 0;
    border: 0;
    border-radius: 0;
    border-right: 1px solid #ccc;
}
.c019:not(.c0124):hover {
    background-color: rgba(255,255,255,.8);
    border-radius: 3px 0 0 0;
}
.datagrid-toolbar .toolbar-item+.toolbar-item {
    margin-left: 0;
    border-left: 1px solid #fff;
}
.c015 {
    margin-right: 8px;
    padding-left: 15px;
}
.datagrid-toolbar > div.toolbar-wrapper.toolbar-wrapper-full > div:nth-child(1) > button > span > div.states-button-action.is-active {
    padding: 0 10px 0 15px;
}
.datagrid-toolbar button.c011.dropdown-toggle {
	padding: 0 15px;
}
.datagrid-row {
    border-radius: 0;
}
.datagrid-toolbar .toolbar-search {
    margin-left: 10px;
}
#page_naboo_artist .content-left {
	background: #fff;
}
.placeholders-title {
    color: #fff;
}
/* Search */
.search-header {
    background: #f8f8f8;
}
.page-naboo-search nav.navbar {
    background: #f8f8f8;
    background: -moz-linear-gradient(top, #f8f8f8 0%, #e7e7e7 40%, #dcdcdc 100%);
    background: -webkit-linear-gradient(top, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    background: linear-gradient(to bottom, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
}
.nano-card-list .nano-card-item {
    background: #f8f8f8;
    background: -moz-linear-gradient(top, #f8f8f8 0%, #e7e7e7 40%, #dcdcdc 100%);
    background: -webkit-linear-gradient(top, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
    background: linear-gradient(to bottom, #f8f8f8 0%,#e7e7e7 40%,#dcdcdc 100%);
}
.thumbnail {
	border-radius: 1px;
}
.datagrid {
    background: #fff;
}
.navbar .container {
    margin-bottom: -1px;
}
}
