css:
@-moz-document domain("forum.xda-developers.com")
{
* { border: none !important }

#sidepanel, #thread-header-social, #showthreadRankedThreads, #prev-next, #footer, #footer-nav, #thread-header-avatar, #thread-header-thanks, .tagbar, .post_thanks_box_wrapper, .postbit-social, .postbit-userinfo-cell, .xda-box, .postbit-signature, .tborder { display: none }

#thread-header-bloglike { background-color: transparent}

#thread-header-bloglike h1, #thread-header-meta {
    margin: 0;
    padding: 0 0 5px 0
}

.post-head-left { padding-left: 15px !important }
.navbits { top: 60px; background: #ebebeb }
.topbar { box-shadow: 0 0 5px 2px rgba(0,0,0,.3) }
.post-text { padding: 15px }
.postbit-posttitle { margin: 10px 15px 15px }
.page { margin: 0 15% 18px 15% }
.bbcode-quote { margin: 0 0 20px 0 }
.fieldset { margin: 0 }
.new-button, .search-input { border-radius: 3px }
.new-button:hover { background: #fff }

.post-head, .post-head-container {
    background-color: #fff;
    border-radius: 5px 5px 0 0
}

.postbutton-row {
    background: #fbfbfb !important;
    border-radius: 0 0 5px 5px; }

.bbcode-quote-text { 
    border-radius: 5px;
    padding: 15px;
    background-color: #f9f9f9
}

.post-head-right {
    float: right;
    margin-right: 10px;
}

.postbit-attachment {
    padding: 10px;
    background: #f7f7f7;
}

.postbit-content {
    background: #fff;
    padding: 0;
}

.postbit-edited {
    float: right;
    padding: 15px;
    margin: 0;
}
}
