css:
@-moz-document domain(throwawaymail.com) {
    html {
    position: relative;
    min-height: 100%;
    background-color: #121212;
}
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #e2e2e2;
    background-color: #121212;
}
.footer {
    position: absolute;
    bottom: 0;
    width: 100%;
    border-top: 2px solid #000;
    padding: 10px;
    text-align: center;
    background-color: #0c0c0c;
}
.table-hover>tbody>tr:hover {
    background-color: #f042bc1f;
    transition: all 0.6s;
}
.email-mid span {
    text-align: center;
    font-size: 250%;
    color: #c5c5c5;
    border: 3px solid #3c3c3c;
    border-radius: 12px;
    padding: 10px;
    display: inline-block;
    margin: 0 auto;
}
.blogbutton {
    background: #0c0c0c none repeat scroll 0 0;
    border: 1px solid #3c3c3c;
    color: #555;
    font-size: 11pt;
}
.btn-primary:hover {
    color: #fff;
    background-color: #c62990;
    border-color: #f042bc;
    transition: all 0.6s;
}
.btn-primary.active, .btn-primary:active, .open>.dropdown-toggle.btn-primary {
    color: #fff;
    background-color: #c62990;
    border-color: #f042bc;
    transition: all 0.6s;
}
.btn-primary.focus, .btn-primary:focus {
    color: #fff;
    background-color: #c62990;
    border-color: #f042bc;
    transition: all 0.6s;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #0c0c0c;
    border: 1px solid #c62990;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.05);
}
.regular-page h1 {
    color: #e6e6e6;
    border-bottom: 1px solid #eee;
    padding-bottom: 6px;
    margin-bottom: 12px;
}

#contact-form-container input, textarea {
    width: 100%px;
    font-size: 11pt;
    padding: 5px;
    border: 1px solid #888;
    color: #dcdcdc;
    background-color: #211e20;
}
.btn-default {
    color: #dcdcdc;
    background-color: #201e20;
    border-color: #888;
}
.btn-default.active.focus, .btn-default.active:focus, .btn-default.active:hover, .btn-default:active.focus, .btn-default:active:focus, .btn-default:active:hover, .open>.dropdown-toggle.btn-default.focus, .open>.dropdown-toggle.btn-default:focus, .open>.dropdown-toggle.btn-default:hover {
    color: #e6e6e6;
    background-color: #201e20;
    border-color: #888888;
}
.btn-default.active, .btn-default:active, .open>.dropdown-toggle.btn-default {
    color: #dedede;
    background-color: #201e20;
    border-color: #888888;
}
.dropup .dropdown-menu, .navbar-fixed-bottom .dropdown .dropdown-menu {
    top: auto;
    bottom: 100%;
    margin-bottom: 2px;
    background-color: #121212;
}
.btn-default:hover {
    color: #e6e6e6;
    background-color: #201e20;
    border-color: #888;
}
a {
    color: #c62990;
    text-decoration: none;
}
a:focus, a:hover {
    color: #f042bc;
    text-decoration: underline;
}
.container.custom-home {
    float: none;
    padding-left: 50px;
}
}
