css:
@-moz-document domain("wasd.tv") {
/* Panels */
  header
  {
	  background: #1d1d24!important;
  }
  .player-info.ng-star-inserted, .channel-wrap, .channel-schedule.section-default.pl-4.pr-4, .channel-schedule__placeholder.ng-star-inserted, .channel-info-wrap.ng-star-inserted, .channel-info, .content, .section-default.pl-4.pr-4.white.line.ng-star-inserted, .section-default, .white, .section-games, .news-academy
  {
	  background-color: #1d1d24!important;
	  background: #1d1d24!important;
  }
  .icon, .channel__stats, .stat-one, .stat-one__icon
  {
	  fill: #77babc!important;
  }
/* Text */
  a, abbr, address, article, aside, audio, b, blockquote, body, body div, caption, cite, code, dd, del, dfn, dl, dt, em, fieldset, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, p, pre, q, samp, section, small, strong, sub, sup, table, tbody, td, tfoot, th, thead, time, tr, ul, var, video
  {
	  color: #77babc!important;
  }
  .channel__stream-name, .channel-info__description, .channel__name, .section-title, .user-profile__name, .menu__link, .message__text, .system-message__text, .subscriber__name
  {
	  color: #77babc!important;
  }
  .btn-link
  {
	  color: #007bff!important;
  }
  .card-header, .card
  {
	  background-color: #31313c!important;
	  background: #31313c!important;
  }
  .streams-layout
  {
	  background-color: #1d1d24!important;
	  border-top: hidden!important;
  }
  .stream-card__title
  {
	  color: #77babc!important;
  }
  
  
/* IS THIS SO DIFFICULT, WASD.TV?????? */
}
