css:
@-moz-document url-prefix("https://nim-lang.org/docs") {
body {
    background-color: #151515;
    color: #969696;
}
a {
    color: #00a9ff;
}
a:hover,
a.toc-backref:hover {
    color: #b9b9b9;
}
a.toc-backref {
    color: #a8a8a8;
}
blockquote {
    border-left-color: #444;
}
table.docutils th {
    background-color: #c5c5c5;
    color: #333;
}
table.docutils tr:hover {
    color: #c5c5c5;
    background-color: #333;
}
.pre {
    color: #c0c0c0;
    background-color: #333;
}

.listing, pre {
    color: #9ba4b7;
    background-color: #1f1f22;
}
span.DecNumber,
span.BinNumber,
span.HexNumber,
span.OctNumber,
span.FloatNumber       { color: #d19a66; }
span.Identifier        { color: #cacaca; }
span.Keyword           { color: #d078dd; }
span.StringLit,
span.LongStringLit,
span.CharLit           { color: #89ce57; }
span.EscapeSequence    { color: #61afef; }
span.Operator          { color: #c678dd; }
span.Punctuation       { color: #71adf9; }
span.Comment,
span.LongComment       { color: #727a8a; }
span.RegularExpression { color: darkviolet; }
span.TagStart          { color: darkviolet; }
span.TagEnd            { color: darkviolet; }
span.Key               { color: #c678dd; }
span.Value             { color: #c678dd; }
span.RawData           { color: #61afef; }
span.Assembler         { color: #61afef; }
span.Preprocessor      { color: yellow; }
span.Directive         { color: #c678dd; }
span.Command,
span.Rule,
span.Hyperlink,
span.Label,
span.Reference,
span.Other             { color: #c678dd; }

}
