css:
@-moz-document url-prefix("https://flutter.dev/docs"), url-prefix("https://api.flutter.dev") {
body {
	color: #ddd;
	background-color: #222;
}

.site-sidebar > .nav .nav .nav-item > .nav-link {
	color: rgba(255,255,255,0.8);
}

.nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link {
	color: #fff;
	background-color: #4A4A4A;
}

.alert-success {
	color: #effaf2;
	background-color: #0f501f;
	border-color: #1a8933;
}

.code-excerpt__code {
	border: 1px solid #80bdfa;
}

.code-excerpt__code pre {
	background-color: #2682de;
}

.str, .highlight .s, .highlight .s1, .highlight .s2 {
	color: #f5f8f9;
}

pre .highlight {
	background: #506a7c;
}

.navbar-brand img {
    -webkit-filter: invert(1);
   filter: invert(1);
}

.alert-warning {
	color: #c8c5bc;
	background-color: #463500;
	border-color: #7a5c00;
}

.site-header {
	background-color: #222;
	box-shadow: 0px 2px 4px rgba(0,0,0,0.1);
	font-family: "Google Sans", "Roboto", sans-serif;
	position: -webkit-sticky;
	position: sticky;
	top: 0;
	z-index: 100;
}

.alert-secondary {
    color: #e2e3e5;
    background-color: #383d41;
    border-color: #535557;
}

.code-excerpt__header {
	background-color: #383d41;
}

.code-excerpt__code pre {
	background-color: #052c52;
}

.pln {
	color: #d6d6d6;
}

.pun, .highlight .p, .opn, .highlight .o, .clo {
	color: #d6d6d6;
}

.typ, .highlight .n {
	color: #da00da;
}

.kwd, .highlight .k, .highlight .kc, .highlight .kd, .highlight .kn, .highlight .kp, .highlight .kr, .highlight .kt {
	color: #25e3d2;
}

code {
	color: #00e5d1;
}

.d2h-file-header {
	background-color: #373737;
}

.d2h-info {
	background-color: #373737;
	color: #ddd;
	border-color: #656565;
}

.d2h-code-linenumber {
	background-color: #232323;
	color: #ddd;
}


.hljs {
	color: #ddd;
}

.d2h-ins {
	background-color: #0b360b;
	border-color: #455845;
}

.line-num2 {
	color: #ddd;
}

.line-num1 {
	color: #ddd;
}

.hljs-keyword, .hljs-selector-tag, .hljs-subst {
	color: #d2d2d2;
}

.d2h-del {
	background-color: #383232;
	border-color: #664b4b;
}

.d2h-code-line del, .d2h-code-side-line del {
	background-color: #5a3f41;
}

.hljs-string, .hljs-doctag {
	color: #ff0040;
}

.d2h-code-line ins, .d2h-code-side-line ins {
	background-color: #335233;
}

.hljs {
	color: #97f295;
}

.code-excerpt__code pre {
	background-color: #04192d;
}

pre .highlight {
	background: #012a47;
}

.alert-info {
	color: #00dbff;
	background-color: #023a45;
	border-color: #015562;
}

header {
	background-color: #424242;
	color: #fff;
}

.self-name {
	color: #fff;
}

h1, h2, h3, h4, h5, h6 {
	color: #ccc;
}

dd {
	color: #eaeaea;
}

pre, pre.prettyprint {
	background: #181818;
}

.snippet-container {
	background-color: #0d3045;
}

header a, header p, header li {
	color: #bbbbbb;
}

.self-crumb {
	color: #ddd;
}

.features {
	color: #73fffa;
}
}
