css:
@-moz-document url-prefix("http://voice.google.com/"), url-prefix("https://voice.google.com/")
{


 :root
  {
    --maincolor: #111;
    --nav-sub: #01264d;
    --header: #080809;
    --nav-text: #395062;
    --main-bg: #151515;
    --white: #f6f6f7;
    --error: #a80202;
    --green: #02b74d;
    --color001: #8f9190;
    --color002: #ff7300;
    --purple: #7602dd;
    --purpledrk: #4a0171;
    --hoverbg: #7901b72e;
    --element: rgba(2, 89, 115, .39) !important;
  }

  ::-webkit-scrollbar
  {
    width: 0 !important;
  }

  img.gb_pa
  {
    content: url(http://icons.iconarchive.com/icons/danieledesantis/playstation-flat/64/playstation-circle-dark-icon.png);
  }

  .gb_ce
  {
    font-size: 32px !important;
    padding-left: 15px !important;
  }

  .gb_B,.gb_sb
  {
    background: var(--nav-sub)!important;
    color: var(--nav-text)!important;
  }

  .gb_sb .gb_tb
  {
    background: var(--maincolor)!important;
    border: none;
  }

  .gb_kd.gb_Ic .gb_Ua, .gb_kd.gb_Ic .gb_Va, .gb_kd.gb_Ic .gb_B, .gb_Ic.gb_B
  {
    border-radius: 15px 15px 15px 15px;
      
  }




/* All Items Using Main Background Color */

  .gb_na:not(.gb_oa) .gb_Td, .layout-column, .root, .accountPhoneBanner, .YYoZ8c-VeKiLd, .wAp0Zd-H9tDt, .GYQtq-JjL0qc,

.gmat-nav-list .gmat-nav-list-item.gmat-list-item-active, .gmat-nav-list .gmat-nav-list-item-ripple.gmat-list-item-active, .gmat-nav-list .BYjt6b-TJEFFc-DH6Rkf-rymPhb-ibnC6b.gmat-list-item-active, .gmat-nav-list .gmat-nav-list-item .mat-ripple-element, .gmat-nav-list .gmat-nav-list-item-ripple .mat-ripple-element, .gmat-nav-list .BYjt6b-TJEFFc-DH6Rkf-rymPhb-ibnC6b .mat-ripple-element, .BN08bf-H9tDt md-content.BN08bf-u9UW7e,

.fFW7wc.XKSfm-Sx9Kwc-xJ5Hnf
  {
    background: var(--maincolor)!important;
  }

  .gb_Ze, .gb_0e
  {
    background: var(--element)!important;
    box-shadow: 3px 3px 11px 2px #000000;
    width: 25% !important;
    transform: translate(100%,-15%);
    border-radius: 0px 0px 15px 15px;
  }




/* Main text color */

  .gvPageRoot .grey-900, .gb_na svg, .gb_Dc svg, .gb_6c .gb_ce, .gb_Qc .gb_ce, .BN08bf-H9tDt md-content.BN08bf-u9UW7e
  {
    color: var(--nav-text)!important;
  }

  input, .md-input
  {
    color: var(--error)!important;
  }

  .uM2Vn-VdSJob
  {
    color: var(--purpledrk)!important;
  }

  .gmat-nav-list-item.gmat-list-item-active .mat-icon svg,

.gmat-nav-list .gmat-nav-list-item.gmat-list-item-active, .gmat-nav-list .gmat-nav-list-item.gmat-list-item-active .mat-icon
  {
    color: var(--purple)!important;
  }


/* Left hand list icons */

  .gmat-nav-list .gmat-nav-list-item .mat-icon
  {
    color: #1aa3ff;
  }

  .mat-list-base .mat-list-item .mat-list-item-content
  {
    max-width: 2rem;
    padding: .8rem;
    background-color: #111;
  }

  .mat-list-base .mat-list-item .mat-list-item-content,
.gmat-nav-list-reverse .gmat-nav-list-item
  {
    -webkit-box-shadow: 2px 5px 15px 7px #000000;
    box-shadow: 2px 5px 15px 7px #000000;
    height: 25px !important;
    margin: 3rem 0;
  }

  .mat-list-base .mat-list-item .mat-list-item-content, .gmat-nav-list-reverse
  {
    box-shadow: none;
  }

  .navItemBadge
  {
    background: var(--error) !important;
  }

  .root[_ngcontent-xfw-c5]:not(.expanded) .gmat-nav-list-reverse .gmat-nav-list-item, .root[_ngcontent-xfw-c5]:not(.expanded) .gmat-nav-list-reverse .gmat-nav-list-item-ripple
  {
    background: var(--maincolor)!important;
  }

  .mat-divider
  {
    border-top-color: var(--maincolor)!important;
  }

  .A7jSV-YLEF4c
  {
    box-shadow: 3px 3px 11px 2px #000000, inset 5px 5px 15px 5px rgba(0,0,0,0.64);
  }

  .gmat-body-2:hover, .dOUild-H9tDt:focus, .rkljfb-gk6SMd,

.rkljfb-Q4CtPe:hover
  {
    background: var(--hoverbg)!important;
  }

  .Bqxti-MJZihc.Bqxti-VCkuzd, .Bqxti-MJZihc.Bqxti-VCkuzd a
  {
    background-color: var(--element)!important;
    color: rgba(255, 255, 255, .77) !important;
  }

  .Bqxti-VCkuzd, .Bqxti-VCkuzd a
  {
    color: hsla(197, 2%, 82%, .5) !important;
    background-color: rgba(0, 0, 0, .3) !important;
    border-radius: 3.5px;
    background: linear-gradient(135deg, #15afce00 0%, #f4f4f421 50%, #15afce00 100%);
    -webkit-box-shadow: 5px 5px 15px 1px #000000;
  }

  .gb_kf
  {
    border-color: var(--nav-text);
  }




.gb_ja{
    
    background: var(--maincolor)!important;
}




.gb_B, .gb_sb{
    
    background: var(--maincolor)!important;
}



.gb_Se{
    background: var(--element)!important;
    border-radius: 0px 0px 15px 15px;
    width:25% !important;
    transform: translate(100%, -25%);
}


.gb_ef{
    border:none;
    box-shadow: 5px 5px 15px 5px #000000;
}



.gb_Zc{
    font-family: Tahoma, Geneva, sans-serif;
font-size: 50px;
letter-spacing: 7px;
word-spacing: 2px;
color: #000000;
font-weight: normal;
text-decoration: line-through solid rgb(68, 68, 68);
font-style: normal;
font-variant: normal;
text-transform: UPPERCASE;
    transform: translate(10px,0);
    text-shadow: 5px 5px 5px #000000;
}


img.gb_la{
    width: 25px !important;
    height: 25px !important;
    border-radius: 50%;
    
}





  
}
