/* ==UserStyle==
@name         MDN Dark
@version      1.0.6
@description  Dark theme for the Mozilla Developer Network
@namespace    AfroThundr3007730
@author       AfroThundr
@homepageURL  https://github.com/AfroThundr3007730/MDN-Dark
@supportURL   https://github.com/AfroThundr3007730/MDN-Dark/issues
@updateURL    https://raw.githubusercontent.com/AfroThundr3007730/MDN-Dark/master/mdn-dark.user.css
@license      GPL-3.0
==/UserStyle== */
@-moz-document domain("developer.mozilla.org") {
  :root {
    --gray-1: #111;
    --gray-2: #222;
    --gray-3: #333;
    --gray-7: #777;
    --gray-9: #999;
    --gray-e: #eee;
    --gray-08: #080808;
    --gray-80: #808080;
    --gray-9a: #9a9a9a;
    --blue-1: #83d0f2;
    --blue-2: #3d7e9a;
    --red-1: #900;
  }
  /* main */
  html, body, #content, .home-hacks {
    background-color: var(--gray-1);
    color: var(--gray-7);
  }
  h1, h2, h3, h4, h5, h6, header {
    color: var(--gray-9);
  }
  .text-content #wikiArticle > blockquote + h2,
  .text-content #wikiArticle > hr + h2 {
    background-color: var(--gray-1);
  }
  .home-masthead {
    background-color: var(--gray-2);
  }
  .home-features a, .quick-links .toggle summary {
    color: var(--blue-1);
  }
  .callout-fx-dev {
    filter: invert(100%) hue-rotate(180deg);
  }
  .text-content .card-grid li, .communitybox, .moreinfo, .user-head,
  .callout-box {
    background-color: var(--gray-2);
  }
  #content #wiki-content article a.new {
    color: var(--red-1);
  }
  .licenseblock, .originaldocinfo {
    background-color: var(--gray-2);
  }
  img, video {
    background-color: var(--gray-e);
  }
  .prevnext a span {
    background-color: var(--gray-2) !important;
    color: var(--gray-9a) !important;
    box-shadow: inset 0 -1px var(--gray-7) !important;
  }
  /* meta */
  .activity thead {
    background-color: var(--gray-3);
  }
  .slug-change {
    background-color: var(--gray-2);
    border-color: var(--gray-3);
  }
  .first-contrib-welcome {
    background-color: var(--gray-2);
    border-color: var(--gray-3);
  }
  .cke_top, .cke_bottom {
    filter: invert(100%) hue-rotate(180deg);
  }
  #page-tags li {
    background-color: var(--gray-3);
    border-color: var(--gray-2);
    color: var(--blue-2);
  }
  /* nav menus */
  #main-header {
    background-color: var(--gray-2);
  }
  #wiki-document-head, .document-head {
    background-color: var(--gray-3);
    color: var(--gray-9);
  }
  #main-header .nav-main-item > a, .login > a, .toolbox > ul > li > a {
    color: var(--blue-1);
  }
  .submenu {
    background-color: var(--gray-3);
    border-top: 5px solid var(--gray-9a);
  }
  .submenu .title, .footer-title {
    color: var(--gray-7);
  }
  .nav-main-item > a:focus, .nav-main-item > a:hover, .login > a:focus,
  .login > a:hover, .toolbox > ul > li > a:focus, .toolbox > ul > li > a:hover {
    background-color: var(--gray-3);
    color: var(--blue-1);
  }
  s.deprecatedElement, s.nonStdElement, s.obsoleteElement {
    opacity: .5;
  }
  /* survey banner */
  .cta-background-linear, .mdn-cta-container .mdn-cta-content-container,
  .mdn-cta-container .mdn-cta-button-container,
  .mdn-cta-container .mdn-cta-button-container:before {
    background: none;
  }
  .mdn-cta-container .mdn-cta-content {
    box-shadow: 5px 0 5px var(--gray-7);
  }
  #developer_needs {
    background-color: var(--gray-2);
  }
  /* forms */
  .button, button, input[type="button"], input[type="submit"] {
    background-color: var(--gray-2);
    color: var(--gray-7);
  }
  input[type=email], input[type=number], input[type=password],
  input[type=search], input[type=text], input[type=url], textarea {
    color: var(--gray-9);
    background-color: var(--gray-2);
    border-color: var(--gray-3);
  }
  input, optgroup, option, select, textarea {
    color: var(--gray-9);
    background-color: var(--gray-2);
    border-color: var(--gray-3);
  }
  .search-form > input#home-q, .search-form > input#search-q {
    background-color: var(--gray-1);
    color: var(--gray-7);
  }
  .search-pane {
    background-color: var(--gray-2);
  }
  .search-results-container mark {
    color: var(--gray-9);
  }
  /* icons */
  .icon-search {
    filter: brightness(50%);
  }
  .logo, .nav-footer-logo, .nav-footer-mozilla, .login-link .icon,
  #wikiArticle i.icon-beaker, #wikiArticle i.icon-stethoscope,
  #wikiArticle i.icon-thumbs-down-alt, #wikiArticle i.icon-trash,
  #wikiArticle i.icon-warning-sign {
    filter: invert(50%);
  }
  .blockIndicator:before, .geckoVersionNote:before, .inheritsbox:before,
  .note:before, .notice:before, .overheadIndicator:before,
  .standardNoteBlock:before, .warning:before, .indicatorInHeadline:before,
  .inlineIndicator:before {
    filter: invert(50%);
  }
  .external.external-icon:before, .crumbs span::after,
  .icon-only-inline i.icon-beaker, .icon-only-inline i.icon-trash,
  .icon-only-inline i.icon-warning-sign, .prevnext .icon-arrow-left,
  .prevnext .icon-arrow-right, .prevnext .icon-arrow-up,
  .standard-table i.icon-cogs, i.icon-beaker, i.icon-info-sign,
  i.icon-thumbs-down-alt, i.icon-trash, i.icon-warning-sign {
    filter: invert(80%);
  }
  .quick-links .sidebar-icon i[class^="icon-"] {
    filter: invert(100%);
  }
  /* pre / code */
  :not(pre) > * > code, :not(pre) > code {
    background-color: var(--gray-9a);
    color: var(--gray-1);
  }
  pre.syntaxbox, pre.twopartsyntaxbox {
    background-color: var(--gray-08);
    color: var(--gray-9a);
  }
  .quick-links :not(pre) > * > code, .quick-links :not(pre) > code {
    color: var(--gray-9a);
  }
  .highlight .highlight-span code, .highlight code,
  .text-content h3 .highlight-span code, .text-content h3 code,
  .text-content h5 .highlight-span code, .text-content h5 code {
    background-color: var(--gray-9a) !important;
    color: var(--gray-2) !important;
  }
  .index :not(pre) > * > code, .index :not(pre) > code,
  .indexListTerm :not(pre) > * > code, .indexListTerm :not(pre) > code {
    color: var(--gray-9a);
  }
  .text-content code[class*="language-"], .text-content pre[class*="language-"] {
    color: var(--gray-80);
  }
  .text-content pre {
    background-color: var(--gray-08);
  }
  .text-content pre code {
    color: var(--gray-9a);
  }
  .text-content pre.line-numbers {
    background-color: var(--gray-08);
    color: var(--gray-9a);
  }
  .text-content .example-bad, .text-content .example-bad::after,
  .text-content pre[class*="language-"].example-bad,
  .text-content pre[class*="language-"].example-bad::after,
  .text-content .example-good, .text-content .example-good::after,
  .text-content pre[class*="language-"].example-good,
  .text-content pre[class*="language-"].example-good::after {
    background-color: var(--gray-08);
  }
  /* iframes */
  .interactive, .live-sample-frame {
    filter: invert(100%) hue-rotate(180deg);
  }
  /* notes */
  .draft, .indicator-warning, .note, .notice, .prefixBox, .privilegedBadge,
  .renamed, .secureContexts, .todo, .translationInProgress,
  .warning.warning-review {
    background-color: var(--gray-2);
  }
  .indicator-info, .optional, .template-jsOverrides {
    background-color: var(--gray-2);
  }
  .deprecated, .deprecatedBadge, .indicator-danger, .nonStandard,
  .nonstandardBadge, .obsolete, .obsoleteBadge, .unimplemented,
  .unimplementedBadge, .warning {
    background-color: var(--gray-2);
  }
  .domLevel, .experimental, .experimentalBadge, .firefoxVersionBadge,
  .geckoMinVer, .geckoMinVerMethod, .geckoVersionBadge, .geckoVersionNote,
  .htmlVer, .indicator-version, .jsMinVer, .jsMinVerHeader, .mbMinVer,
  .mobileOnly, .notXPCOM, .projectSpecific, .smMinVerHeader, .standardNote,
  .tbMinVer {
    background-color: var(--gray-2);
  }
  .reviews {
    background-color: var(--gray-2);
    color: var(--gray-7);
  }
  ul.tags li {
    background-color: var(--gray-3);
    border-color: var(--gray-2);
  }
  ul.tags li a {
    color: var(--blue-2);
  }
  span[class*="spec-"]:before {
    background-color: var(--gray-1);
  }
  /* tables */
  .text-content .cssprop, .text-content .htmlelt, .text-content .learn-box,
  .text-content .properties, .text-content .summary-box-events,
  .text-content .summary-box-js {
    background-color: var(--gray-2);
  }
  .text-content code[class*="language-"], .text-content pre[class*="language-"] {
    background-color: var(--gray-08);
  }
  .text-content table.standard-table:not(.learn-box) th {
    color: var(--gray-1);
  }
  .text-content table.standard-table:not(.learn-box) td {
    background-color: var(--gray-2);
  }
  .text-content table.standard-table:not(.learn-box):not(.nostripe) tr:nth-child(2n+1) td {
    background-color: var(--gray-3);
  }
  .text-content table.standard-table:not(.learn-box) td.header,
  .text-content table.standard-table:not(.learn-box) th,
  .text-content table.standard-table:not(.learn-box) td,
  .text-content table.standard-table:not(.learn-box) {
    border-color: var(--gray-1);
  }
  /* compatability table */
  .bc-table {
    background-color: var(--gray-7);
  }
  .bc-table th {
    color: var(--gray-1);
  }
  .bc-legend .ic-altname, .bc-legend .ic-deprecated, .bc-legend .ic-disabled,
  .bc-legend .ic-experimental, .bc-legend .ic-footnote,
  .bc-legend .ic-non-standard, .bc-legend .ic-prefix {
    filter: invert(50%);
  }
  .bc-supports-no, .bc-supports-partial, .bc-supports-unknown, .bc-supports-yes,
  .bc-table .bc-history {
    filter: invert(100%);
  }
}
