css:
@-moz-document domain("jezevec10.com") {

/* Font import */
/* Remove comment below to use font from */
/*@import url('https://fonts.googleapis.com/css?family=Montserrat');*/

/*
    Style by Founntain 
    Twitter: @FounntainXD
*/

/* Website */
html,
body {
    font-family: 'Montserrat', sans-serif;
    background-color: #282C34 !important;
}

.table-striped > tbody > tr:nth-child(odd) > td {
    background-color: #21252B;
}

.nav-tabs {
    border-color: #fc2;
}

input[type=text],
textarea {
    border: none;
    border-radius: 0px;
    background-color: rgba(0, 0, 0, .5) !important;
    color: white !important;
    border-bottom: 3px solid #fc2;
    width: 190px !important;
    margin-right: 10px;
    transition-duration: .5s;
    outline: none;
}

input[type=text]:hover,
textarea:hover {
    border-bottom: 3px solid deeppink;
    transition-duration: .5s;
}

select {
    border: none;
    border-radius: 0px;
    background-color: rgba(0, 0, 0, .5) !important;
    color: white !important;
    border-bottom: 3px solid #fc2;
    width: 190px !important;
    margin-right: 10px;
    transition-duration: .5s;
    outline: none;
    box-shadow: none;
}

option {
    background-color: black;
}

textarea {
    height: 100px;
}

.navbar-default {
    background-color: #21252B;
    border: none;
    border-bottom: 3px solid #fc2 !important;
}

.navbar-brand {
    font-family: 'Montserrat', sans-serif;
    font-weight: bold;
}

.navbar-nav > li > a:hover,
.navbar-brand:hover {
    color: #fc2 !important;
}

.navbar-nav > .open > a:focus,
.navbar-nav > .active > a {
    background-color: #fc2 !important;
    color: black !important;
}

h1 {
    font-family: 'Montserrat', sans-serif;
    border-bottom: 3px solid #fc2;
}

h2,
h3,
h4,
h5 {
    font-family: 'Montserrat', sans-serif;
}

.dropdown-menu {
    background-color: #282C34 !important;
}

.dropdown-menu > li > a:hover {
    background-color: #fc2 !important;
    color: black;
}

.btn {
    border: none;
    border-bottom: 3px solid #fc2;
    border-radius: 0px;
    transition-duration: .5s;
    background-color: rgba(0, 0, 0, .5);
}

.btn:hover {
    border-bottom: 3px solid deeppink;
    color: black;
    background-color: white;
    transition-duration: .5s;
}

a {
    color: #fc2;
}

.label-success {
    background-color: #fc2 !important;
    color: black
}

.label-primary {
    background-color: silver !important;
    color: black
}
.label-danger {
    background-color: bronze !important;
    color: black
}

.pagination > .active > span {
    background-color: #ffb700 !important;
    color: black;
}

.pagination > li > a {
    background-color: #fc2 !important;
    color: black;
}

.pagination > li:first-child > span,
.pagination > .disabled > span {
    background-color: #f90 !important;
    color: black;
}

.dropdown-menu > .active > a {
    background-color: #fc2 !important;
    color: black;
}

/* Playsite */
.mainLayer,
.players canvas {
    border: 3px solid #fc2;
}

button {
    background-color: rgba(0, 0, 0, .5);
    outline: none;
    border: none;
    color: white;
    border-bottom: 3px solid #fc2;
    height: 30px !important;
}

.gCapt {
    color: white !important;
    border: 3px solid #fc2;
}

#lrem {
    color: white;
    font-weight: bold;
}

#sprintText {
    color: white;
}

#stats {
    color: white;
}

#stats .sval {
    font-family: 'Montserrat', sans-serif;
}

#chatInputArea > input {
    height: 30px !important;
    padding: 0px !important;
    border-bottom: 3px solid #fc2
}

#chatBox {
    border-bottom: 3px solid #fc2;
}

#createRoom,
#lobbyBox {
    background-color: #21252B;
    height: 650px !important;
    border: 3px solid #fc2;
}

#create,
#cancelCreation,
#saveRD {
    color: white !important;
    border-color: #fc2;
}

.inputDesc {
    color: white;
}

.roomButtons {
    bottom: 15px;
}

a:hover {
    color: deeppink;
}

.lobbyTable thead {
    border-color: #fc2;
}

.roomListSep {
    color: white;
    border-color: #fc2;
}

/* Scollbar */
/* width */
::-webkit-scrollbar {
    width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
    background: #21252B;
    ;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #fc2;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: silver;
    ;
}
}
