css:
@-moz-document domain("healingwell.com") {
/* 7/15/19 */
body {
	background: #545353
}
h1 {
	color: #ddd
}

div.row a.options,
div.page-listing-top a,
div.page-listing-bottom a,
div.post-nav a  {
	color: #ccc; 
}

div.section-header.bread-crumbs,
div.bread-crumb-links a{
	color: white; 
}

div[class*="forum-row-odd"] {
	background: #ccc
}

div[class*="forum-row-even"] {
	background: #ddd
}

div.post-even  {
	background: #ccc 
}

div.post-odd  {
	background: #ddd 
}

div[class^="medium-3"] div.user-info{
	background: #ddd;
}

div.post-body {
	font-family: sans;
	font-size: 14px;
}

div.posted ,
div.post-nav a,
div.options-dropdown a,
div[class^="small-12"] div.page-listing-top a {
	color: #1d7d03 !important
}

div.page-listing-bottom a {
	color: #555
}

div.post-box-input input[type="button"],
a#mcode-help {
	color: #eee !important
}
div.edit-menu.text-right a {
	color: #7f4e15
}

div#post-preview-body {
	background: #ddd
}

div#post-preview-body p.msgQuote {
	background: #ccc
}

hr.PostHR NW{
	color: red !important;
	font-weight: 600 !important ;
	text-decoration: underline !important
}

div.section-body-secondary {
	background: #bbb
}


}

@-moz-document url-prefix("https://www.healingwell.com/community/profile") {
/*PROFILE*/

body {
	background: #b2b0b0
}
}
